INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '221', '132002', '2037', '9', 'L''insufficiente pressione di gonfiaggio di uno pneumatico comporta l''eccessivo aumento della sua flessibilità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '221', '132002', '2038', '10', 'L''insufficiente pressione di gonfiaggio di uno pneumatico comporta l''aumento del rollio e del beccheggio del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '221', '132002', '2039', '11', 'L''insufficiente pressione di gonfiaggio di uno pneumatico comporta la minor tenuta di strada del veicolo', 'V');
