INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '221', '132002', '2041', '13', 'Una pressione di gonfiaggio dello pneumatico inferiore del 10% rispetto al valore consigliato facilita le manovre di parcheggio', 'F');
