INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '221', '132002', '2043', '15', 'L''insufficiente pressione di gonfiaggio di uno pneumatico comporta una sua maggior usura al centro del battistrada', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '221', '132002', '2044', '16', 'L''insufficiente pressione di gonfiaggio di uno pneumatico comporta una sua miglior aderenza su terreno innevato', 'F');
