INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '222', '132002', '2046', '2', 'L''eccessiva pressione di gonfiaggio di uno pneumatico riduce l''appoggio e la pressione dei bordi del battistrada sulla sede stradale', 'V');
