INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '222', '132002', '2048', '4', 'L''eccessiva pressione di gonfiaggio di uno pneumatico ne aumenta il diametro di rotolamento', 'V');
