INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '222', '132002', '2050', '6', 'L''eccessiva pressione di gonfiaggio di uno pneumatico peggiora il confort di marcia', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '222', '132002', '2051', '7', 'Una pressione di gonfiaggio dello pneumatico superiore del 10% rispetto al valore consigliato non modifica il confort di marcia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '222', '132002', '2052', '8', 'Una pressione di gonfiaggio dello pneumatico superiore del 10% rispetto al valore consigliato permette un risparmio di carburante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '222', '132002', '2053', '9', 'Una pressione di gonfiaggio dello pneumatico superiore del 10% rispetto al valore consigliato riduce la distanza di frenatura', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '222', '132002', '2054', '10', 'L''eccessiva pressione di gonfiaggio di uno pneumatico non fa appoggiare la parte centrale del battistrada sulla sede stradale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '222', '132002', '2055', '11', 'L''eccessiva pressione di gonfiaggio di uno pneumatico non influisce sul confort di marcia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '222', '132002', '2056', '12', 'L''eccessiva pressione di gonfiaggio di uno pneumatico ne aumenta la flessibilità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '222', '132002', '2057', '13', 'L''eccessiva pressione di gonfiaggio di uno pneumatico ne diminuisce il diametro di rotolamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '222', '132002', '2058', '14', 'L''eccessiva pressione di gonfiaggio di uno pneumatico non comporta problemi se la temperatura esterna è inferiore a 10°C', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '222', '132002', '2059', '15', 'L''eccessiva pressione di gonfiaggio di uno pneumatico comporta una sua maggior usura ai bordi del battistrada', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '223', '131011', '', '', 'Efficienza degli pneumatici Aquaplaning 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '223', '131011', '2060', '1', 'Gli intagli del battistrada servono a favorire il deflusso di acqua eventualmente presente sulla carreggiata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '223', '131011', '2061', '2', 'L’aquaplaning consiste in una perdita di aderenza legata alla presenza di un velo di acqua compr essa tra battistrada e pavimentazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '223', '131011', '2062', '3', 'In fase di acquaplaning il veicolo perde direzionalità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '223', '131011', '2063', '4', 'A parità di altre condizioni, il pericolo che si verifichi il fenomeno dell''aquaplaning aumenta con l''aumentare della velocità del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '223', '131011', '2064', '5', 'A parità di altre condizioni, il pericolo che si verifichi il fenomeno dell''aquaplaning aumenta con la diminuzione di profondità degli intagli del battistrada', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '223', '131011', '2065', '6', 'A parità di altre condizioni, il pericolo che si verifichi il fenomeno dell''aquaplaning aumenta con la diminuzione della pressione degli pneumatici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '223', '131011', '2066', '7', 'Se il veicolo è in situazione di aquaplaning, la prima cosa da fare è diminuire la velocità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '223', '131011', '2067', '8', 'L''aquaplaning non può verificarsi quando i veicoli sono dotati di ruote gemellate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '223', '131011', '2068', '9', 'L''aquaplaning non può verificarsi se gli intagli del battistrada hanno almeno quattro millimetri di profondità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '223', '131011', '2069', '10', 'L''aquaplaning può interessare la motrice ma non il rimorchio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '223', '131011', '2070', '11', 'Se il veicolo è in situazione di aquaplaning, la prima cosa da fare è aumentare la velocità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '223', '131011', '2071', '12', 'Se il veicolo è in situazione di aquaplaning, la prima cosa da fare è frenare a fondo, premendo nel contempo il pedale della frizione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '223', '131011', '2072', '13', 'Se il veicolo è in situazione di aquaplaning, la prima cosa da fare è sterzare a fondo in direzione del margine destro della carreggiata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '223', '131011', '2073', '14', 'L''aquaplaning non può verificarsi su veicoli dotati di sistema antibloccaggio in frenata ABS', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '224', '133002', '', '', 'Pneumatici invernali', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '224', '133002', '2074', '1', 'I cosiddetti pneumatici invernali sono più efficaci dei pneumatici ordinari, anche quando la strada non è innevata, se la temperatura dell''asfalto è inferiore a circa 7°C', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '224', '133002', '2075', '2', 'I cosiddetti pneumatici invernali, sfruttano particolari disegni delle scolpiture del battistrada e mescole diverse da quelli convenzionali', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '224', '133002', '2076', '3', 'È opportuno montare gli pneumatici invernali su tutte le ruote del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '224', '133002', '2077', '4', 'I cosiddetti pneumatici invernali sono dotati di lamelle, cioè di piccoli e sottili intagli praticati in tutti i tasselli del battistrada', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '224', '133002', '2078', '5', 'I cosiddetti pneumatici invernali hanno mescole del battistrada che rimangono sufficientemente elastiche anche con basse temperature dell''asfalto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '224', '133002', '2079', '6', 'A basse temperature, la mescola dei pneumatici convenzionali perde elasticità e tale fenomeno riduce l''aderenza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '224', '133002', '2080', '7', 'Alcuni tipi di pneumatici invernali sono predisposti all''origine per la chiodatura', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '224', '133002', '2081', '8', 'L''uso di pneumatici invernali omologati sostituisce l''eventuale obbligo di montaggio catene in tratti di strada innevati', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '224', '133002', '2082', '9', 'I cosiddetti pneumatici invernali perdono aderenza sopra i 20°C di temperatura dell''asfalto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '224', '133002', '2083', '10', 'Sui cosiddetti pneumatici invernali non è possibile montare le catene', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '224', '133002', '2084', '11', 'L''uso di pneumatici invernali omologati non sostituisce l''eventuale obbligo di montaggio catene in tratti di strada innevati', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '224', '133002', '2085', '12', 'I cosiddetti pneumatici invernali sono utili e più efficaci dei pneumatici ordinari solo se la strada è fortemente innevata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '224', '133002', '2086', '13', 'I cosiddetti pneumatici invernali non prevedono la possibilità di chiodatura', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '224', '133002', '2087', '14', 'I cosiddetti pneumatici invernali devono essere montati solo sull''asse motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '224', '133002', '2088', '15', 'I cosiddetti pneumatici invernali non devono essere montati su veicoli dotati di sistema antipattinamento ESP', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '224', '133002', '2089', '16', 'I cosiddetti pneumatici invernali possono essere montati solo su veicoli appositamente omologati', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '225', '133003', '', '', 'Uso delle catene', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '225', '133003', '2090', '1', 'Chi fa uso di catene da neve non omologate è sanzionabile come se ne fosse sprovvisto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '225', '133003', '2091', '2', 'Se si fa uso di catene da neve, queste devono essere di misura idonea agli pneumatici del veicolo e di tipo omologato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '225', '133003', '2092', '3', 'È sconsigliabile utilizzare le catene su tratti di strada non innevati', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '225', '133003', '2093', '4', 'Nei veicoli a trazione integrale, le catene da neve devono essere montate solo sulle ruote posterior', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '225', '133003', '2094', '5', 'Se si utilizzano le catene nei tratti di strada privi di neve, la pressione di gonfiaggio degli pneumatici deve essere aumentata di 1 bar', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '225', '133003', '2095', '6', ' I veicoli muniti di pneumatici invernali devono rispettare limiti di velocità massima inferiori a quelli ordinari', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '226', '132004', '', '', 'Squilibratura delle ruote', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '226', '132004', '2096', '1', 'Una ruota può essere squilibrata per effetto di depositi solidi quali ghiaietto incastrato, fango, catrame', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '226', '132004', '2097', '2', 'La squilibratura di una ruota può portare a saltellamenti o sfarfallamenti della ruota stessa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '226', '132004', '2098', '3', 'La squilibratura di una ruota può avere effetti sullo sterzo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '226', '132004', '2099', '4', 'La squilibratura di una ruota deve essere corretta in officina specializzata con l''utilizzazione di appositi contrappesi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '226', '132004', '2100', '5', 'La squilibratura di una ruota porta a consumi anomali dello pneumatico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '226', '132004', '2101', '6', 'La squilibratura di una ruota peggiora la stabilità di marcia del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '226', '132004', '2102', '7', 'La squilibratura di una ruota è indicata da apposita spia rossa posta sul cruscotto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '226', '132004', '2103', '8', 'La squilibratura di una ruota può essere eliminata variando l''angolo di campanatura delle ruote del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '226', '132004', '2104', '9', 'La squilibratura di una ruota può essere eliminata variando l''angolo di convergenza delle ruote del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '226', '132004', '2105', '10', 'In uno pneumatico tubeless, il saltellamento di una ruota squilibrata può essere eliminato introducendo una camera d''aria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '226', '132004', '2106', '11', 'La squilibratura di una ruota può essere eliminata aumentando la pressione di 0,2 bar', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '226', '132004', '2107', '12', 'La squilibratura di una ruota può essere eliminata diminuendo la pressione di 0,4 bar', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '227', '132003', '', '', 'Manutenzione degli pneumatici 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '227', '132003', '2108', '1', 'La più importante operazione di manutenzione degli pneumatici è il periodico controllo della pressione di gonfiaggio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '227', '132003', '2109', '2', 'La corretta pressione di gonfiaggio degli pneumatici deve essere uguale a quella indicata dalla casa costruttrice dello pneumatico stesso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '227', '132003', '2110', '3', 'Il controllo della pressione deve essere effettuato su tutti gli pneumatici, compreso quello della ruota di scorta', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '227', '132003', '2111', '4', 'Causa di consumo irregolare di uno pneumatico è la mancata equilibratura della ruota su cui è montato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '227', '132003', '2112', '5', 'Causa di consumo irregolare di uno pneumatico sono i non corretti angoli di convergenza o di campanatura delle ruote', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '227', '132003', '2113', '6', 'Causa di consumo irregolare di uno pneumatico è la cattiva registrazione degli organi di direzione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '227', '132003', '2114', '7', 'Gli pneumatici con tagli sui fianchi devono essere sostituiti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '227', '132003', '2115', '8', 'La pressione di gonfiaggio degli pneumatici deve essere misurata a caldo, dopo aver percorso diversi chilometri, in modo da fargli raggiungere la giusta temperatura di esercizio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '227', '132003', '2116', '9', 'Lo pneumatico che presenti, a caldo, una pressione superiore a quella prevista a freddo, deve essere immediatamente sgonfiato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '227', '132003', '2117', '10', 'La pressione di gonfiaggio degli pneumatici è stabilita dal conducente in funzione del tipo di carico trasportato e della temperatura dell''asfalto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '227', '132003', '2118', '11', 'I difetti di convergenza delle ruote non influiscono sul consumo uniforme degli pneumatici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '227', '132003', '2119', '12', 'La distribuzione del carico non incide sul consumo degli pneumatici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '227', '132003', '2120', '13', 'Nei mesi estivi, la pressione di gonfiaggio degli pneumatici può essere controllata con meno frequenza perché l''alta temperatura dell''asfalto la mantiene regolare', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '227', '132003', '2121', '14', 'La bassa pressione di gonfiaggio degli pneumatici diminuisce la loro deriva', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '228', '132005', '', '', 'Manutenzione degli pneumatici 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '228', '132005', '2122', '1', 'In caso di foratura o anomalia ad uno pneumatico occorsa durante il servizio, bisogna rallentare e fermarsi appena è possibile farlo in sicurezza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '228', '132005', '2123', '2', 'La maggior usura del battistrada sul lato interno rispetto a quello esterno è attribuibile anche ad un''eccessiva convergenza delle ruote', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '228', '132005', '2124', '3', 'In caso di lunghi viaggi è opportuno controllare periodicamente la temperatura del battistrada', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '228', '132005', '2125', '4', 'La pressione di gonfiaggio di uno pneumatico è indicata generalmente in bar', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '228', '132005', '2126', '5', 'In caso di foratura o anomalia ad uno pneumatico occorsa durante il servizio, è necessario sostituirlo subito ovunque ci si trovi, lasciando a bordo del veicolo i passeggeri per salvaguardare la loro incolumità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '228', '132005', '2127', '6', 'La maggior usura del battistrada sul lato interno rispetto a quello esterno è attribuibile esclusivamente a una cattiva bilanciatura delle ruote', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '228', '132005', '2128', '7', 'In caso di lunghi viaggi occorre fermarsi periodicamente solo per raffreddare gli pneumatici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '228', '132005', '2129', '8', 'La pressione di gonfiaggio di uno pneumatico si esprime generalmente in atmosfere', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '228', '132005', '2130', '9', 'La pressione di gonfiaggio di uno pneumatico è un dato che è generalmente riportato sul fianco degli pneumatici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '229', '133001', '', '', 'Sostituzione di una ruota', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '229', '133001', '2131', '1', 'Se si deve sostituire una ruota occorre, se possibile, fermarsi lungo un tratto rettilineo e in piano, segnalando la sosta, nei casi previsti, con l''apposito segnale mobile di pericolo (triangolo)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '229', '133001', '2132', '2', 'Prima della sostituazione di una ruota occore calzare i cunei per il fermo sotto altre ruote del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '229', '133001', '2133', '3', 'Se si deve sostituire una ruota occorre posizionare il veicolo in sicurezza, possibilmente fuori della carreggiata e azionare il freno di stazionamento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '229', '133001', '2134', '4', 'Se si deve sostituire una ruota, occorre prima allentare i dadi e solo in seguito azionare il martinetto di sollevamento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '229', '133001', '2135', '5', 'La sostituzione della ruota di un mezzo pesante è un''operazione non semplice, che richiede prudenza, perizia ed allenamento preventivo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '229', '133001', '2136', '6', 'Il serraggio definitivo dei dadi dopo aver sostituito la ruota di un mezzo pesante, deve essere portato a termine quando la ruota è appoggiata al suolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '229', '133001', '2137', '7', 'Se il veicolo monta ruote gemellate, la sostituzione di una ruota deve necessariamente essere svolta in officina specializzata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '229', '133001', '2138', '8', 'Quando si sostituisce una ruota, l''ordine di serraggio dei dadi è stabilito dalla casa costruttrice dello pneumatico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '229', '133001', '2139', '9', 'Quando si sostituisce una ruota, il corretto serraggio dei dadi deve essere controllato con il dinamometro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '229', '133001', '2140', '10', 'Quando si sostituisce una ruota, la ruota stessa deve essere montata sgonfia e poi gonfiata con apposito compressore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '229', '133001', '2141', '11', 'Quando si sostituisce una ruota, al termine del montaggio occorre fare una prova di tenuta della valvola', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '229', '133001', '2142', '12', 'La ruota di scorta deve sempre essere tenuta ad una pressione di mezzo bar superiore a quella degli altri pneumatici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '230', '62001', '', '', 'Sostituzione di una ruota in un veicolo per il trasporto di persone 1 ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2143', '1', 'Quando si effettua il cambio di una ruota di un autobus è necessario fermarsi in un luogo sicuro e, se è possibile farlo in condizioni di sicurezza, far scendere le persone trasportate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2144', '2', 'Se, per necessità, è necessario cambiare la ruota di un autobus sulla corsia di emergenza o su una piazzola di sosta di un''autostrada, è opportuno non far scendere i passeggeri dal veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2145', '3', 'Quando si cambia una ruota di un autobus, non è sempre obbligatorio far scendere i passeggeri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2146', '4', 'Il cambio di una ruota di un autocarro o di un autobus, qualora il veicolo ingombri anche solo parzialmente la carreggiata, deve essere segnalato ai veicoli che sopraggiungono con il segnale mobile di pericolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2147', '5', 'Prima di effettuare il cambio di una ruota su un mezzo pesante, inserire il freno di stazionamento e applicare i cunei alle altre ruote per evitare spostamenti accidentali del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2148', '6', 'Il cambio di una ruota di un autocarro o di un autobus deve essere effettuato, se possibile, su una strada piana', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2149', '7', 'I martinetti idraulici in dotazione per la sostituzione di una ruota su un mezzo pesante, possono sollevare fino a 20 tonnellate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2150', '8', 'Con il martinetto idraulico in dotazione di un autobus per la sostituzione di una ruota è possibile sollevare l''autobus stesso, anche se carico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2151', '9', 'Il martinetto idraulico va posizionato vicino alla ruota da sostituire ed è necessario farlo appoggiare al telaio per il corretto sollevamento del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2152', '10', 'Quando si cambia una ruota di un autobus lungo la corsia di emergenza di un''autostrada, se i passeggeri lo chiedono, si può farli scendere dal veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2153', '11', 'In caso di cambio ruota su di un autobus non far mai scendere i passeggeri dal veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2154', '12', 'Prima di sollevare l''autobus per effettuare il cambio ruota è di norma obbligatorio far scendere i passeggeri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2155', '13', 'Quando si cambia la ruota di un rimorchio è di norma obbligatorio sganciarlo dalla motrice', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2156', '14', 'Prima di effettuare il cambio ruota bisogna inserire il freno di stazionamento, ma non è necessario applicare i cunei ferma ruota se il veicolo è su strada piana', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2157', '15', 'Prima di cambiare una ruota di un mezzo pesante è sufficiente applicare un solo cuneo ferma ruota, se si è inserito il freno di stazionamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2158', '16', 'Per poter sollevare un autobus con il martinetto idraulico in dotazione per la sostituzione di una ruota, è necessario far scendere tutti i passeggeri ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2159', '17', 'I martinetti idraulici in dotazione per la sostituzione di una ruota di un mezzo pesante possono sollevare fino a 900 chilogrammi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2160', '18', 'È possibile posizionare il martinetto idraulico in qualunque parte del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '230', '62001', '2161', '19', 'Gli autobus e gli autocarri, in caso di foratura di uno pneumatico, devono utilizzare il ruotino al posto della ruota di scorta', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '231', '62001', '', '', 'Sostituzione di una ruota in un veicolo per il trasporto di persone 2 ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '231', '62001', '2162', '1', 'Non bisogna attivare la valvola di scarico del martinetto idraulico, per abbassarlo, quando si è ancora sotto al veicolo, al fine di evitare di essere schiacciati dallo stesso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '231', '62001', '2163', '2', 'Se possibile, quando si solleva il veicolo per cambiare una ruota, è consigliabile mettere una tavoletta di legno sotto il martinetto idraulico per evitare che per il peso si affossi nel terreno', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '231', '62001', '2164', '3', 'Prima di sollevare il veicolo per la sostituzione di una ruota è opportuno avere a portata di mano la ruota di scorta e gli attrezzi necessari, affinché il veicolo resti sollevato per il minor tempo possibile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '231', '62001', '2165', '4', 'Prima di sollevare il veicolo per la sostituzione di una ruota allentare i dadi, se necessario aiutandosi con una leva', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '231', '62001', '2166', '5', 'Prima di sollevare il veicolo per la sostituzione di una ruota, assicurarsi di avere tutto l''occorrente per effettuare l''operazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '231', '62001', '2167', '6', 'Una volta montata la ruota di scorta su un autocarro o un autobus, dopo pochi chilometri di percorrenza, è necessario serrare nuovamente i dadi per essere sicuri del fissaggio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '231', '62001', '2168', '7', 'Quando si cambia una ruota, è necessario serrare i dadi con sequenza a croce e poco alla volta, per far sì che la ruota si avvicini correttamente al mozzo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '231', '62001', '2169', '8', 'Quando si sostituisce la ruota di un asse gemellato occorre assicurarsi di riposizionare il distanziale, se presente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '231', '62001', '2170', '9', 'Quando si sostituisce la ruota di un asse gemellato occorre assicurarsi che le due ruote non sfreghino tra di loro durante la marcia, per evitare pericolosi surriscaldamenti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '231', '62001', '2171', '10', 'Quando si solleva il veicolo con il martinetto idraulico, non si devono utilizzare tavolette di legno perché si tratta di materiale infiammabile', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '231', '62001', '2172', '11', 'In caso di sostituzione di una ruota di un autocarro o di un autobus, la ruota di scorta deve essere tolta dal suo vano solo dopo aver sollevato il veicolo con il martinetto idraulico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '231', '62001', '2173', '12', 'Quando si cambia una ruota, una volta messo il veicolo in sicurezza, è opportuno sollevarlo immediatamente con il martinetto idraulico e solo dopo prendere tutto l''occorrente per effettuare l''operazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '231', '62001', '2174', '13', 'Una volta montata la ruota di scorta di un autocarro o di un autobus, non è consigliato fermarsi per controllare il serraggio dei dadi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '231', '62001', '2175', '14', 'Quando si monta la ruota di scorta non bisogna serrare i dadi a fondo, per non danneggiarne il filetto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '231', '62001', '2176', '15', 'Quando si sostituisce la ruota di un asse gemellato, non è necessario riposizionare il distanziale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('11', '231', '62001', '2177', '16', 'Quando si sostituisce la ruota di un asse gemellato, bisogna assicurarsi che le ruote gemellate siano con i fianchi perfettamente a contatto tra di loro, così da ruotare in sincrono', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '232', '141001', '', '', 'Impianti di frenatura 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '232', '141001', '2178', '1', 'Il sistema di frenatura consente di trasformare in calore l''energia cinetica posseduta da un veicolo in movimento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '232', '141001', '2179', '2', 'Il sistema di frenatura pneumatico di tipo continuo ed automatico, installato sui veicoli pesanti, ha tempi di risposta più lenti rispetto ai sistemi di frenatura idraulici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '232', '141001', '2180', '3', 'Il sistema di frenatura di soccorso di un veicolo ha il compito di garantire l''arresto di emergenza in caso di avaria del sistema di servizio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '232', '141001', '2181', '4', 'Il sistema di frenatura di un veicolo pesante può essere di tipo pneumatico integrale o pneumoidraulico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '232', '141001', '2182', '5', 'Il compressore di un impianto di frenatura, ha il compito di inviare continuamente aria compressa nei serbatoi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '232', '141001', '2183', '6', 'La valvola di sicurezza contenuta nel regolatore di pressione di un impianto di frenatura pneumatico è tarata a una pressione compresa tra il valore minimo di funzionamento dell''impianto e il valore massimo a cui corrisponde l''apertura della valvola di scarico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '232', '141001', '2184', '7', 'I dispositivi di frenatura obbligatori per un autoveicolo sono il freno di servizio ed il freno a mano, che funge sempre anche da freno di soccorso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '232', '141001', '2185', '8', 'L''impianto di frenatura pneumoidraulico è sempre munito di un serbatoio ad azoto compresso per imprimere maggior potenza frenante all''impianto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '233', '141003', '', '', 'Impianti di frenatura 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '233', '141003', '2186', '1', 'La frenatura pneumatica è tipica dei veicoli di massa elevata, per i quali è necessaria una grande potenza frenante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '233', '141003', '2187', '2', 'Nella frenatura pneumoidraulica sono presenti sia un circuito idraulico che uno pneumatico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '233', '141003', '2188', '3', 'La forza necessaria alla frenatura di stazionamento di un autoveicolo con frenatura pneumatica è di tipo meccanico ed è garantita da molle', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '233', '141003', '2189', '4', 'In un autoveicolo devono essere presenti ed efficienti il freno di servizio, quello di stazionamento e quello di soccorso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '233', '141003', '2190', '5', 'Se si è alla guida di un autoveicolo di recente costruzione, la rottura del tubo dell''aria dei freni della ruota anteriore destra ci permette comunque di raggiungere un''officina, anche se a velocità estremamente ridotta', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '233', '141003', '2191', '6', 'Sugli autoveicoli di massa superiore a 3,5 tonnellate, il freno di soccorso interviene automaticamente in caso di malore del conducente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '233', '141003', '2192', '7', 'La frenatura di stazionamento e quella di soccorso sono due diversi modi di definire lo stesso tipo di frenatura', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '233', '141003', '2193', '8', 'Sugli autoveicoli dotati di frenatura pneumatica, la condotta moderabile del freno è sempre in pressione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '233', '141003', '2194', '9', 'Non esistono circuiti frenanti di tipo pneumoidraulico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '233', '141003', '2195', '10', 'Sugli autoveicoli dotati di frenatura pneumatica, la rottura del tubo dell''aria dei freni della ruota anteriore destra richiede l''arresto immediato del veicolo, ovunque ci si trovi, con contestuale richiesta di soccorso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '233', '141003', '2196', '11', 'Sugli autoveicoli dotati di frenatura pneumatica, la condotta automatica del freno non è mai in pressione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '233', '141003', '2197', '12', 'La pressione d''esercizio dell''impianto frenante pneumatico è di circa 25 bar', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '234', '141005', '', '', 'Frenatura', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '234', '141005', '2198', '1', 'La frenatura di un veicolo avviene grazie alla trasformazione della sua energia cinetica in calore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '234', '141005', '2199', '2', 'La frenatura di servizio è ripartita su tutte le ruote del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '234', '141005', '2200', '3', 'La frenatura degli autoveicoli è in genere ottenuta mediante freni ad attrito', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '234', '141005', '2201', '4', 'La massima forza frenante applicabile all''autoveicolo è pari al suo peso aderente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '234', '141005', '2202', '5', 'Per ottenere una buona frenatura occorre evitare il bloccaggio delle ruote', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '234', '141005', '2203', '6', 'La distribuzione dei pesi sul veicolo influisce sulla frenatura', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '234', '141005', '2204', '7', 'La distanza di frenatura minima si ottiene con il bloccaggio di tutte le ruote', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '234', '141005', '2205', '8', 'La distribuzione dei pesi sul veicolo non influisce sulla frenatura', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '234', '141005', '2206', '9', 'La massima forza frenante applicabile all''autoveicolo è pari a metà del suo peso aderente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '234', '141005', '2207', '10', 'Il riscaldamento dei freni ne migliora la funzionalità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '234', '141005', '2208', '11', 'I freni dei veicoli di massa complessiva a pieno carico superiore a 7,5 tonnellate sono raffreddati ad acqua', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '234', '141005', '2209', '12', 'Sui veicoli a 3 assi, la frenatura è ripartita tra l''asse anteriore e uno dei due assi posteriori', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '235', '1401006', '', '', 'Impianto di frenatura: tipi e caratteristiche generali', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '235', '1401006', '2210', '1', 'Gli autoveicoli devono essere dotati di 3 sistemi di frenatura: servizio, soccorso, stazionamento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '235', '1401006', '2211', '2', 'La frenatura di stazionamento di un autoveicolo deve essere ad azione puramente meccanica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '235', '1401006', '2212', '3', 'La frenatura di servizio degli autoveicoli deve agire su tutte le ruote', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '235', '1401006', '2213', '4', 'La frenatura di servizio degli autoveicoli deve essere moderabile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '235', '1401006', '2214', '5', 'La frenatura di servizio degli autoveicoli deve essere azionabile dal conducente dal proprio posto di guida senza togliere le mani dal volante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '235', '1401006', '2215', '6', 'La frenatura di soccorso deve essere moderabile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '235', '1401006', '2216', '7', 'La frenatura di soccorso viene normalmente realizzata con l''uso a fondo del freno di stazionamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '235', '1401006', '2217', '8', 'La frenatura di stazionamento è in genere conglobata col servofreno', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '235', '1401006', '2218', '9', 'La frenatura di servizio degli autoveicoli deve essere di tipo a impulsi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '235', '1401006', '2219', '10', 'Il comando del freno di stazionamento è lo stesso del freno di servizio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '235', '1401006', '2220', '11', 'Su tutti gli autoveicoli devono essere presenti idonei cunei per il fermo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '236', '1401007', '', '', 'Impianto di frenatura pneumatici: caratteristiche e componenti', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2221', '1', 'Fanno parte dell’impianto di frenatura pneumatica il compressore e serbatoi dell’aria compressa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2222', '2', 'Quando il veicolo è in marcia, nell''impianto di frenatura pneumatico deve esservi sempre aria in pressione nel tratto di impianto compreso tra il compressore e il distributore duplex', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2223', '3', 'Nell''impianto di frenatura pneumoidraulico (freno misto), i convertitori trasformano la pressione dell''aria in pressione idraulica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2224', '4', 'L’impianto di frenatura pneumatico integrale o quello pneumoidraulico, sono diviv in più sezioni indipendenti, una per le ruote anteriori, una per quelle posteriori ed eventualmente una per il rimorchio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2225', '5', 'Nei veicoli con impianto di frenatura ad aria compressa, appositi dispositivi modulatori regolano la pressione dell''aria diretta agli elementi frenanti dell''asse posteriore in base al carico del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2226', '6', 'Se l''impianto di frenatura pneumatica o pneumoidraulica non è in pressione, il veicolo non può essere messo in movimento perché non è possibile disinserire il freno di stazionamento a molla', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2227', '7', 'Se l''impianto di frenatura è tipo pneumatico, prima della partenza è necessario accertarsi che nei serbatoi vi sia pressione sufficiente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2228', '8', 'Sugli autoveicoli dotati di frenatura pneumatica, se si verifica una perdita di aria in una sezione dell''impianto di frenatura, le altre sezioni possono funzionare regolarmente; tale struttura costituisce il freno di soccorso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2229', '9', 'A partire dalla valvola protezione serbatoi, l''impianto di frenatura di servizio ad aria compressa è costituito da due sezioni distinte più una eventuale per il rimorchio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2230', '10', 'In caso di avaria in una sezione dei freni, anche se le altre sezioni funzionano, non si deve intraprendere un nuovo viaggio se non si è provveduto alla riparazione del guasto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2231', '11', 'I serbatoi dell''aria dei veicoli dotati di frenatura pneumatica o pneumoidraulica, sono normalmente: uno per ogni sezione dell''impianto, uno per i servizi ed eventualmente per le sospensioni pneumatiche', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2232', '12', 'I serbatoi dell''aria compressa degli impianti di frenatura dei mezzi pesanti sono dotati di un dispositivo per lo scarico dell''acqua di condensa accumulatasi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2233', '13', 'Gli elementi frenanti dei mezzi pesanti sono costituiti da tamburi e ganasce o da disco e pastiglie', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2234', '14', 'Sugli autoveicoli dotati di frenatura pneumatica, il modulatore di frenata regola automaticamente la pressione dell''aria inviata agli elementi frenanti in base al carico del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2235', '15', 'Negli impianti di frenatura pneumatica, l''anticongelatore è necessario perché l''aria, quando è molto fredda, ha poca pressione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2236', '16', 'Gli impianti di frenatura di tipo pneumoidraulico non possono essere utilizzati su veicoli per trasporto persone per motivi di sicurezza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2237', '17', 'Il freno automatico è presente su tutti gli autocarri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2238', '18', 'In caso di avaria in una sezione dell''impianto di frenatura ad aria, il veicolo può venire usato normalmente perché i freni restano sempre pienamente efficienti grazie all''altra sezione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2239', '19', 'In un impianto di frenatura pneumoidraulico, è l''olio che comprime l''aria diretta agli elementi frenanti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2240', '20', 'Sugli autoveicoli dotati di frenatura pneumatica, quando cessa la frenatura, l''aria compressa si scarica dai freni attraverso il tubo di scappamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2241', '21', 'Gli impianti frenanti ad aria compressa non possono essere dotati di ABS', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2242', '22', 'In un impianto di frenatura pneumatico per veicoli molto pesanti, l''aria compressa viene sostituita con azoto compresso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2243', '23', 'In un impianto di frenatura pneumoidraulico, gli elementi frenanti sono sempre del tipo a disco e pastiglie, negli impianti pneumatici integrali sono invece solamente di tipo a tamburo e ganasce', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2244', '24', 'In un impianto di frenatura pneumoidraulico, l''olio dei convertitori pneumoidraulici viene raffreddato in apposito radiatore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '236', '1401007', '2245', '25', 'In un impianto di frenatura pneumatico, il compressore dell''aria viene attivato dal conducente alla partenza tramite apposito pulsante posto sul cruscotto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '237', '1401008', '', '', 'Impianto di frenatura pneumatici: freno di stanziament', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '237', '1401008', '2246', '1', 'Il freno di stazionamento dei veicoli dotati di freni pneumatici agisce solo sulle ruote posteriori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '237', '1401008', '2247', '2', 'Il freno di stazionamento dei veicoli dotati di freni pneumatici è un freno ad azione meccanica e si attiva scaricando l''aria dai bielementi a molla', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '237', '1401008', '2248', '3', 'Il freno di stazionamento dei veicoli dotati di freni pneumatici cessa la sua azione quando, tramite il distributore a mano, gli viene inviata aria compressa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '237', '1401008', '2249', '4', 'Il freno di stazionamento dei veicoli dotati di freni pneumatici, se moderabile, può essere utilizzato per la frenatura di soccorso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '237', '1401008', '2250', '5', 'Sugli autoveicoli dotati di frenatura pneumatica, il freno di stazionamento, si inserisce quando il freno a molla riceve aria dal distributore a mano', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '237', '1401008', '2251', '6', 'Il freno di stazionamento dei veicoli dotati di freni pneumatici, se non moderabile, può fare parte dell''impianto di frenatura di soccorso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '237', '1401008', '2252', '7', 'Il freno di stazionamento dei veicoli dotati di freni pneumatici, può essere azionato solo da un pedale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '237', '1401008', '2253', '8', 'Il freno di stazionamento a molla dei veicoli dotati di freni pneumatici, cessa di funzionare se i serbatoi dell''aria compressa non sono carichi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '238', '101001/1401003/141004', '', '', 'Impianti di frenatura - bielemento', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '238', '101001/1401003/141004', '2254', '1', 'L''azionamento del freno di stazionamento comporta l''espulsione dell''aria dal bielemento frenante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '238', '101001/1401003/141004', '2255', '2', 'La frenatura di stazionamento di un autoveicolo con frenatura pneumatica, avviene per svuotamento di una camera del bielemento frenante (contenente aria compressa a veicolo sfrenato)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '238', '101001/1401003/141004', '2256', '3', 'In presenza di bielemento frenante, il freno di stazionamento utilizza la forza meccanica fornita da una molla', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '238', '101001/1401003/141004', '2257', '4', 'Per trainare un veicolo in avaria dotato di bielemento frenante, si può sbloccare il freno di stazionamento agendo su un dispositivo meccanico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '238', '101001/1401003/141004', '2258', '5', 'Spostare a mano un rimorchio sganciato dalla motrice è impossibile dato che resta in ogni caso frenato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '239', '141002', '', '', 'Elementi Frenanti – Freno a disco', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '239', '141002', '2259', '1', 'Fa parte del freno a disco di un autoveicolo, un disco metallico che gira solidale con la ruota', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '239', '141002', '2260', '2', 'È parte del freno a disco di un autoveicolo, una pinza che contiene gli elementi d''attrito e, tramite un sistema idraulico, permette di stringerli fortemente al disco durante la frenatura', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '239', '141002', '2261', '3', 'Fanno parte del freno a disco i pistoncini, azionati da una pompa idraulica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '239', '141002', '2262', '4', 'I freni a disco possono essere del tipo autoventilato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '239', '141002', '2263', '5', 'I freni a disco non possono essere montati su veicoli dotati di rallentatore elettromagnetico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '239', '141002', '2264', '6', 'Fanno parte del freno a disco i pistoncini, azionati direttamente dall''aria compressa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '239', '141002', '2265', '7', 'I freni a disco vengono impiegati soltanto sulle autovetture', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '239', '141002', '2266', '8', 'I freni a disco sono poco usati sui veicoli industriali perché scaldano troppo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '240', '141002', '', '', 'Elementi Frenanti – Freno a tamburo', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '240', '141002', '2267', '1', 'È parte del freno a tamburo, un''asta con puntale a cuneo che, spinta da una membrana, permette di schiacciare le ganasce contro i tamburi generando attrito', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '240', '141002', '2268', '2', 'Nei freni a tamburo, le ganasce sono ricoperte di guarnizione di attrito (ferodo)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '240', '141002', '2269', '3', 'I freni a tamburo possono essere del tipo autoventilato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '240', '141002', '2270', '4', 'I freni a tamburo non possono essere montati su veicoli dotati di rallentatore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '240', '141002', '2271', '5', 'È parte del freno a tamburo un disco metallico attorno al quale si stringono le ganasce ricoperte di guarnizione di attrito (ferodo)', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '240', '141002', '2272', '6', 'I freni a disco e quelli a tamburo sono costruttivamente uguali, a parte il tipo di guarnizione di attrito usata in ciascuno dei due', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '240', '141002', '2273', '7', 'Nei veicoli pesanti di maggiori dimensioni (M3 ed N3), su ogni ruota sono montati insieme sia un freno a disco che uno a tamburo, per avere una maggior potenza frenante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '241', '141013', '', '', 'Elementi frenanti', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '241', '141013', '2274', '1', 'Gli elementi frenanti dei veicoli stradali possono essere del tipo a tamburo e ganasce dotate di guarnizione di attrito (ferodo)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '241', '141013', '2275', '2', 'Gli elementi frenanti dei veicoli stradali possono essere del tipo a disco e pastiglie dotate di guarnizione di attrito (ferodo)', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '241', '141013', '2276', '3', 'Gli elementi frenanti dei veicoli stradali pesanti sono del tipo ad attrito', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '241', '141013', '2277', '4', 'Gli elementi frenanti dei veicoli possono avere problemi di surriscaldamento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '241', '141013', '2278', '5', 'Gli elementi frenanti dei veicoli, trasformano l''energia cinetica posseduta dal veicolo in calore da disperdere in atmosfera', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '241', '141013', '2279', '6', 'Gli elementi frenanti possono essere del tipo a disco e ganasce dotate di guarnizione di attrito (ferodo)', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '241', '141013', '2280', '7', 'Gli elementi frenanti possono essere del tipo a ganasce e pastiglie dotate di guarnizione di attrito (ferodo)', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '241', '141013', '2281', '8', 'Gli elementi frenanti dei veicoli possono avere problemi di eccessiva ventilazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '241', '141013', '2282', '9', 'Gli elementi frenanti dei veicoli stradali pesanti sono del tipo elettromagnetico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '241', '141013', '2283', '10', 'I veicoli stradali di massa complessiva a pieno carico superiore a 7,5 tonnellate, devono essere dotati di elementi frenanti raffreddati a liquido', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '241', '141013', '2284', '11', 'I veicoli stradali di massa complessiva a pieno carico superiore a 3,5 tonnellate, non possono essere dotati di elementi frenanti autoventilanti per pericolo di rottura del disco', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '242', '141015', '', '', 'Compressore dell''impianto di frenatura penumatico', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '242', '141015', '2285', '1', 'Sui veicoli pesanti, il compressore dell''impianto di frenatura pneumatico o pneumoidraulico prende in genere movimento dal motore del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '242', '141015', '2286', '2', 'Sui veicoli pesanti, il compressore dell''impianto di frenatura pneumatico o pneumoidraulico aspira aria dall''esterno attraverso un filtro e la invia a pressione verso i serbatoi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '242', '141015', '2287', '3', 'Sui veicoli pesanti, il compressore dell''impianto di frenatura pneumatico o pneumoidraulico necessita di raffreddamento e lubrificazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '242', '141015', '2288', '4', 'Sui veicoli pesanti, il compressore dell''impianto di frenatura pneumatico o pneumoidraulico è alimentato con il combustibile del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '242', '141015', '2289', '5', 'Sui veicoli pesanti, il compressore dell''impianto di frenatura pneumatico o pneumoidraulico è alimentato da miscela al 4% di olio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '242', '141015', '2290', '6', 'Sugli autoveicoli dotati di frenatura pneumoidraulica, il compressore può essere utilizzato come freno motore, invertendone il senso di rotazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '243', '1401011', '', '', 'Impianto di frenatura pneumatici: essicatore e anticongelatore 1401011', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '243', '1401011', '2291', '1', 'L''essiccatore o l''anticongelatore si trovano tra il compressore ed i serbatoi dell''aria compressa dell''impianto di frenatura pneumatico o pneumoidraulico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '243', '1401011', '2292', '2', 'L''essiccatore o l''anticongelatore di cui sono dotati gli impianti di frenatura ad aria compressa, evitano che si formino cristalli di ghiaccio nelle tubazioni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '243', '1401011', '2293', '3', 'L''essiccatore o l''anticongelatore di cui sono dotati gli impianti di frenatura ad aria compressa, evitano che si formino particelle di ghiaccio nelle valvole', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '243', '1401011', '2294', '4', 'Sugli autoveicoli dotati di frenatura pneumatica, l''essiccatore può essere raggruppato in un unico apparecchio assieme al regolatore di pressione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '243', '1401011', '2295', '5', 'Sugli autoveicoli dotati di frenatura pneumatica, l''essiccatore provvede anche al filtraggio dell''aria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '243', '1401011', '2296', '6', 'L''anticongelatore impedisce il congelamento dell''aria nei serbatoi dei freni pneumatici e pneumoidraulici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '243', '1401011', '2297', '7', 'Negli impianti di frenatura ad aria compressa, l''essiccatore serve a impedire che il compressore aspiri aria troppo umida', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '243', '1401011', '2298', '8', 'Sugli autoveicoli dotati di frenatura pneumatica, l''essiccatore deve essere messo in funzione manualmente dal conducente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '243', '1401011', '2299', '9', 'Nell''essiccatore di un impianto frenante pneumatico, una volta asciugata l''aria, vengono aggiunte piccole particelle di lubrificante per oliare le valvole del circuito dell''aria compressa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '243', '1401011', '2300', '10', 'Nell''impianto di frenatura pneumatico, vi sono tanti essiccatori quanti sono i serbatoi dell''aria compressa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '244', '141002', '', '', 'Regolatore di pressione di un impianto frenante 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '244', '141002', '2301', '1', 'In un impianto frenante pneumatico, il regolatore di pressione ha il compito di mantenere l''impianto ad una pressione non superiore a un limite prefissato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '244', '141002', '2302', '2', 'Il regolatore di pressione di un impianto frenante pneumatico o pneumoidraulico comprende una valvola di ritenuta, che impedisce la caduta di pressione dell''impianto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '244', '141002', '2303', '3', 'Il regolatore di pressione di un impianto frenante pneumatico o pneumoidraulico comprende una valvola di scarico, che scarica in atmosfera l''aria in eccesso quando si è raggiunto il valore di pressione massimo stabilito', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '244', '141002', '2304', '4', 'Il regolatore di pressione di un impianto frenante pneumatico o pneumoidraulico comprende una valvola di sicurezza, che scarica l''aria in atmosfera nel caso di avarie del dispositivo che abbiano causato anomali aumenti di pressione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '244', '141002', '2305', '5', 'Il regolatore di pressione di un impianto frenante pneumatico o pneumoidraulico è collegato direttamente alla catena di distribuzione del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '244', '141002', '2306', '6', 'Il regolatore di pressione di un impianto frenante pneumatico o pneumoidraulico ha il compito di inviare aria in pressione direttamente agli elementi frenanti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '244', '141002', '2307', '7', 'In un impianto frenante pneumatico, il regolatore di pressione ha il compito di mantenere l''impianto a una pressione sempre superiore a 18 bar', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '245', '141012', '', '', 'Regolatore di pressione di un impianto frenante 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '245', '141012', '2308', '1', 'Negli impianti di frenatura ad aria compressa, il regolatore di pressione permette di mantenere l''aria dei circuiti frenanti ad una pressione compresa tra due valori, massimo e minimo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '245', '141012', '2309', '2', 'Negli impianti di frenatura ad aria compressa, il regolatore di pressione permette di evitare sovrappressioni nell''impianto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '245', '141012', '2310', '3', 'Negli impianti di frenatura ad aria compressa, il regolatore di pressione è dotato di una valvola di sicurezza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '245', '141012', '2311', '4', 'Negli impianti di frenatura ad aria compressa, il regolatore di pressione prevede anche fasi di funzionamento a vuoto del compressore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '245', '141012', '2312', '5', 'Negli impianti di frenatura ad aria compressa, il regolatore di pressione prevede anche fasi in cui scarica aria in atmosfera', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '245', '141012', '2313', '6', 'Negli impianti di frenatura ad aria compressa, il regolatore di pressione permette di mantenere l''aria dei circuiti frenanti alla pressione fissa di quindici bar', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '245', '141012', '2314', '7', 'Negli impianti di frenatura ad aria compressa, il regolatore di pressione ha il compito di regolare il valore della pressione dell''aria inviata agli elementi frenanti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '245', '141012', '2315', '8', 'Negli impianti di frenatura ad aria compressa, il regolatore di pressione ha il compito di regolare il valore della pressione dell''aria all''interno delle sospensioni pneumatiche', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '245', '141012', '2316', '9', 'Negli impianti di frenatura ad aria compressa, il regolatore di pressione non è necessario se il veicolo è dotato di sospensioni pneumatiche', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '245', '141012', '2317', '10', 'Negli impianti di frenatura ad aria compressa, il regolatore di pressione è comandato dal conducente attraverso apposito manettino posto sul cruscott', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '245', '141012', '2318', '11', 'Nel regolatore di pressione di un impianto frenante pneumatico, la valvola di sicurezza deve restare sempre aperta ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '245', '141012', '2319', '12', 'Nel regolatore di pressione di un impianto frenante pneumatico, la valvola di sicurezza si apre al raggiungimento della pressione di esercizio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '245', '141012', '2320', '13', 'Nel regolatore di pressione di un impianto frenante pneumatico, la valvola di scarico si deve aprire appena acceso il motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '246', '1001001', '', '', 'regolatore di pressione', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '246', '1001001', '2321', '1', 'Quando si raggiunge la pressione massima nei serbatoi, il regolatore di pressione impedisce che siano alimentati con altra aria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '247', '1401017', '', '', 'Impianto di frenatura pneumatici: caratteristiche e componenti', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '247', '1401017', '2322', '1', 'Negli impianti di frenatura ad aria compressa, è presente un indicatore di pressione per ciascuna sezione dell''impianto di frenatura di servizio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '247', '1401017', '2323', '2', 'Negli impianti di frenatura ad aria compressa, l''indicatore di pressione è integrato da una spia rossa che segnala la bassa pressione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '247', '1401017', '2324', '3', 'Negli impianti di frenatura ad aria compressa, il pedale del freno comanda un distributore che invia l''aria agli elementi frenanti a pressione moderabile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '247', '1401017', '2325', '4', 'Negli impianti di frenatura ad aria compressa, la pressione dell''aria nei serbatoi dei freni viene mantenuta alla giusta pressione da un regolatore automatico di pressione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '247', '1401017', '2326', '5', 'Negli impianti di frenatura ad aria compressa, la pressione dell''aria negli elementi frenanti è tanto più elevata, quanto maggiore è la pressione esercitata sul pedale del freno', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '247', '1401017', '2327', '6', 'Sugli autoveicoli dotati di frenatura pneumatica o pneumoidraulica, per evitare che, in caso di rottura della condotta che porta aria ai serbatoi, tutti i serbatoi dell''impianto frenante si svuotino, tra regolatore di pressione e serbatoi è inserita una particolare valvola a più vie', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '247', '1401017', '2328', '7', 'Sugli autoveicoli dotati di frenatura pneumatica o pneumoidraulica, la pressione dell''aria nei serbatoi dei freni ad aria compressa deve essere più alta se il veicolo è molto carico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '247', '1401017', '2329', '8', 'Sugli autoveicoli dotati di frenatura pneumatica o pneumoidraulica, la pressione dell''aria nei serbatoi dei freni pneumatici deve essere più bassa se il veicolo viaggia a vuoto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '247', '1401017', '2330', '9', 'Sugli autoveicoli dotati di frenatura pneumatica o pneumoidraulica, in caso di perdita di pressione da una sezione dell''impianto ad aria compressa, il freno automatico blocca istantaneamente l''autoveicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '247', '1401017', '2331', '10', 'Gli impianti frenanti ad aria compressa sono dotati di un serbatoio dell''aria per ogni ruota', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '247', '1401017', '2332', '11', 'Negli impianti frenanti ad aria compressa, il conducente regola la pressione dell''aria della frenatura di servizio tramite un apposito manettino posto sul cruscotto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '247', '1401017', '2333', '12', 'Sugli autoveicoli dotati di frenatura pneumatica o pneumoidraulica, a seconda della forza frenante richiesta, l''autista può selezionare la modalità di frenatura continua o quella moderabile', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '248', '141004', '', '', 'Impianti di frenatura 4', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '248', '141004', '2334', '1', 'Sugli autoveicoli dotati di frenatura pneumatica, all''interno dei serbatoi dell''aria può depositarsi, per condensazione, una certa quantità di acqua', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '248', '141004', '2335', '2', 'Nella manutenzione ordinaria degli autoveicoli dotati di frenatura pneumatica, è buona norma spurgare periodicamente i serbatoi dell''aria compressa, eliminando l''acqua eventualmente formatasi per condensazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '248', '141004', '2336', '3', 'Occorre sempre controllare che la pressione nei serbatoi principali dell''impianto frenante pneumatico non scenda al di sotto del valore minimo previsto dalla casa costruttrice del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '248', '141004', '2337', '4', 'Sugli autoveicoli dotati di frenatura pneumatica, il distributore a mano permette la frenatura di stazionamento immettendo aria nei bielementi frenanti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '248', '141004', '2338', '5', 'Sugli autoveicoli dotati di frenatura pneumatica, la presenza di acqua nei serbatoi è utile perché aumenta la pressione dell''aria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '248', '141004', '2339', '6', 'In caso di svuotamento dei serbatoi dell''aria compressa l''autoveicolo rimane sfrenato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '248', '141004', '2340', '7', 'Sugli autoveicoli dotati di frenatura pneumatica, occorre sempre controllare che la pressione nei serbatoi principali dell''impianto frenante sia pari a quella delle sospensioni pneumatiche', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '248', '141004', '2341', '8', 'Sugli autoveicoli dotati di frenatura pneumatica, l''azionamento del freno di stazionamento può essere effettuato anche con veicolo in marcia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '249', '141016', '', '', 'Impianti di frenatura 7', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '249', '141016', '2342', '1', 'Eventuali formazioni di ghiaccio nelle tubazioni dei freni ad aria compressa possono bloccare le valvole del regolatore di pressione e del distributore duplex', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '249', '141016', '2343', '2', 'Negli autoveicoli dotati di frenatura pneumoidraulica, è bene effettuare periodicamente lo spurgo dell''acqua di condensa formatasi nei serbatoi per evitare il pericolo che si formi del ghiaccio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '249', '141016', '2344', '3', 'Sugli autoveicoli dotati di frenatura pneumoidraulica, all''avviamento del veicolo, può essere necessario un breve lasso di tempo perché il compressore dell''impianto di frenatura ricarichi i serbatoi dell''aria compressa dei freni fino alla pressione minima di esercizio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '249', '141016', '2345', '4', 'Come sui treni, anche su tutti gli autobus è obbligatorio un freno di emergenza a disposizione dei viaggiatori', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '249', '141016', '2346', '5', 'Sugli autobus deve essere presente, a disposizione dei passeggeri, un martelletto, posto in una cassetta con vetro a frantumazione, che permetta di azionare i freni di emergenza in caso di incendio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '250', '141009', '', '', 'Distributore duplex - 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '250', '141009', '2347', '1', 'Nei veicoli dotati di frenatura ad aria compressa, il modulatore della frenata è situato tra il distributore duplex e gli elementi frenanti posteriori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '250', '141009', '2348', '2', 'Sugli autoveicoli dotati di frenatura pneumatica, agendo sul pedale del freno, il distributore duplex invia aria compressa in due sezioni dell''impianto non comunicanti tra loro', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '250', '141009', '2349', '3', 'I sistemi di frenatura pneumatica di servizio e di soccorso dei veicoli pesanti prevedono almeno due serbatoi per l''aria compressa, un distributore duplex e una sezione di tubazioni indipendenti per ciascuno dei due assi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '250', '141009', '2350', '4', 'Sugli autoveicoli dotati di frenatura pneumatica, il distributore duplex serve, tra l''altro, a regolare la pressione dell''aria inviata agli elementi frenanti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '250', '141009', '2351', '5', 'Sugli autoveicoli dotati di frenatura pneumatica, il distributore duplex invia aria alla stessa pressione agli elementi frenanti anteriori e posteriori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '250', '141009', '2352', '6', 'Sugli autoveicoli dotati di frenatura pneumatica, il distributore duplex ha due comandi: uno a pedale e l''altro a mano', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '250', '141009', '2353', '7', 'Sugli autoveicoli dotati di frenatura pneumatica, il distributore duplex invia aria compressa alternativamente ai due serbatoi principali', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '250', '141009', '2354', '8', 'Sugli autoveicoli dotati di frenatura pneumatica, il distributore duplex raddoppia la pressione dell''aria quando il veicolo è carico e sta percorrendo una discesa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '250', '141009', '2355', '9', 'Sugli autoveicoli dotati di frenatura pneumatica, il distributore duplex serve a inviare differente pressione agli elementi frenanti anteriori o posteriori', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '250', '141009', '2356', '10', 'Sugli autoveicoli dotati di frenatura pneumatica, il distributore duplex non può garantire la frenatura di soccorso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '251', '101002', '', '', 'Distributore duplex - 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '251', '101002', '2357', '1', 'Il distributore duplex viene azionato dal conducente tramite il pedale del freno', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '251', '101002', '2358', '2', 'Il distributore duplex ha il compito di realizzare la frenatura di servizio, garantendo quella di soccorso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '251', '101002', '2359', '3', 'La valvola di scarico del distributore duplex si apre quando il conducente rilascia il pedale del freno', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '251', '101002', '2360', '4', 'Il distributore duplex è diviso in due sezioni separate, una per frenare l''asse anteriore e una per frenare quello posteriore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '251', '101002', '2361', '5', 'Nell''impianto di frenatura pneumatico, il distributore duplex garantisce la frenatura di stazionamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '251', '101002', '2362', '6', 'Il distributore duplex viene comandato a mano per ottenere la frenatura di stazionamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '251', '101002', '2363', '7', 'Il distributore duplex è dotato di sole valvole d''immissione perché deve immagazzinare l''aria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '251', '101002', '2364', '8', 'Nel distributore duplex, le valvole di scarico si aprono solo in caso di avaria all''impianto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '251', '101002', '2365', '9', 'Nell''impianto di frenatura pneumatico, il distributore duplex garantisce la frenatura del rimorchio in caso di guasto della condotta del moderabile', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '251', '101002', '2366', '10', 'Nell''impianto di frenatura pneumatico, il distributore duplex garantisce la frenatura del rimorchio in caso di guasto della condotta dell''automatico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '251', '101002', '2367', '11', 'Nell''impianto di frenatura pneumatico, il distributore duplex non è montato sulle motrici dotate di freni pneumoidraulici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '251', '101002', '2368', '12', 'Il circuito frenante del rimorchio è comandato da un secondo distributore duplex, che si trova subito a valle del giunto di accoppiamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '252', '101004', '', '', 'Modulatore di pressione in funzione del carico - 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '252', '101004', '2369', '1', 'Il modulatore di pressione in funzione del carico può essere montato sia sui rimorchi che sulle motrici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '252', '101004', '2370', '2', 'Il modulatore di pressione in funzione del carico è montato sul telaio ed è collegato con una leva all''assale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '252', '101004', '2371', '3', 'Il modulatore di pressione in funzione del carico garantisce la progressività della frenata col variare del carico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '252', '101004', '2372', '4', 'Il modulatore di pressione in funzione del carico controlla, generalmente, l''intensità della forza frenante applicata agli assi posteriori della motrice', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '252', '101004', '2373', '5', 'Il modulatore di pressione in funzione del carico, se presente sul rimorchio, ne controlla la frenatura di tutti gli assi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '252', '101004', '2374', '6', 'Il modulatore di pressione in funzione del carico serve a dosare la forza frenante in base al carico gravante su un asse', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '252', '101004', '2375', '7', 'Il modulatore di pressione in funzione del carico funziona automaticamente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '252', '101004', '2376', '8', 'Il modulatore di pressione in funzione del carico, in base alla distanza tra assale e telaio determinatasi in base al carico, modula la forza frenante da applicare all''asse stesso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '252', '101004', '2377', '9', 'Il modulatore di pressione in funzione del carico, limita la possibilità che le ruote dell''assale cui è applicato si blocchino in caso di frenata a veicolo scarico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '252', '101004', '2378', '10', 'Il modulatore di pressione in funzione del carico non è montato sui rimorchi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '252', '101004', '2379', '11', 'Il modulatore di pressione in funzione del carico è messo in funzione dal conducente prima della partenza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '252', '101004', '2380', '12', 'Il modulatore di pressione in funzione del carico è montato direttamente sull''assale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '252', '101004', '2381', '13', 'Il modulatore di pressione in funzione del carico garantisce la frenatura del rimorchio in caso di distacco accidentale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '252', '101004', '2382', '14', 'Il modulatore di pressione in funzione del carico garantisce la frenatura del rimorchio in caso di guasto del serbatoio del rimorchio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '253', '141010', '', '', 'Modulatore di pressione in funzione del carico - 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '253', '141010', '2383', '1', 'Nei veicoli dotati di frenatura ad aria compressa, il modulatore di frenata è situato tra il distributore duplex e gli elementi frenanti dell''asse posteriore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '253', '141010', '2384', '2', 'Sugli autoveicoli dotati di frenatura pneumatica, il modulatore di frenata in funzione del carico ha il compito di ridurre la pressione dell''aria inviata agli elementi frenanti dell''asse posteriore quando il veicolo non circola a pieno carico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '253', '141010', '2385', '3', 'Sugli autoveicoli dotati di frenatura pneumatica, il modulatore di frenata in funzione del carico ha il compito di evitare frenate a ruote bloccate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '253', '141010', '2386', '4', 'Sugli autoveicoli dotati di frenatura pneumatica e sospensioni a balestra, il modulatore di frenata in funzione del carico è normalmente fissato al telaio del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '253', '141010', '2387', '5', 'Sugli autoveicoli dotati di frenatura pneumatica, il modulatore di frenata in funzione del carico può essere a comando pneumatico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '253', '141010', '2388', '6', 'Sugli autoveicoli dotati di frenatura pneumatica, il modulatore di frenata in funzione del carico viene inserito e disinserito dal conducente a mezzo di apposito pulsante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '253', '141010', '2389', '7', 'Il modulatore di frenata in funzione del carico non può essere montato su veicoli dotati di ABS', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '253', '141010', '2390', '8', 'Sugli autoveicoli dotati di frenatura pneumatica, il modulatore di frenata in funzione  del carico è normalmente fissato sull''assale delle ruote', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '253', '141010', '2391', '9', 'Sugli autoveicoli dotati di frenatura pneumatica, il modulatore di frenata in funzione del carico può essere a comando idraulico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '253', '141010', '2392', '10', 'Il modulatore di frenata in funzione del carico necessita di raffreddamento ad aria', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '253', '141010', '2393', '11', ' Sugli autoveicoli dotati di frenatura pneumatica, il dispositivo modulatore di pressione in funzione del carico esclude l''ABS quando il veicolo è molto carico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '254', '141014', '', '', 'Componenti dei sistemi di frenatura 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '254', '141014', '2394', '1', 'Nei veicoli con frenatura di tipo pneumoidraulico, gli elementi frenanti sono azionati dalla pressione idraulica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '254', '141014', '2395', '2', 'Il freno di tipo pneumoidraulico funziona grazie all''azione combinata dell''aria e dell''olio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '254', '141014', '2396', '3', 'L''impianto di frenatura di tipo pneumoidraulico è composto, tra l''altro, da un compressore, da alcuni serbatoi d''accumulo per l''aria compressa e dai convertitori pneumoidraulici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '254', '141014', '2397', '4', 'Se l''impianto di frenatura è del tipo pneumoidraulico, l''aria compressa viene inviata dal distributore ai convertitori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '254', '141014', '2398', '5', 'Le ganasce di un impianto di frenatura pneumoidraulico, vengono allargate da due pistoncini spinti dalla pressione idraulica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '254', '141014', '2399', '6', 'Sugli autoveicoli dotati di frenatura pneumoidraulica, il convertitore pneumoidraulico sfrutta la pressione dell''aria per mettere in pressione uno speciale liquido idraulico che agisce sugli elementi frenanti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '254', '141014', '2400', '7', 'Sugli autoveicoli dotati di frenatura pneumoidraulica, il liquido del convertitore pneumoidraulico è sostanzialmente incomprimibile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '254', '141014', '2401', '8', 'Negli autoveicoli dotati di frenatura pneumoidraulica, il liquido del convertitore pneumoidraulico va periodicamente sostituito', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '254', '141014', '2402', '9', 'Negli autoveicoli dotati di frenatura pneumoidraulica, può essere necessario rabboccare periodicamente il liquido del convertitore pneumoidraulico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '254', '141014', '2403', '10', 'Sugli autoveicoli dotati di frenatura pneumoidraulica, il convertitore di pressione pneumoidraulico, serve a trasformare la pressione di un liquido in aria fortemente compressa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '254', '141014', '2404', '11', 'Sugli autoveicoli dotati di frenatura pneumoidraulica, il convertitore di pressione pneumoidraulico è posto tra il compressore ed i serbatoi dell''aria compressa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '254', '141014', '2405', '12', 'Un autocarro pesante non può avere i freni pneumoidraulici perché troppo elastici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '254', '141014', '2406', '13', 'Gli impianti di frenatura pneumoidraulici, detti anche freni misti, funzionano con una miscela di aria e olio preparata dal distributore duplex', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '254', '141014', '2407', '14', 'Negli impianti di frenatura pneumoidraulici, deve essere presente un miscelatore per l''aria e l''olio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '254', '141014', '2408', '15', 'Se si verifica una perdita di olio dai freni pneumoidraulici, il conducente può convertire il funzionamento dei freni a pneumatico integrale escludendo i convertitori', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '254', '141014', '2409', '16', 'Sugli autoveicoli dotati di frenatura pneumoidraulica, l''olio dell''impianto frenante pneumoidraulico serve a mantenere ben lubrificati gli elementi frenanti, anche nelle forti discese', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '254', '141014', '2410', '17', 'Gli autoveicoli dotati di impianto frenante pneumoidraulico non possono essere atti al traino', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '255', '75001', '', '', 'Freno motore 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '255', '75001', '2411', '1', 'Azionando il freno motore dei veicoli pesanti, il dispositivo chiude la mandata del gasolio da parte del sistema di iniezione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '255', '75001', '2412', '2', 'Azionando il freno motore dei veicoli pesanti, si crea una strozzatura del tubo di scarico tramite l''azionamento di un''apposita valvola a farfalla', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '255', '75001', '2413', '3', 'Azionando il freno motore dei veicoli pesanti, la fase di scarico del motore diventa in pratica una fase di compressione che frena/rallenta il veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '255', '75001', '2414', '4', 'Il freno motore dei veicoli pesanti può essere utilizzato solo su strade in discesa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '255', '75001', '2415', '5', 'Azionando il freno motore dei veicoli pesanti, la fase di scarico del motore diventa una fase di aspirazione che frena/rallenta il veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '255', '75001', '2416', '6', 'Azionando il freno motore dei veicoli pesanti, il dispositivo disinnesta automaticamente la frizione per sfruttare l''inerzia del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '256', '75002', '', '', 'Freno motore 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '256', '75002', '2417', '1', 'Il freno-motore è particolarmente importante per ridurre il rischio di possibile surriscaldamento degli elementi frenanti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '256', '75002', '2418', '2', 'Il freno-motore ed il rallentatore non sono dispositivi uguali, ma hanno funzioni analoghe', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '256', '75002', '2419', '3', 'Il dispositivo freno motore può essere integrato con il rallentatore e con l''impianto di frenatura di servizio in un unico sistema a controllo elettronico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '256', '75002', '2420', '4', 'Con l''azione del solo freno-motore non è possibile fermare il veicolo in distanze ragionevoli in tutte le situazioni di marcia', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '256', '75002', '2421', '5', 'Innestando un basso rapporto del cambio si ottiene un maggior effetto frenante del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '256', '75002', '2422', '6', 'L''effetto frenante del motore a scoppio è, a parità di cilindrata, generalmente maggiore di quello del motore Diesel', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '256', '75002', '2423', '7', 'Il freno motore è particolarmente efficiente sui veicoli elettrici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '256', '75002', '2424', '8', 'Il freno motore è azionato tramite il manettino del freno di stazionamento a molla, quando lo stesso viene portato a fine corsa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '256', '75002', '2425', '9', 'Il freno motore funziona meglio quando il cambio è in folle', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '256', '75002', '2426', '10', 'Il freno motore è un dispositivo che serve a mantenere frenato il veicolo quando è in sosta su strada in pendenza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '256', '75002', '2427', '11', 'Il freno motore ed il rallentatore si azionano assieme al freno di servizio in caso di pericolo immediato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '256', '75002', '2428', '12', 'L''effetto frenante del motore Diesel è, a parità di cilindrata, generalmente, maggiore di quello del motore a scoppio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '257', '075003/075004', '', '', 'Rallentatore ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '257', '075003/075004', '2429', '1', 'Il rallentatore ed il freno motore integrano il sistema di frenatura di servizio ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '257', '075003/075004', '2430', '2', 'Il rallentatore può essere di tipo idraulico o elettromagnetico ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '257', '075003/075004', '2431', '3', 'Il rallentatore (retarder) può venire usato anche assieme al freno motore ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '257', '075003/075004', '2432', '4', 'Il rallentatore può essere del tipo idraulico, elettromagnetico o a circolazione di acqua (aquatarder) ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '257', '075003/075004', '2433', '5', 'Con l''azione del solo rallentatore non si può fermare il veicolo in una distanza ragionevole in ogni situazione di marcia ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '257', '075003/075004', '2434', '6', 'Il rallentatore non deve essere usato assieme al freno motore ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '258', '075003 /075004', '', '', 'Rallentatore - idraulico', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '258', '075003 /075004', '2435', '1', 'Il rallentatore idraulico è posto all''uscita del cambio ed è collegato con l''albero di trasmissione ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '258', '075003 /075004', '2436', '2', 'Il rallentatore idraulico necessita di uno scambiatore di calore per evitare surriscaldamenti ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '258', '075003 /075004', '2437', '3', 'Utilizzando il rallentatore idraulico, il minore o maggiore rallentamento del veicolo è legato dalla quantità d''olio che entra nel rallentatore stesso ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '258', '075003 /075004', '2438', '4', 'In genere, il comando del rallentatore idraulico è ottenuto tramite un dispositivo a leva o a pedale, che consente vari livelli di frenatura ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '258', '075003 /075004', '2439', '5', 'Nel rallentatore idraulico viene utilizzato uno speciale olio che genera attrito e si oppone alla rotazione ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '258', '075003 /075004', '2440', '6', 'L''olio del rallentatore idraulico può raggiungere elevate temperature ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '258', '075003 /075004', '2441', '7', 'Il comando del rallentatore idraulico può essere sul volante ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '258', '075003 /075004', '2442', '8', 'L''uso del rallentatore idraulico comporta una maggiore usura dei materiali di attrito degli elementi frenanti ed un maggior riscaldamento dei tamburi ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '258', '075003 /075004', '2443', '9', 'Il rallentatore idraulico non necessita di uno scambiatore di calore, perché utilizza la propria scatola metallica per il raffreddamento ad aria ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '258', '075003 /075004', '2444', '10', 'Il rallentatore idraulico è posto tra la coppia conica ed i semiassi ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '258', '075003 /075004', '2445', '11', 'Il rallentatore idraulico consente il bloccaggio immediato delle ruote motrici ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '258', '075003 /075004', '2446', '12', 'Il rallentatore idraulico agisce direttamente sugli elementi frenanti delle ruote motrici ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '258', '075003 /075004', '2447', '13', 'Il rallentatore idraulico deve essere periodicamente svuotato dell''olio e pulito con dello sgrassante ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '258', '075003 /075004', '2448', '14', 'Il rallentatore idraulico è raffreddato ad aria ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '258', '075003 /075004', '2449', '15', 'Il rallentatore idraulico fa parte dell''impianto di frenatura pneumoidraulico ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '259', '075003 /075004', '', '', 'Rallentatore - elettromagnetico', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '259', '075003 /075004', '2450', '1', 'Il rallentatore elettromagnetico è schematicamente composto da uno statore e da un rotore ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '259', '075003 /075004', '2451', '2', 'Nel rallentatore elettromagnetico, lo statore è fissato al telaio mentre i dischi del rotore sono vincolati all''albero di trasmissione ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '259', '075003 /075004', '2452', '3', 'Attivando il rallentatore elettromagnetico, viene generato un potente campo magnetico che si oppone alla rotazione del rotore e quindi dell''albero di trasmissione ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '259', '075003 /075004', '2453', '4', 'Il rallentatore elettromagnetico genera una forte quantità di calore che deve essere smaltita ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '259', '075003 /075004', '2454', '5', 'Il rallentatore elettromagnetico deve essere periodicamente pulito da polvere e terriccio ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '259', '075003 /075004', '2455', '6', 'II rallentatore elettromagnetico tende a surriscaldarsi maggiormente di quello idraulico ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '259', '075003 /075004', '2456', '7', 'Il rallentatore elettromagnetico è più pesante di quello idraulico ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '259', '075003 /075004', '2457', '8', 'Il rallentatore elettromagnetico può sopportare carichi frenanti minori rispetto a quello idraulico ', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '259', '075003 /075004', '2458', '9', 'Nel rallentatore elettromagnetico, lo statore è fissato al piantone dello sterzo mentre i dischi del rotore sono calettati sull''albero primario del cambio ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '259', '075003 /075004', '2459', '10', 'Il rallentatore elettromagnetico accumula energia centrifuga durante le curve per ricaricarsi ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '259', '075003 /075004', '2460', '11', 'Il rallentatore elettromagnetico è composto da un interruttore magnetotermico e da un diffusore ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '259', '075003 /075004', '2461', '12', 'Il rallentatore idraulico è più usato perché quello elettromagnetico consuma molta corrente ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '259', '075003 /075004', '2462', '13', 'Il rallentatore idraulico è di più agevole montaggio rispetto a quello elettromagnetico ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '259', '075003 /075004', '2463', '14', 'Sui veicoli molto pesanti si possono montare sia il rallentatore idraulico che quello elettromagnetico insieme, purché siano disposti in serie ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '259', '075003 /075004', '2464', '15', 'Il rallentatore può essere di tipo pneumatico o pneumoidraulico ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '259', '075003 /075004', '2465', '16', 'Il rallentatore elettromagnetico è più leggero di quello idraulico ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '260', '75005', '', '', 'Limitatore di velocità 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2466', '1', 'Il limitatore di velocità è un dispositivo che interviene sull''iniezione del combustibile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2467', '2', 'Il limitatore di velocità è un dispositivo la cui funzione è quella di regolare l''alimentazione di combustibile al motore, al fine di limitare la velocità del veicolo ad un valore stabilito', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2468', '3', 'Il limitatore di velocità deve essere di tipo omologato e montato da officina autorizzata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2469', '4', 'Il limitatore di velocità, montato da officina autorizzata, comporta l''obbligo dell''aggiornamento della carta di circolazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2470', '5', 'Il limitatore di velocità deve essere inviolabile, cioè non deve essere possibile aumentare o spostare temporaneamente o permanentemente il livello della limitazione di velocità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2471', '6', 'A bordo dei veicoli che montano il limitatore di velocità, deve essere presente una copia del certificato di installazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2472', '7', 'A bordo dei veicoli che montano il limitatore di velocità, deve essere presente una copia dell''attestazione di apposizione dei sigilli da parte di officina autorizzata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2473', '8', 'Il limitatore di velocità non deve essere montato sugli autobus che effettuano servizio pubblico urbano', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2474', '9', 'Il limitatore di velocità deve essere montato su tutti gli autocarri, di massa complessiva a pieno carico superiore a 3,5 tonnellate, immatricolati dopo il 1 ottobre 2001', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2475', '10', 'Su un veicolo dotato di motore Diesel, il limitatore di velocità interviene sulla mandata della pompa di iniezione oppure degli iniettori pompa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2476', '11', 'Il limitatore di velocità viene regolato dal conducente a mezzo di una leva posta sul cruscotto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2477', '12', 'Il limitatore di velocità deve essere montato su tutti gli autocarri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2478', '13', 'Il limitatore di velocità deve essere montato su tutti gli autocarri di massa superiore a 7,5 tonnellate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2479', '14', 'Il limitatore di velocità deve essere montato su tutti gli autobus di massa superiore a 5 tonnellate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2480', '15', 'Il limitatore di velocità deve essere montato su tutti gli autobus con più di 22 posti a sedere', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2481', '16', 'Il limitatore di velocità può essere disattivato dal conducente sui percorsi urbani', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2482', '17', 'Il limitatore di velocità può essere disattivato dal conducente quando circola su autostrade con 3 o più corsie per senso di marcia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2483', '18', 'Il limitatore di velocità può essere disattivato dal conducente in caso di emergenza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2484', '19', 'Il limitatore di velocità è raffreddato ad acqua', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '260', '75005', '2485', '20', 'Il limitatore di velocità interviene sul rallentatore, potenziandone l''effetto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '261', '75005', '', '', 'Limitatore di velocità 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '261', '75005', '2486', '1', 'Sulla copia del certificato di installazione del limitatore di velocità devono risultare, tra l''altro, il numero e la data di redazione del certificato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '261', '75005', '2487', '2', 'Sulla copia del certificato di installazione del limitatore di velocità devono risultare, tra l''altro, il numero di omologazione e di matricola del limitatore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '261', '75005', '2488', '3', 'Sulla copia del certificato di installazione del limitatore di velocità devono risultare, tra l''altro, il numero di telaio e di targa del veicolo su cui è montato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '261', '75005', '2489', '4', 'Sulla copia del certificato di installazione del limitatore di velocità deve risultare, tra l''altro, la dichiarazione che il limitatore è stato omologato per il tipo di veicolo su cui è montato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '261', '75005', '2490', '5', 'Sulla copia del certificato di installazione del limitatore di velocità devono risultare, tra l''altro, l''esito positivo della revisione triennale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '261', '75005', '2491', '6', 'Sulla copia del certificato di installazione del limitatore di velocità devono risultare, tra l''altro, le generalità del tecnico che lo ha installato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '262', '75005', '', '', 'Limitatore di velocità 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '262', '75005', '2492', '1', 'La velocità massima consentita dal limitatore ad un autobus deve essere di 100 km/h', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '262', '75005', '2493', '2', 'La velocità massima consentita dal limitatore ad un autocarro, di massa complessiva a pieno carico superiore a 3,5 tonnellate, deve essere di 90 km/h', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '262', '75005', '2494', '3', 'La velocità massima consentita dal limitatore ad un autobus deve essere di 80 km/h', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '262', '75005', '2495', '4', 'La velocità massima consentita dal limitatore ad un autobus deve essere di 110 km/h', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '262', '75005', '2496', '5', 'La velocità massima consentita dal limitatore ad un autocarro, di massa complessiva a pieno carico superiore a 3,5 tonnellate, deve essere di 80 km/h', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '262', '75005', '2497', '6', 'La velocità massima consentita dal limitatore ad un autocarro di massa complessiva a pieno carico superiore a 3,5 tonnellate, deve essere di 70 km/h', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '262', '75005', '2498', '7', 'La velocità massima consentita dal limitatore ad un autocarro, di massa complessiva a pieno carico superiore a 3,5 tonnellate, deve essere di 100 km/h', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '263', '75006', '', '', 'Limitatore di velocità 3', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '263', '75006', '2499', '1', 'Il montaggio del limitatore di velocità è obbligatorio per i veicoli M2, M3, N2, N3 immatricolati dopo il 1 ottobre 2001', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '263', '75006', '2500', '2', 'La verifica del corretto montaggio del limitatore di velocità avviene accertando la presenza della apposita targhetta, conforme alle norme, e del certificato di installazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '263', '75006', '2501', '3', 'Il dispositivo limitatore di velocità agisce, se presente, sulla pompa di iniezione del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '263', '75006', '2502', '4', 'Sono esentati dall''obbligo del montaggio del dispositivo limitatore di velocità gli autobus urbani', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '263', '75006', '2503', '5', 'La circolazione con limitatore di velocità alterato comporta la sanzione accessoria della revoca della patente di guida', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '263', '75006', '2504', '6', 'Il limitatore di velocità favorisce la riduzione dell''inquinamento atmosferico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '263', '75006', '2505', '7', 'Il montaggio del limitatore di velocità è obbligatorio solo per gli autobus', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '263', '75006', '2506', '8', 'Il dispositivo limitatore di velocità blocca il pedale dell''acceleratore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '263', '75006', '2507', '9', 'Il dispositivo limitatore di velocità agisce sul freno di soccorso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '263', '75006', '2508', '10', 'Il montaggio del limitatore di velocità può essere eseguito da qualsiasi officina meccanica', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '263', '75006', '2509', '11', 'Sono esentati dall''obbligo del montaggio del dispositivo limitatore di velocità solo i veicoli militari o appartenenti ai corpi di polizia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '263', '75006', '2510', '12', 'È esentato dall''obbligo del montaggio del dispositivo limitatore di velocità qualsiasi veicolo che faccia un servizio di linea', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '263', '75006', '2511', '13', 'La verifica del corretto montaggio del limitatore di velocità avviene provando il veicolo su strada', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '263', '75006', '2512', '14', 'La circolazione con limitatore di velocità alterato comporta la sospensione della patente da 15 giorni a tre mesi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '263', '75006', '2513', '15', 'La verifica del corretto montaggio del limitatore di velocità avviene mediante certificato rilasciato dalla Motorizzazione Civile', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '264', '141002', '', '', 'Impianto frenante – ABS  1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '264', '141002', '2514', '1', 'Nei veicoli con impianti di frenatura dotati di ABS viene mantenuta la stabilità direzionale in frenata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '264', '141002', '2515', '2', 'Nei veicoli con impianti di frenatura dotati di ABS si ottiene in genere una riduzione delle distanze di frenatura, soprattutto in condizioni di scarsa aderenza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '264', '141002', '2516', '3', 'Il sistema ABS, evitando il bloccaggio delle ruote in caso di energica frenata, consente di non far diminuire l''aderenza con il fondo stradale, riducendo così lo spazio di arresto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '264', '141002', '2517', '4', 'Il sistema ABS rende inutile la presenza del freno di soccorso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '264', '141002', '2518', '5', 'Il dispositivo antibloccaggio in frenata ABS impedisce il bloccaggio delle ruote in caso di frenatura, purché il fondo stradale non sia innevato Ascolta il testo del quesito', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '265', '1401005', '', '', 'Impianto frenante – ABS 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '265', '1401005', '2519', '1', 'L''ABS permette di sfruttare al meglio la forza frenante disponibile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '265', '1401005', '2520', '2', 'L’ABS permette di raddoppiare la forza frenante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '266', '1401016', '', '', 'Impianto frenante – ABS 3', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '266', '1401016', '2521', '1', 'Il sistema frenante antibloccaggio ruote ABS (Antilock Braking System), evita il bloccaggio delle ruote, ottimizzando la frenata e migliorando la dirigibilità del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('12', '266', '1401016', '2522', '2', 'Sugli autoveicoli dotati di frenatura pneumoidraulica, il sistema ABS consente alle ruote di bloccarsi anche quando la pressione nei serbatoi è molto bassa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '267', '101001', '', '', 'Agganciamento tra motrice e rimorichio', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2523', '1', 'L''agganciamento fra motrice e rimorchio avviene tramite campana e timone', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2524', '2', 'Il timone del rimorchio è collegato alla ralla posta sul rimorchio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2525', '3', 'Il timone del rimorchio consente la sterzatura del rimorchio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2526', '4', 'Il timone del rimorchio si collega alla campana tramite l’occhione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2527', '5', 'Gli organi di traino vanno periodicamente ingrassati', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2528', '6', 'Il rimorchio a volta corretta ha due ralle con un cavo a otto che le collega', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2529', '7', 'Il rimorchio a volta corretta permette, rispetto ad altri rimorchi, di ridurre la fascia di ingombro del complesso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2530', '8', 'La frenatura di un rimorchio con freno continuo e automatico è anticipata rispetto a quella della motrice', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2531', '9', 'Sul timone è punzonato il numero del telaio del rimorchio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2532', '10', 'Il sistema di aggancio dell''autotreno è caratterizzato dalla presenza di una ralla sulla motrice', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2533', '11', 'Le ralle con rivestimento in teflon necessitano di un ingrassaggio settimanale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2534', '12', 'I semigiunti di accoppiamento motrice-rimorchio dell''impianto frenante permettono il trasferimento di olio in pressione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2535', '13', 'Il timone del rimorchio deve avere lunghezza massima 60 centimetri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2536', '14', 'Il timone del rimorchio sopporta sempre un carico verticale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2537', '15', 'Gli organi di traino vanno ingrassati solo in occasione di revisione veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2538', '16', 'Per la frenatura di un rimorchio con freno continuo e automatico esiste un solo serbatoio montato sulla motrice', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2539', '17', 'Nel tubo moderabile c''è sempre aria in pressione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2540', '18', 'Nel tubo automatico l''aria va in pressione quando il conducente schiaccia il pedale del freno', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2541', '19', 'Nei giunti di accoppiamento dei tubi che portano aria al rimorchio, il colore rosso identifica il tubo moderabile', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '267', '101001', '2542', '20', 'Sganciando i tubi dell''aria dei freni che vanno al rimorchio si svuota immediatamente il serbatoio posto sul rimorchio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '268', '101001', '', '', 'Agganciamento tra motrice e rimorichio con freno ad inerzia', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '268', '101001', '2543', '1', 'Un rimorchio con freno ad inerzia non può superare i 3 500 chilogrammi di massa complessiva', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '268', '101001', '2544', '2', 'In caso di sganciamento accidentale del rimorchio con freno ad inerzia entra in funzione il freno di soccorso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '268', '101001', '2545', '3', 'Dopo aver agganciato calotta e sfera, si deve allacciare il cavo d''acciaio del freno di soccorso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '268', '101001', '2546', '4', 'Dopo aver agganciato calotta e sfera, si deve allacciare il cavo elettrico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '268', '101001', '2547', '5', 'Dopo aver agganciato calotta e sfera si deve alzare il ruotino pivottante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '268', '101001', '2548', '6', 'Il gancio a sfera è l''organo di traino tipico per rimorchi superiori alle 3,5 t di massa', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '268', '101001', '2549', '7', 'Il freno ad inerzia necessita di un impianto oleopneumatico sul semirimorchio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '269', '101003', '', '', 'Impianto di frenatura: servodistributore a triplo comando', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '269', '101003', '2550', '1', 'Nell''impianto di frenatura pneumatico, il servodistributore a triplo comando, garantisce la frenatura di servizio, di soccorso e di stazionamento del rimorchio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '269', '101003', '2551', '2', 'Nell''impianto di frenatura pneumatico, il servodistributore a triplo comando è montato sulle motrici atte al traino', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '269', '101003', '2552', '3', 'Nell''impianto di frenatura pneumatico, al servodistributore a triplo comando sono collegati cinque tubi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '269', '101003', '2553', '4', 'Nell''impianto di frenatura pneumatico, al servodistributore a triplo comando sono collegati sia i tubi dei freni anteriori che quelli dei freni posteriori della motrice', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '269', '101003', '2554', '5', 'Nell''impianto di frenatura pneumatico, dal servodistributore a triplo comando l''aria esce da un solo tubo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '269', '101003', '2555', '6', 'Nell''impianto di frenatura pneumatico, il servodistributore a triplo comando garantisce la frenatura del rimorchio in caso di avaria del tubo moderabile', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '269', '101003', '2556', '7', 'Nell''impianto di frenatura pneumatico, il servodistributore a triplo comando, garantisce la frenatura del rimorchio in caso di avaria del tubo automatico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '269', '101003', '2557', '8', 'La frenatura di soccorso del servodistributore a triplo comando garantisce la frenatura del rimorchio in caso di guasto del tubo moderabile', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '269', '101003', '2558', '9', 'Quando, per un''avaria, si svuota durante la marcia il tubo automatico, il servodistributore a triplo comando garantisce comunque la frenatura del rimorchio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '269', '101003', '2559', '10', 'Quando, per un''avaria, si svuota durante la marcia il tubo automatico, il servodistributore a triplo comando svuota immediatamente il tubo moderabile', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '270', '101002', '', '', 'Impianto di frenatura: servodistributore a triplo comando', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '270', '101002', '2560', '1', 'Nell''impianto di frenatura pneumatico, il servodistributore a triplo comando, garantisce la frenatura di soccorso in caso di avaria di una sezione dell''impianto stesso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '270', '101002', '2561', '2', 'Nell''impianto frenante pneumatico, il servodistributore a triplo comando è posto tra il distributore duplex e il servodeviatore modulato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '271', '101003', '', '', 'Impianto di frenatura: servodeviatore  modulato', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '271', '101003', '2562', '1', 'Il servodeviatore  modulato garantisce  la frenatura del rimorchio anche in caso di avaria del tubo moderabile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '271', '101003', '2563', '2', 'Il servodeviatore modulato simula l''avaria del tubo automatico, quando invece si è verificata l''avaria del tubo moderabile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '271', '101003', '2564', '3', 'Il servodeviatore modulato innesca la frenatura automatica in caso di avaria del tubo moderabile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '271', '101003', '2565', '4', 'Il servodeviatore modulato è montato sulla motrice', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '271', '101003', '2566', '5', 'Il servodeviatore modulato è posto a valle del servodistributore a triplo comando', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '271', '101003', '2567', '6', 'Dal servodeviatore modulato partono i tubi automatico e moderabile', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '271', '101003', '2568', '7', 'Il servodeviatore modulato non è necessario su una motrice non atta al traino', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '271', '101003', '2569', '8', 'Nell''impianto di frenatura pneumatico, il servodeviatore modulato è posto sul rimorchio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '271', '101003', '2570', '9', 'Quando, per un''avaria, non funziona una sezione dell''impianto di frenatura della motrice, il servodeviatore modulato garantisce ugualmente la frenatura del rimorchio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '272', '101001', '', '', 'Frenatura del rimorchio', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '272', '101001', '2571', '1', 'Nei rimorchi dotati di freni pneumatici, in caso di rottura dei tubi di collegamento motrice-rimorchio si mette in funzione la frenatura automatica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '273', '101003', '', '', 'Impianto di frenatura: servoautodistributore', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '273', '101003', '2572', '1', 'Il servoautodistributore è montato sul rimorchio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '273', '101003', '2573', '2', 'Al servoautodistributore sono collegati in ingresso due tubi, quello del moderabile e quello dell''automatico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '273', '101003', '2574', '3', 'Dal servoautodistributore sono collegati in uscita due tubi, uno che va al serbatoio del rimorchio e uno agli elementi frenanti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '273', '101003', '2575', '4', 'Il servoautodistributore garantisce la frenatura istantanea ed automatica del rimorchio in caso di sganciamento accidentale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '273', '101003', '2576', '5', 'Nel servoautodistributore esiste un comando manuale per lo sblocco dei freni di stazionamento del rimorchio quando lo stesso è sganciato dalla motrice', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '273', '101003', '2577', '6', 'Il servoautodistributore è dotato di una valvola di ritenuta, una di scarico e una d''immissione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '273', '101003', '2578', '7', 'Nel servoautodistributore, la valvola d''immissione resta aperta finché viene premuto il pedale del freno', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '273', '101003', '2579', '8', 'Quando per un''avaria non funziona una sezione dell''impianto frenante della motrice, il servoautodistributore garantisce comunque la frenatura del rimorchi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '273', '101003', '2580', '9', 'Nel caso di rottura del tubo moderabile, si ha la frenatura istantanea del rimorchio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '274', '141001', '', '', 'Impianti di frenatura 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '274', '141001', '2581', '1', 'Il sistema di frenatura pneumatico di un rimorchio consente la frenatura automatica di emergenza in caso di sganciamento accidentale dalla motrice', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '275', '103001', '', '', 'Sistema frenante di un rimorchio di massa limitata', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '275', '103001', '2582', '1', 'Nei rimorchi di massa limitata la disposizione del carico influisce sulla stabilità del veicolo trainante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '275', '103001', '2583', '2', 'La frenatura di un rimorchio leggero avviene con un piccolo ritardo rispetto alla motrice', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '275', '103001', '2584', '3', 'La frenatura di un rimorchio di massa fino a 3,5 t avviene, in genere, con un impianto frenante che sfrutta l''inerzia del rimorchio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '275', '103001', '2585', '4', 'Il sistema frenante di un rimorchio di massa fino a 3,5 t può essere ad inerzia e di tipo idraulico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '275', '103001', '2586', '5', 'Il sistema frenante di un rimorchio di massa fino a 3,5 t può essere ad inerzia e di tipo meccanico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '275', '103001', '2587', '6', 'Il servoautodistributore non è in genere montato sui rimorchi di massa limitata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '275', '103001', '2588', '7', 'Il modulatore di pressione in funzione del carico non è in genere montato sui rimorchi di massa limitata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '275', '103001', '2589', '8', 'Il sistema frenante di un rimorchio di massa fino a 3,5 t non è mai di tipo meccanico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '275', '103001', '2590', '9', 'Il sistema frenante di un rimorchio di massa fino a 3,5 t può essere solo di tipo meccanico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '275', '103001', '2591', '10', 'Dopo aver agganciato un rimorchio monoasse, si deve fissare a terra il ruotino pivottante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '275', '103001', '2592', '11', 'Un rimorchio con impianto frenante ad inerzia, quando viene sganciato manualmente, resta bloccato automaticamente', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '276', '141018', '', '', 'Collegamenti tra motrice e rimochio negli impianti di frenatura pneumatici', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '276', '141018', '2593', '1', 'Negli impianti di frenatura ad aria compressa, il collegamento tra motrice e rimorchio prevede un collegamento meccanico, uno pneumatico e uno elettrico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '276', '141018', '2594', '2', 'Negli impianti di frenatura ad aria compressa, il collegamento pneumatico tra motrice e rimorchio è realizzato mediante due tubi flessibili e due coppie di semigiunti omologati', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '276', '141018', '2595', '3', 'Negli impianti di frenatura ad aria compressa dotati di ABS, il collegamento tra motrice e rimorchio deve prevedere anche uno specifico collegamento elettrico per l''ABS stesso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '276', '141018', '2596', '4', 'Negli impianti di frenatura ad aria compressa, le due coppie di semigiunti che realizzano il collegamento pneumatico tra motrice e rimorchio hanno colore differente tra loro', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '276', '141018', '2597', '5', 'Negli impianti di frenatura ad aria compressa, l''autista che realizzi il collegamento pneumatico tra motrice e rimorchio deve indossare idonei guanti protettivi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '276', '141018', '2598', '6', 'Negli impianti di frenatura ad aria compressa, il collegamento pneumatico tra motrice e rimorchio è realizzato mediante tubi rigidi e due giunti di accoppiamento di colore verde', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '276', '141018', '2599', '7', 'Negli impianti di frenatura ad aria compressa, il collegamento pneumatico tra motrice e rimorchio può essere di tipo wireless (senza fili)', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '276', '141018', '2600', '8', 'Negli impianti di frenatura ad aria compressa, il collegamento tra motrice e rimorchio prevede un collegamento idraulico, uno pneumatico e uno elettrico di tipo wireless (senza fili)', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '276', '141018', '2601', '9', 'Negli impianti di frenatura ad aria compressa, il collegamento pneumatico tra motrice e rimorchio è realizzato mediante quattro tubi flessibili e quattro coppie di semigiunti omologati', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('13', '276', '141018', '2602', '10', 'Negli impianti di frenatura ad aria compressa, il collegamento pneumatico tra motrice e rimorchio deve essere realizzato da operaio specializzato in officina opportunamente attrezzata e autorizzata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '277', '151003', '', '', 'Fumo bianco allo scarico', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '277', '151003', '2603', '1', 'La fuoriuscita di fumo bianco ed abbondante dallo scarico può indicare una perdita d''olio in camera di scoppio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '277', '151003', '2604', '2', 'La cattiva combustione che provoca fumosità allo scarico può essere causata dal filtro dell''aria intasato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '277', '151003', '2605', '3', 'La cattiva combustione che provoca fumosità allo scarico può essere causata dall''iniettore non funzionante correttamente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '277', '151003', '2606', '4', 'La cattiva combustione che provoca fumosità allo scarico può essere causata da una quantità eccessiva di carburante iniettata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '277', '151003', '2607', '5', 'La fuoriuscita di fumo bianco ed abbondante dallo scarico può indicare una rottura nella guarnizione della testata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '277', '151003', '2608', '6', 'La fuoriuscita di fumo bianco ed abbondante dallo scarico può indicare l''uso di benzina al posto del gasolio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '277', '151003', '2609', '7', 'La fuoriuscita di fumo bianco ed abbondante dallo scarico può indicare un consumo eccessivo di gasolio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '277', '151003', '2610', '8', 'La cattiva combustione che provoca fumosità allo scarico può essere causata dal filtro dell''olio intasato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '278', '151002', '', '', 'Fumo nero allo scarico', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '278', '151002', '2611', '1', 'Il fumo nero allo scarico può indicare una scarsità di aria nella combustione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '278', '151002', '2612', '2', 'Il fumo nero allo scarico può indicare un eccesso di gasolio nella combustione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '278', '151002', '2613', '3', 'Il fumo nero allo scarico indica una cattiva combustione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '278', '151002', '2614', '4', 'Nel motore Diesel, il fumo nero allo scarico indica un''insufficiente compressione in uno o più cilindri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '278', '151002', '2615', '5', 'Il fumo nero allo scarico indica un inizio di incendio nel motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '278', '151002', '2616', '6', 'Il fumo nero allo scarico indica presenza d''olio nella combustione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '278', '151002', '2617', '7', 'Il fumo nero allo scarico indica che dobbiamo utilizzare un tipo di gasolio con più alto numero di ottani', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '279', '151001', '', '', 'Vapore dal tubo di scarico o acqua nella coppa dell''olio', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '279', '151001', '2618', '1', 'Se dal tubo di scarico esce molto vapore, ciò può significare che vi è un''infiltrazione di acqua o di liquido di raffreddamento nei cilindri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '279', '151001', '2619', '2', 'Se si riscontra la presenza di liquido di raffreddamento nella coppa dell''olio, ciò può dipendere dalla guarnizione della testata bruciata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '279', '151001', '2620', '3', 'Se si accende la spia rossa dell''impianto di raffreddamento, è necessario fermarsi e spegnere subito il motore, per evitare danni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '279', '151001', '2621', '4', 'Se si accende la spia rossa dell''impianto di lubrificazione, è necessario fermarsi e spegnere subito il motore per evitare danni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '279', '151001', '2622', '5', 'Se si accende la spia rossa dell''impianto di lubrificazione è necessario fermare subito il motore e, se non vi sono perdite di lubrificante, occorre rabboccare con lubrificante, possibilmente dello stesso tipo', 'V');
