INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '299', '163002', '', '', 'Compiti e competenze del conducente 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2748', '1', 'È compito del conducente segnalare il malfunzionamento del veicolo al responsabile del parco macchine o dell''officina di riparazione dell''azienda', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2749', '2', 'Il conducente deve essere in grado di distinguere i guasti che sconsigliano la prosecuzione del viaggio da quelli che ne possono permettere, pur con le dovute cautele, la continuazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2750', '3', 'La manutenzione ha lo scopo di consentire lo svolgimento del servizio di trasporto in condizioni di sicurezza e regolarità', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2751', '4', 'La manutenzione ha lo scopo di mantenere i veicoli nel migliore stato di efficienza possibile, in modo da evitare l''interruzione del servizio di trasporto per guasti', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2752', '5', 'Per ridurre al massimo i problemi di manutenzione, il conducente deve avere uno stile di guida che limiti le sollecitazioni alle parti meccaniche del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2753', '6', 'Per ridurre al massimo i problemi di manutenzione, il conducente deve avere uno stile di guida che limiti al massimo i consumi delle parti del veicolo soggette a usura', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2754', '7', 'Per ridurre al massimo i problemi di manutenzione, il conducente deve segnalare subito in azienda qualunque possibile difetto o anomalia riscontrata durante il suo turno di lavoro', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2755', '8', 'Scopo della manutenzione ordinaria è anche quello di rendere il veicolo meno rumoroso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2756', '9', 'Scopo della manutenzione ordinaria è anche quello di rendere il veicolo meno inquinante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2757', '10', 'Scopo della manutenzione ordinaria è anche quello di limitare i consumi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2758', '11', 'Le principali operazioni di manutenzione ordinaria che devono essere effettuate sul veicolo sono tra l''altro indicate sul libretto di uso e manutenzione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2759', '12', 'Le scadenze delle verifiche manutentive sono in genere riferite al chilometraggio percorso o al tempo trascorso dalla precedente manutenzione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2760', '13', 'È compito del conducente, prima della partenza, regolare la pressione dell''aria dei freni in base al carico del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2761', '14', 'Il conducente si deve accorgere di una perdita di pressione dall''impianto dei freni ascoltando l''inconfondibile sibilo dell''aria che esce dai tubi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2762', '15', 'Per ridurre al massimo i problemi di manutenzione, il conducente deve segnalare subito al collega del turno successivo qualunque possibile difetto o anomalia riscontrata durante il suo turno di lavoro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2763', '16', 'Per ridurre al massimo i problemi di manutenzione, il conducente deve provare a riparare subito qualunque possibile difetto o anomalia riscontrata durante il suo turno di lavoro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2764', '17', 'È compito del conducente, prima di partire, ripulire con uno straccio gli iniettori', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2765', '18', 'Per ridurre al massimo i problemi di manutenzione, il conducente deve viaggiare a motore spento nelle lunghe discese', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2766', '19', 'Per ridurre al massimo i problemi di manutenzione, il conducente deve effettuare frenate a fondo di prova al termine di ogni turno di lavoro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2767', '20', 'Scopo della manutenzione ordinaria è anche quello di poter aumentare le tariffe per il servizio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2768', '21', 'Scopo della manutenzione ordinaria è anche quello di ottenere sgravi fiscali', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2769', '22', 'Le principali operazioni di manutenzione ordinaria che devono essere effettuate sul veicolo sono indicate sul libretto di circolazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2770', '23', 'Per ridurre al massimo i problemi di manutenzione, il conducente deve cambiare marcia il meno possibile', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '299', '163002', '2771', '24', 'Le scadenze di tutte le verifiche manutentive sono coincidenti con le revisioni periodiche di legge', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '300', '163001', '', '', 'Comportamenti da tenere per mantenere il veicolo in buone condizioni', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '300', '163001', '2772', '1', 'Per mantenere il veicolo in buone condizioni, occorre eseguire i periodici controlli seguendo le indicazioni della casa costruttrice', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '300', '163001', '2773', '2', 'Per mantenere il veicolo in buone condizioni, occorre controllare periodicamente la pressione di gonfiaggio degli pneumatici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '300', '163001', '2774', '3', 'Per mantenere il veicolo in buone condizioni, occorre non viaggiare in condizioni di sovraccarico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '300', '163001', '2775', '4', 'Per mantenere il veicolo in buone condizioni, occorre utilizzare sempre il serbatoio del combustibile riempito ad oltre un terzo della sua capacità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '300', '163001', '2776', '5', 'Per mantenere il veicolo in buone condizioni, occorre viaggiare sempre con pneumatici gonfiati a pressione superiore all''indicazione generale di due decimi di bar', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '300', '163001', '2777', '6', 'Per mantenere il veicolo in buone condizioni occorre controllare la pressione di gonfiaggio solo in occasione di giornate molto calde', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '301', '163003', '', '', 'Conseguenze delle mancanze agli obblighi di manutenzione in caso di incidente', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '301', '163003', '2778', '1', 'In caso di incidente stradale, se vengono riscontrate gravi mancanze agli obblighi di manutenzione del veicolo coinvolto, vi è il rischio di aggravanti di responsabilità in un eventuale processo penale', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '301', '163003', '2779', '2', 'In caso di incidente stradale, se vengono riscontrate gravi mancanze agli obblighi di manutenzione del veicolo coinvolto, vi è il rischio di aggravanti di responsabilità civile, anche se il conducente ha rispettato le norme di circolazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '301', '163003', '2780', '3', 'In caso di incidente stradale, se vengono riscontrate gravi mancanze agli obblighi di manutenzione del veicolo coinvolto, può esserci il rischio che l''impresa assicuratrice si avvalga del diritto di rivalsa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '301', '163003', '2781', '4', 'In caso di incidente stradale, se vengono riscontrate gravi mancanze agli obblighi di manutenzione del veicolo coinvolto, vi è il rischio di arresto immediato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '301', '163003', '2782', '5', 'In caso di incidente stradale, se vengono riscontrate gravi mancanze agli obblighi di manutenzione del veicolo coinvolto, vi è il rischio di raddoppio della cifra da rimborsare', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '301', '163003', '2783', '6', 'In caso di incidente stradale, se vengono riscontrate gravi mancanze agli obblighi di manutenzione del veicolo coinvolto, vi è il rischio che l''assicurazione si rifiuti di dare copertura al sinistro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '302', '164002', '', '', 'Attività di manutenzione e autoriparazione da svolgersi in officine autorizzate 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '302', '164002', '2784', '1', 'La legge stabilisce che l''attività di manutenzione e autoriparazione dei veicoli debba essere svolta esclusivamente da officine iscritte ad apposito registro', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '302', '164002', '2785', '2', 'La legge stabilisce che la manutenzione e l''autoriparazione devono essere svolte solo da officine iscritte ad apposito registro, in modo da garantire il corretto smaltimento dei rifiuti dovuti a tali attività', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '302', '164002', '2786', '3', 'La legge stabilisce che la manutenzione e l''autoriparazione deve essere svolta solo da officine iscritte ad apposito registro, in modo da garantire un''adeguata professionalità ed evitare incidenti dovuti a guasti per errate manutenzioni o riparazioni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '302', '164002', '2787', '4', 'La legge stabilisce che la sostituzione del filtro dell''aria, non rientra nelle attività di autoriparazione da eseguire solo presso officine autorizzate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '302', '164002', '2788', '5', 'La legge stabilisce che la sostituzione del filtro dell''olio lubrificante, non rientra nelle attività di autoriparazione da eseguire solo presso officine autorizzate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '302', '164002', '2789', '6', 'La legge stabilisce che la sostituzione dell''olio e di altri liquidi che lubrificano le parti dell''autoveicolo, non rientra nelle attività di autoriparazione da eseguire solo presso officine autorizzate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '302', '164002', '2790', '7', 'La legge stabilisce che la sostituzione del liquido di raffreddamento del motore non rientra nelle attività di autoriparazione da eseguire solo presso officine autorizzate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '302', '164002', '2791', '8', 'Le sostituzioni di liquidi o filtri del motore che è ammesso fare in proprio per legge, devono comunque essere eseguite garantendo il corretto smaltimento dei liquidi o delle parti esauste', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '302', '164002', '2792', '9', 'Le sostituzioni di liquidi o filtri del motore che è ammesso fare in proprio per legge, possono essere eseguite purché i liquidi o le parti esauste vengano buttate in un contenitore pubblico per rifiuti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '302', '164002', '2793', '10', 'Le sostituzioni di liquidi del motore che è ammesso fare in proprio per legge, possono essere eseguite purché i liquidi vengano raccolti in fusti di plastica e lasciati vicino ai cassonetti dell''immondizia raccolta dai servizi di nettezza urbana', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '302', '164002', '2794', '11', 'Le sostituzioni di filtri del motore che è ammesso fare in proprio per legge, possono essere eseguite purché i filtri vengano raccolti negli appositi sacchi viola con la scritta rifiuto meccanico e lasciati vicino ai cassonetti dell''immondizia raccolta dai servizi di nettezza urbana', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '302', '164002', '2795', '12', 'La legge stabilisce che la sostituzione delle pastiglie dei freni non rientra nelle attività di autoriparazione da eseguire solo presso officine autorizzate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '302', '164002', '2796', '13', 'La legge stabilisce che la sostituzione degli ammortizzatori non rientra nelle attività di autoriparazione da eseguire solo presso officine autorizzate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '302', '164002', '2797', '14', 'La legge stabilisce che le riparazioni all''impianto di illuminazione non rientrano tra le attività di autoriparazione da eseguire solo presso officine autorizzate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '302', '164002', '2798', '15', 'La legge stabilisce che le riparazioni all''impianto elettrico non rientrano tra le attività di autoriparazione da eseguire solo presso officine autorizzate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '302', '164002', '2799', '16', ' La legge non prevede un registro specifico delle officine che possono effettuare attività di autoriparazione, ma solo un elenco comunale consultabile su internet', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '303', '164003', '', '', 'Attività di manutenzione e autoriparazione da svolgersi in officine autorizzate 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '303', '164003', '2800', '1', 'Le riparazioni degli organi del motore quali: distribuzione, impianto di iniezione o di alimentazione, salvo la sostituzione dei filtri a cartuccia, devono essere effettuati in officina autorizzata e da personale qualificato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '303', '164003', '2801', '2', 'Se si introduce acqua o liquido nel radiatore quando il motore è molto caldo c''è il rischio di crepare il monoblocco o di deformare la testata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '303', '164003', '2802', '3', 'Tutte le riparazioni relative al cambio di velocità devono venire effettuate in officina autorizzata e da personale qualificato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '303', '164003', '2803', '4', 'Per quanto concerne l''attività di autoriparazione, la regola generale è quella di avvalersi di apposite imprese iscritte nel registro delle officine abilitate a tali interventi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '303', '164003', '2804', '5', 'Secondo le norme di legge, anche il cambio dei filtri dell''aria e dell''olio deve avvenire solo in officina ed eseguito da personale qualificato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '303', '164003', '2805', '6', 'Il conducente può fare anche importanti riparazioni al motore del veicolo purché sia in possesso almeno di diploma di perito meccanico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '303', '164003', '2806', '7', 'In caso di guasto agli organi di trasmissione, la riparazione può essere effettuata dal conducente purché egli sia in possesso della patente C+E o D+E', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '303', '164003', '2807', '8', 'Il cambio dell''olio di un autocarro di massa complessiva a pieno carico superiore a 7,5 tonnellate, dotato di motore Diesel, può essere effettuato solo in un''officina specializzata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '303', '164003', '2808', '9', 'Il conducente può fare anche importanti riparazioni al motore del veicolo purché sia in possesso almeno di diploma di perito meccanico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '303', '164003', '2809', '10', 'Secondo le norme di legge, anche il cambio dei filtri dell''aria e dell''olio deve avvenire solo in officina ed eseguito da personale qualificato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '304', '161001', '', '', 'Cunei fermaruota', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2810', '1', 'I cunei fermaruota fanno parte della dotazione di sicurezza obbligatoria per autoveicoli e rimorchi di massa complessiva superiore a 3,5 tonnellate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2811', '2', 'I cunei fermaruota servono per bloccare le ruote in caso di sosta dei rimorchi isolati di massa superiore a 3,5 tonnellate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2812', '3', 'I cunei fermaruota devono essere utilizzati, su un veicolo di massa complessiva superiore a 3,5 tonnellate, in caso di sostituzione di una ruota', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2813', '4', 'I cunei fermaruota in dotazione devono essere almeno due, a bordo di un veicolo isolato di massa complessiva superiore a 3,5 tonnellate', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2814', '5', 'Ai fini della sicurezza, un autobus di massa superiore a 3,5 tonnellate deve essere dotato di almeno due cunei fermaruota di idonee dimensioni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2815', '6', 'Ai fini della sicurezza, un rimorchio o un semirimorchio, di massa complessiva superiore a 3,5 tonnellate, devono essere dotati di almeno due cunei fermaruota di idonee dimensioni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2816', '7', 'Tutti i veicoli di massa a pieno carico superiore a 3,5 tonnellate, devono essere dotati di due cunei atti a impedire che, su strade in pendenza, il veicolo fermo in sosta possa muoversi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2817', '8', 'Su un veicolo di massa complessiva superiore a 3,5 tonnellate, ai fini della sicurezza, occorre utilizzare i cunei fermaruota anche durante le operazioni di smontaggio e rimontaggio di una ruota', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2818', '9', 'Un autoveicolo di massa a pieno carico superiore a 7,5 tonnellate deve essere equipaggiato con dispositivi omologati atti a ridurre la nebulizzazione dell''acqua sollevata dalle ruote in caso di pioggia', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2819', '10', 'I dispositivi paraincastro, ove obbligatori, devono essere posizionati posteriormente, lateralmente e/o anteriormente a seconda della tipologia di carrozzeria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2820', '11', 'I cunei fermaruota possono essere utilizzati in caso di ingombro della carreggiata al posto del segnale mobile di pericolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2821', '12', 'A bordo di un veicolo isolato di massa complessiva superiore a 3,5 tonnellate, i cunei fermaruota in dotazione devono essere almeno quattro', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2822', '13', 'I cunei fermaruota possono essere utilizzati come dispositivo antirapina', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2823', '14', 'Il dispositivo paraincastro, ove previsto, è situato solo nella parte posteriore del veicolo a protezione di eventuali tamponamenti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2824', '15', 'Tutti gli autoveicoli pesanti, se circolnti su autostrada, devono essere dotati di dispositivo paraincastro nella parte anteriore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2825', '16', 'Tutti i veicoli dotati di ruote gemellate hanno l''obbligo di essere dotati dei dispositivi antinebulizzazione dell''acqua presente sulla carreggia in caso di pioggia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2826', '17', 'Gli autocarri di massa superiore a 3,5 tonnellate a pieno carico, devono essere muniti di dispositivi antinebulizzazione dell''acqua presente sulla carreggiata in caso di pioggia', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2827', '18', 'I dispositivi antinebulizzazione dell''acqua presente sulla carreggiata, devono essere disinseriti in caso di strada asciutta', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '304', '161001', '2828', '19', 'I dispositivi antinebulizzazione dell''acqua presente sulla carreggiata, sono attivati dal conducente tramite apposito comando posto sul cruscotto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '305', '162002', '', '', 'Sostituzione dello sterzo', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '305', '162002', '2829', '1', 'Il volante di un veicolo può essere sostituito solo da un''officina specializzata', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '305', '162002', '2830', '2', 'Il volante di un veicolo si può sostituire con altro di diametro maggiore, quando si rileva eccessiva resistenza nella rotazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '306', '162003', '', '', 'Controllo dello sterzo', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '306', '162003', '2831', '1', 'L''efficienza dello sterzo può essere controllata soltanto in officina secondo apposite procedure', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '306', '162003', '2832', '2', 'L''efficienza dello sterzo può essere controllata personalmente, a vista, seguendo il manuale d''uso e manutenzione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '306', '162003', '2833', '3', 'L''efficienza dello sterzo può essere controllata chiedendo l''ausilio di un collega più esperto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '306', '162003', '2834', '4', 'Lo stato meccanico dello sterzo relativo al gioco dei giunti sferici a snodo deve essere verificato con le ruote sollevate dal suolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '307', '164001', '', '', 'Comportamenti per garantire visibilità e sicurezza', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '307', '164001', '2835', '1', 'Per garantire la sicurezza della circolazione è necessario, fra l''altro, mantenere libero il campo di visibilità del conducente', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '307', '164001', '2836', '2', 'Per garantire la sicurezza della circolazione è necessario, fra l''altro, evitare di applicare decalcomanie, ciondoli o altro al parabrezza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '307', '164001', '2837', '3', 'Per garantire la sicurezza della circolazione è necessario, fra l''altro, assicurarsi della pulizia ed efficienza degli specchi retrovisori interni ed esterni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '307', '164001', '2838', '4', 'Per garantire la sicurezza della circolazione è necessario, fra l''altro, assicurare la trasparenza e pulizia delle superfici vetrate, comprese quelle laterali', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '307', '164001', '2839', '5', 'Per garantire la sicurezza della circolazione è necessario, fra l''altro, rifornire periodicamente di liquido detergente il serbatoio dei dispositivi lavacristallo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '307', '164001', '2840', '6', 'Per garantire la sicurezza della circolazione è necessario, fra l''altro, verificare periodicamente le spazzole tergicristallo, sostituendole se non sono in buono stato', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '307', '164001', '2841', '7', 'Per garantire la sicurezza della circolazione è necessario che, in caso di lesioni al parabrezza, si provveda a coprirle con nastro adesivo trasparente non colorato', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '307', '164001', '2842', '8', 'Per garantire la sicurezza della circolazione è necessario, fra l''altro, installare sui finestrini pellicole opacizzate per evitare fenomeni di abbagliamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '307', '164001', '2843', '9', 'Per garantire la sicurezza della circolazione è necessario, fra l''altro, sostituire i catadiottri ogni volta che si effettua la revisione del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '307', '164001', '2844', '10', 'Per garantire la sicurezza della circolazione è necessario, fra l''altro, sostituire le lampadine dei proiettori di profondità ogni volta che si effettua la revisione del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '307', '164001', '2845', '11', 'Per garantire la sicurezza della circolazione è necessario, fra l''altro, sostituire tutti i fusibili dell''impianto elettrico ogni volta che si effettua la revisione del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '308', '1601005', '', '', 'Parabrezza lesionati', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '308', '1601005', '2846', '1', 'I parabrezza lesionati degli autoveicoli vanno sostituiti perché le crepe tendono ad estendersi nel tempo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '308', '1601005', '2847', '2', 'I parabrezza lesionati degli autoveicoli vanno sostituiti perché vi è il rischio che, a causa di un sobbalzo, il parabrezza vada in frantumi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '308', '1601005', '2848', '3', 'I parabrezza lesionati degli autoveicoli, anche se sono costituiti da vetro stratificato, devono comunque essere sostituito in caso di lesione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '308', '1601005', '2849', '4', 'I parabrezza lesionati degli autoveicoli con una lesione inferiore a dieci centimetri possono essere riparati con uno speciale nastro adesivo ad alta trasparenza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '309', '161002', '', '', 'Uso e regolazione degli specchi retrovisori', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '309', '161002', '2850', '1', 'È opportuno che il conducente verifichi, prima di ogni viaggio, la posizione, pulizia e stato generale degli specchi retrovisori del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '309', '161002', '2851', '2', 'Anche se gli specchi retrovisori sono ben regolati, esistono parti della strada che il conducente non riesce a vedere', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '309', '161002', '2852', '3', 'Vi sono parti della strada che il conducente riesce a vedere direttamente e altre che può vedere solo di riflesso attraverso gli specchi retrovisori', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '309', '161002', '2853', '4', 'Se gli specchi retrovisori sono ben regolati non esistono parti della strada che il conducente non possa vedere', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '309', '161002', '2854', '5', 'Se gli specchi retrovisori di un lato sono sporchi o danneggiati non vi sono pericoli se quelli dell''altro lato sono in perfetto ordine', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '309', '161002', '2855', '6', 'Gli specchi retrovisori possono essere integralmente sostituiti da telecamere opportunamente disposte', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '310', '161003', '', '', 'Controlli da effettuare prima della partenza 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2856', '1', 'Prima di partire, il conducente deve controllare la buona efficienza generale del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2857', '2', 'Prima di partire, il conducente deve controllare che tutte le spie luminose riguardanti il funzionamento dei vari impianti, si siano spente dopo l''avviamento del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2858', '3', 'Prima di partire per un viaggio, è bene eseguire il controllo dei livelli dell''olio, del liquido del raffreddamento e, se l''impianto di frenatura è idraulico o pneumoidraulico, del liquido dei freni', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2859', '4', 'Prima di partire, il conducente deve eseguire il controllo dello stato delle spazzole dei tergicristalli e del livello del liquido lavacristalli', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2860', '5', 'Prima di partire, il conducente deve eseguire il controllo del corretto funzionamento di proiettori e degli indicatori di direzione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2861', '6', 'Prima di partire, il conducente deve eseguire il controllo dell''integrità e della pulizia dei catadiottri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2862', '7', 'Prima di partire, il conducente deve eseguire il controllo dell''integrità, della corretta pressione e dello spessore minimo del battistrada di tutti gli pneumatici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2863', '8', 'Prima di partire, il conducente deve eseguire il controllo della presenza e dell''integrità di tutte le dotazioni di sicurezza previste per la circolazione del suo veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2864', '9', 'Prima di partire, il conducente deve eseguire il controllo dell''efficienza del cronotachigrafo e della sua carta tachigrafica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2865', '10', 'Prima di partire, il conducente deve assicurarsi di avere a bordo una batteria carica di riserva', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2866', '11', 'Se viaggia di notte, prima di partire, il conducente deve assicurarsi di avere a bordo due taniche da venticinque litri piene di combustibile di riserva', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2867', '12', 'Prima di partire, il conducente deve effettuare alcune prove di marcia e arresto in un cortile chiuso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2868', '13', 'Prima di partire, il conducente deve telefonare in azienda e ottenere l''autorizzazione all''inizio del viaggio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2869', '14', 'Prima di partire, il conducente deve assicurarsi di avere almeno una tanica da dieci litri d''acqua di riserva, in caso di problemi al sistema di raffreddamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2870', '15', 'Prima di partire, il conducente deve assicurarsi di avere almeno a bordo dieci litri di olio di riserva, in caso di problemi al sistema di lubrificazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2871', '16', 'Prima di partire, il conducente deve assicurarsi di avere a bordo almeno quattro litri di antigelo di riserva, in caso di abbassamento improvviso della temperatura esterna', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2872', '17', 'Il conducente deve effettuare verifiche di efficienza del veicolo prima della partenza solo se il veicolo è stato immatricolato da più di due anni', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2873', '18', 'Le principali verifiche che il conducente deve effettuare prima della partenza per un viaggio sono indicate sulla carta di circolazione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2874', '19', 'Il conducente deve fermarsi ogni centocinquanta chilometri percorsi, o ogni due ore di viaggio, per verificare lo stato di pressione degli pneumatici', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '310', '161003', '2875', '20', 'Prima di partire, non è necessario verificare lo stato dell''impianto di illuminazione se si prevede che il viaggio si esaurirà prima del tramonto del sole', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '311', '161007', '', '', 'Controlli da effettuare prima della partenza 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '311', '161007', '2876', '1', 'Prima della partenza, il conducente deve controllare il livello del liquido refrigerante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '311', '161007', '2877', '2', 'Prima della partenza, il conducente deve controllare la pressione degli pneumatici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '311', '161007', '2878', '3', 'Prima della partenza, il conducente deve controllare il corretto funzionamento del cronotachigrafo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '311', '161007', '2879', '4', 'Prima della partenza, il conducente deve controllare la presenza a bordo di tutti i documenti di viaggio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '311', '161007', '2880', '5', 'Prima della partenza, il conducente deve controllare lo stato e l''usura del battistrada degli pneumatici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '311', '161007', '2881', '6', 'Prima della partenza, il conducente deve controllare l''efficienza dell''impianto frenante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '311', '161007', '2882', '7', 'Prima della partenza, il conducente deve controllare il regolare assetto degli organi di sospensione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '311', '161007', '2883', '8', 'Prima della partenza, il conducente deve controllare l''efficienza dei dispositivi di sicurezza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '311', '161007', '2884', '9', 'Prima della partenza, il conducente deve verificare la presenza di almeno due ruote di scorta, in caso di viaggi superiori ai cinquecento chilometri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '311', '161007', '2885', '10', 'Prima della partenza, il conducente deve controllare la regolarità di pagamento delle rate dell''eventuale leasing gravante sul veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '311', '161007', '2886', '11', 'Prima della partenza, il conducente deve verificare la presenza a bordo del certificato di proprietà del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '311', '161007', '2887', '12', 'Prima della partenza, il conducente deve verificare la leggibilità delle insegne esterne con la pubblicità dell''azienda proprietaria del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '311', '161007', '2888', '13', 'Prima della partenza, il conducente deve verificare la data di fabbricazione degli pneumatici, che non deve essere anteriore a due anni', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '312', '163006', '', '', 'Controlli e manutenzione a ruote e pneumatici', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '312', '163006', '2889', '1', 'La non corretta convergenza delle ruote anteriori determina l''asimmetrico consumo degli pneumatici', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '312', '163006', '2890', '2', 'L''eccessiva pressione di gonfiaggio degli pneumatici provoca una maggiore rigidità di marcia, con minor confort per i passeggeri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '312', '163006', '2891', '3', 'Un''insufficiente pressione di gonfiaggio degli pneumatici provoca un aumento della deriva con conseguente minor tenuta di strada in curva', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '312', '163006', '2892', '4', 'La mancata equilibratura dello pneumatico ne provoca un anomalo consumo e una vibrazione dello sterzo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '312', '163006', '2893', '5', 'Le ruote gemellate, se poco gonfie, possono deteriorarsi a causa del loro reciproco sfregamento', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '312', '163006', '2894', '6', 'Se il conducente di un veicolo aziendale, durante un''ispezione, nota anomali consumi degli pneumatici, deve immediatamente riferirlo al responsabile del parco veicolare', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '312', '163006', '2895', '7', 'Se il conducente, durante un''ispezione al veicolo, nota forte riscaldamento di alcune parti degli pneumatici, deve evitare di riprendere il viaggio riferendo all''azienda della situazione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '312', '163006', '2896', '8', 'La perdita di convergenza del veicolo diminuisce il rischio di sbandamento del veicolo, ma ne aumenta il consumo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '312', '163006', '2897', '9', 'La non corretta equilibratura delle ruote diminuisce lo spazio di frenatura del veicolo, ma porta a fenomeni di vibrazione allo sterzo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '312', '163006', '2898', '10', 'Un''insufficiente pressione di gonfiaggio degli pneumatici, provoca un aumento di temperatura del liquido di raffreddamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '312', '163006', '2899', '11', 'Il conducente che noti un differente consumo delle varie zone del battistrada deve immediatamente sospendere il viaggio e attendere soccorsi', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '312', '163006', '2900', '12', 'Il conducente che noti un differente consumo delle varie zone del battistrada, deve immediatamente cambiare lo pneumatico in oggetto con quello di scorta', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '312', '163006', '2901', '13', 'Il conducente che noti un differente consumo delle varie zone del battistrada, deve immediatamente scaricare la merce o i passeggeri e avvisare l''azienda perché provveda al recupero e proseguire il viaggio a vuoto', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '313', '161004', '', '', 'Controlli e manutenzioni ammesse 1', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2902', '1', 'La pressione degli pneumatici va controllata quando sono freddi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2903', '2', 'È opportuno verificare periodicamente le condizioni delle cinghie di trasmissione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2904', '3', 'È opportuno verificare periodicamente il corretto serraggio dei morsetti dei due poli della batteria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2905', '4', 'Periodicamente deve essere controllato il livello dell''olio del servosterzo idraulico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2906', '5', 'Aggiungendo acqua pura al liquido refrigerante, aumenta il rischio di congelamento dello stesso alle basse temperature', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2907', '6', 'Periodicamente deve essere controllato il livello del liquido detergente del parabrezza e del lunotto', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2908', '7', 'Bisogna periodicamente controllare la pulizia del filtro dell''aria del motore, al fine di garantire una corretta combustione', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2909', '8', 'L''individuazione immediata di un guasto da parte di un conducente può evitare un aggravamento dell''avaria', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2910', '9', 'È consentito montare sullo stesso asse pneumatici di tipo e disegno diverso, purché lo spessore del battistrada sia superiore a tre millimetri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2911', '10', 'L''alternatore è collegato direttamente agli indicatori di direzione e ne permette il funzionamento a intermittenza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2912', '11', 'Se il veicolo stenta ad avviarsi, il conducente può provare a collegare tra loro i due poli della batteria con l''apposito cavo elettrico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2913', '12', 'Nel periodo invernale, è opportuno aggiungere liquido antigelo all''olio del differenziale e del cambio per evitare rischi di congelamento', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2914', '13', 'Si può iniziare il viaggio con il parabrezza che presenta punti di rottura, purché di modesta entità', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2915', '14', 'La pressione degli pneumatici va controllata quando sono in temperatura, cioè dopo avere percorso almeno una cinquantina di chilometri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2916', '15', 'È inutile sostituire le spazzole del tergicristallo danneggiate se nella giornata in cui si dovrà viaggiare le previsioni meteorologiche non prevedono precipitazioni', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2917', '16', 'La corretta pressione di gonfiaggio degli pneumatici è indipendente dal carico del veicolo', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2918', '17', 'I due pneumatici montati in gemello devono avere la somma delle profondità delle scolpiture dei due battistrada pari ad almeno tre millimetri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2919', '18', 'L''individuazione della causa dei guasti da parte del conducente è indispensabile per contenere i consumi di carburante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '313', '161004', '2920', '19', 'Il conducente deve firmare la carta di circolazione dopo la revisione periodica', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '314', '161004', '', '', 'Controlli e manutenzioni ammesse 2', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '314', '161004', '2921', '1', 'Il conducente deve essere in grado di capire quando è il caso di ricorrere immediatamente a personale specializzato per riparare un''avaria o quando può provvedere personalmente alla risoluzione temporanea del problema', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '314', '161004', '2922', '2', 'Il conducente deve segnalare tempestivamente alla propria impresa eventuali malfunzionamenti gravi del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '314', '161004', '2923', '3', 'Il conducente deve conoscere approfonditamente il funzionamento della strumentazione di bordo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '314', '161004', '2924', '4', 'Il conducente deve conoscere e saper consultare il libretto d''uso e manutenzione del veicolo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '314', '161004', '2925', '5', 'Prima di partire, il conducente deve essere certo che il veicolo sia in regola con la revisione periodica', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '314', '161004', '2926', '6', 'Prima di partire, il conducente deve essere certo che sia stata effettuata la revisione periodica del cronotachigrafo', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '314', '161004', '2927', '7', 'Prima di partire, il conducente deve essere certo che sia stata effettuata la revisione periodica dell''estintore di bordo nei tempi stabiliti dalla legge', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '314', '161004', '2928', '8', ' Il conducente deve azionare periodicamente l''estintore in luogo aperto, in modo da verificarne l''efficienza', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '314', '161004', '2929', '9', 'Il conducente deve essere in grado di mettere in fase l''impianto di distribuzione di un motore Diesel', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '314', '161004', '2930', '10', 'Il conducente deve personalmente procedere allo smontaggio del cronotachigrafo digitale nel caso di malfunzionamento durante il viaggio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '314', '161004', '2931', '11', 'Il conducente deve provvedere personalmente alla sostituzione delle pastiglie dei freni quando ritiene che siano eccessivamente consumate', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '314', '161004', '2932', '12', ' Il conducente deve fermarsi non appena riscontra un''anomalia del navigatore satellitare', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '315', '164004', '', '', 'Controlli e manutenzioni ammesse 3', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '315', '164004', '2933', '1', 'Per mantenere l''efficienza nel tempo del motore è importante effettuare il cambio periodico dell''olio lubrificante', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '315', '164004', '2934', '2', 'Ad ogni rifornimento di carburante è buona regola controllare il livello dell''olio', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '315', '164004', '2935', '3', 'Tra le manutenzioni che il conducente è autorizzato a compiere sono comprese la sostituzione o la pulizia dei filtri dell''aria e dell''olio,', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '315', '164004', '2936', '4', 'In un motore a Diesel, ogni ventimila chilometri occorre sostituire la guarnizione della testata', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '315', '164004', '2937', '5', 'In un motore Diesel, ogni diecimila chilometri occorre sostituire le candelette di preaccensione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '315', '164004', '2938', '6', 'Per mantenere l''efficienza nel tempo  del motore è importante effettuare il cambio del liquido di raffreddamento ogni venticinquemila chilometri', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '316', '162001', '', '', 'Uso e manutenzione del servosterzo', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '316', '162001', '2939', '1', 'Occorre verificare periodicamente il livello del liquido nel serbatoio del servosterzo idraulico', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '316', '162001', '2940', '2', 'In caso di emergenza, è possibile aggiungere acqua nel serbatoio del liquido idraulico del servosterzo, quando questo è molto basso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '316', '162001', '2941', '3', 'In caso di emergenza, è possibile aggiungere olio lubrificante nel serbatoio del liquido idraulico del servosterzo, quando questo è molto basso', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '316', '162001', '2942', '4', 'Il servosterzo può essere disinserito dall''autista tramite apposita levetta che si trova vicino al volante', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '317', '163004', '', '', 'Conseguenze dell''intasamento del filtro del combustibile', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '317', '163004', '2943', '1', 'L''intasamento del filtro del combustibile può comportare un irregolare afflusso di gasolio alla pompa', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '317', '163004', '2944', '2', 'L''intasamento del filtro del combustibile comporta una perdita di efficienza del motore', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '317', '163004', '2945', '3', 'L''intasamento del filtro del combustibile comporta maggiori consumi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '317', '163004', '2946', '4', 'L''intasamento del filtro del combustibile comporta l''emissione di fumo giallastro allo scarico', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '317', '163004', '2947', '5', 'L''intasamento del filtro del combustibile comporta l''insufficiente lubrificazione dei cilindri del motore', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '317', '163004', '2948', '6', 'L''intasamento del filtro del combustibile comporta una maggior pressione di iniezione del gasolio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('', '318', '161006', '', '', 'Controlli da effettuare prima della partenza su un autobus ', '');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '318', '161006', '2949', '1', 'Prima della partenza, il conducente dell''autobus deve verificare la presenza a bordo della cassetta di pronto soccorso', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '318', '161006', '2950', '2', 'Prima della partenza, il conducente dell''autobus deve verificare la presenza degli eventuali martelletti per i vetri delle uscite di sicurezza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '318', '161006', '2951', '3', 'Prima della partenza, il conducente dell''autobus deve verificare che gli estintori siano stati revisionati da non più di sei mesi', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '318', '161006', '2952', '4', 'Prima della partenza, il conducente dell''autobus di massa superiore a 3,5 t deve verificare che il veicolo sia dotato dei cunei ferma ruota', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '318', '161006', '2953', '5', 'Prima della partenza, il conducente dell''autobus deve verificare la presenza del triangolo di emergenza', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '318', '161006', '2954', '6', 'Prima di partire, il conducente deve eseguire il controllo dell''efficienza dei sistemi di salita e discesa dei passeggeri', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '318', '161006', '2955', '7', 'Prima della partenza, il conducente deve controllare l''efficienza dei sistemi di apertura delle porte e delle bagagliere', 'V');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '318', '161006', '2956', '8', 'Prima della partenza, il conducente dell''autobus deve controllare il livello dell''olio nel cambio', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '318', '161006', '2957', '9', 'Prima della partenza, il conducente dell''autobus deve controllare il livello del liquido lubrificante nella scatola del differenziale', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '318', '161006', '2958', '10', 'Prima della partenza, il conducente dell''autobus deve verificare l''efficienza della cinghia della pompa dell''acqua', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '318', '161006', '2959', '11', 'Prima della partenza, il conducente dell''autobus deve controllare il funzionamento corretto dell''apparato di iniezione', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '318', '161006', '2960', '12', 'Prima della partenza, il conducente dell''autobus deve verificare la pulizia del filtro dell''aria condizionata ', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '318', '161006', '2961', '13', 'Prima della partenza, il conducente dell''autobus deve verificare la presenza del pannello dei carichi sporgenti', 'F');
INSERT INTO D (Chapter, Topic_No, Topic_Nr, Ques_Num, Per_topic, Question, Answer) VALUES ('14', '318', '161006', '2962', '14', 'Prima della partenza, il conducente deve verificare la presenza a bordo di bibite di rinfresco per i passeggeri', 'F');
