INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '126', '3026', '', '', ' Semafori di corsia', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('3', '126', '3026', '750', '1', 'Nei semafori raffigurati le frecce accese indicano, se verdi, le direzioni verso le quali si può proseguire', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('3', '126', '3026', '751', '2', 'Nei semafori raffigurati le frecce accese indicano, se rosse, che non possiamo proseguire in quelle direzioni', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('3', '126', '3026', '752', '3', 'Nei semafori raffigurati le frecce accese indicano, se gialle fisse, di sgomberare l''incrocio o di fermarsi in condizioni di sicurezza', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('3', '126', '3026', '753', '4', 'Nei semafori raffigurati le frecce accese indicano, se gialle lampeggianti, di fermarci in ogni caso', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('3', '126', '3026', '754', '5', 'Nei semafori raffigurati le frecce accese indicano, se verdi e rivolte verso l''alto, che si può proseguire in tutte le direzioni', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('3', '126', '3026', '755', '6', 'Nei semafori raffigurati le frecce accese indicano, se rosse, di svoltare a destra ma con prudenza', 'F', '');
