INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('3', '127', '3029', '756', '1', 'Il dispositivo raffigurato si può trovare subito prima di passaggi a livello con semibarriere', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('3', '127', '3029', '757', '2', 'Il dispositivo raffigurato quando è in funzione vieta il passaggio di veicoli e pedoni', 'V', '');
