INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('3', '127', '3029', '759', '4', 'Il dispositivo raffigurato quando lampeggia alternativamente invita ad attraversare rapidamente il passaggio a livello', 'F', '');
