INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('3', '127', '3029', '761', '6', 'Il dispositivo raffigurato si può trovare in prossimità di un passaggio a livello con barriere', 'F', '');
