INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('3', '137', '3033', '822', '6', 'Le frecce direzionali raffigurate, segnate sulla carreggiata, permettono di scegliere la corsia di preselezione se le strisce di corsia sono continue', 'F', '');
