INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '161', '5028', '990', '5', 'Per cambiare corsia, dobbiamo segnalare sempre la manovra in tempo utile con il clacson', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '161', '5028', '991', '6', 'Per cambiare corsia, dobbiamo accertarci che le corsie siano separate da strisce continue', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '162', '5025', '', '', ' Cambio di direzione', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '162', '5025', '992', '1', 'Quando si vuole cambiare direzione, per svoltare a destra bisogna tenersi sul margine destro della strada', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '162', '5025', '993', '2', 'Quando si vuole cambiare direzione, per svoltare a sinistra su strade a doppio senso bisogna spostarsi al centro', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '162', '5025', '994', '3', 'Quando si vuole cambiare direzione, su strada a senso unico per svoltare a sinistra bisogna spostarsi sul margine sinistro', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '162', '5025', '995', '4', 'Quando si vuole cambiare direzione, l''indicatore di direzione deve restare in funzione per l''intera durata della manovra', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '162', '5025', '996', '5', 'Quando si vuole cambiare direzione, per svoltare a sinistra bisogna sempre girare intorno al centro dell''incrocio', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '162', '5025', '997', '6', 'Quando si vuole cambiare direzione, è necessario in ogni caso fare uso del clacson', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '163', '5023', '', '', 'Svolta a destra', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '163', '5023', '998', '1', 'La svolta a destra si effettua tenendosi il più vicino possibile al margine destro della strada', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '163', '5023', '999', '2', 'La svolta a destra si effettua azionando l''indicatore di direzione con sufficiente anticipo', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '163', '5023', '1000', '3', 'La svolta a destra si effettua regolando la velocità man mano che ci si avvicina all''incrocio', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '163', '5023', '1001', '4', 'La svolta a destra si effettua tenendosi il più vicino possibile al margine sinistro della strada', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '163', '5023', '1002', '5', 'La svolta a destra si effettua spostandosi sulla corsia di sinistra', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '163', '5023', '1003', '6', 'La svolta a destra si effettua portandosi al centro dell''incrocio', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '164', '5026', '', '', 'Svolta a sinistra', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '164', '5026', '1004', '1', 'Nell''effettuare la svolta a sinistra si ha l''obbligo di non imboccare contromano la nuova strada', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '164', '5026', '1005', '2', 'Nell''effettuare la svolta a sinistra si ha l''obbligo di rispettare, di norma, la precedenza dei veicoli provenienti da destra', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '164', '5026', '1006', '3', 'Nell''effettuare la svolta a sinistra si ha l''obbligo di lasciare, di norma, il centro dell''incrocio alla nostra destra', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '164', '5026', '1007', '4', 'Nell''effettuare la svolta a sinistra si ha l''obbligo di spostarsi sempre nella corsia di destra', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '164', '5026', '1008', '5', 'Nell''effettuare la svolta a sinistra si ha l''obbligo di disporsi a sinistra della linea continua', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '164', '5026', '1009', '6', 'Nell''effettuare la svolta a sinistra si ha l''obbligo di guardare solo a destra', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '165', '5024', '', '', 'Svolta a sinistra da una strada a doppio senso di circolazione', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '165', '5024', '1010', '1', 'In una strada a doppio senso di circolazione, per svoltare a sinistra il conducente deve avvicinarsi il più possibile al centro della strada', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '165', '5024', '1011', '2', 'In una strada a doppio senso di circolazione, per svoltare a sinistra il conducente deve lasciare di norma il centro dell''incrocio alla propria destra', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '165', '5024', '1012', '3', 'In una strada a doppio senso di circolazione, per svoltare a sinistra il conducente deve dare la precedenza ai veicoli provenienti da destra, salvo diversa segnalazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '165', '5024', '1013', '4', 'In una strada a doppio senso di circolazione, per svoltare a sinistra il conducente deve spostarsi il più possibile sul margine sinistro della strada', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '165', '5024', '1014', '5', 'In una strada a doppio senso di circolazione, per svoltare a sinistra il conducente deve effettuare la svolta con rapidità per evitare i veicoli provenienti da destra e da sinistra', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '165', '5024', '1015', '6', 'In una strada a doppio senso di circolazione, per svoltare a sinistra il conducente deve dare sempre la precedenza ai veicoli provenienti da sinistra', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '166', '5027', '', '', ' Svolta a sinistra da una strada a senso unico', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '166', '5027', '1016', '1', 'Percorrendo una strada a senso unico, la svolta a sinistra si effettua tenendosi il più vicino possibile al margine sinistro della strada', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '166', '5027', '1017', '2', 'Percorrendo una strada a senso unico, la svolta a sinistra si effettua lasciando di norma il centro dell''incrocio alla nostra destra', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '166', '5027', '1018', '3', 'Percorrendo una strada a senso unico, la svolta a sinistra si effettua dando la precedenza ai veicoli che provengono da destra', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '166', '5027', '1019', '4', 'Percorrendo una strada a senso unico, la svolta a sinistra si effettua dando in ogni caso la precedenza ai veicoli provenienti da sinistra', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '166', '5027', '1020', '5', 'Percorrendo una strada a senso unico, la svolta a sinistra si effettua senza che sia necessario rallentare, se l''incrocio è libero', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '166', '5027', '1021', '6', 'Percorrendo una strada a senso unico, la svolta a sinistra si effettua se è ammessa la circolazione per file parallele, incolonnandosi in una corsia qualsiasi', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '167', '5029', '', '', 'Comportamenti da tenere avvicinandosi ad un incrocio', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '167', '5029', '1022', '1', 'Giungendo in prossimità di un incrocio si deve, conducendo un ciclomotore, incolonnarsi con gli altri veicoli in attesa', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '167', '5029', '1023', '2', 'Giungendo in prossimità di un incrocio si deve segnalare in tempo utile l''intenzione di svoltare', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '167', '5029', '1024', '3', 'Giungendo in prossimità di un incrocio si deve usare la massima prudenza al fine di evitare incidenti', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '167', '5029', '1025', '4', 'Giungendo in prossimità di un incrocio si deve dare sempre la precedenza a chi proviene da destra', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '167', '5029', '1026', '5', 'Giungendo in prossimità di un incrocio si deve sorpassare a sinistra se vogliamo svoltare a destra', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '167', '5029', '1027', '6', 'Giungendo in prossimità di un incrocio si deve, con semaforo verde, accelerare senza usare prudenza', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '168', '5030', '', '', 'Comportamenti vietati in vicinanza o in corrispondenza degli incroci', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '168', '5030', '1028', '1', 'In prossimità o in corrispondenza degli incroci è vietato procedere a zig zag anche se ci sono strisce tratteggiate', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '168', '5030', '1029', '2', 'In prossimità o in corrispondenza degli incroci è vietato modificare improvvisamente la direzione di marcia', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '168', '5030', '1030', '3', 'In prossimità o in corrispondenza degli incroci è vietato effettuare l''inversione di marcia', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '168', '5030', '1031', '4', 'In prossimità o in corrispondenza degli incroci è vietato usare i fari anabbaglianti', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '168', '5030', '1032', '5', 'In prossimità o in corrispondenza degli incroci è vietato azionare l''indicatore di direzione se si svolta', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '168', '5030', '1033', '6', 'In prossimità o in corrispondenza degli incroci è vietato superare la velocità di 20 km/h', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '169', '5001', '', '', 'Regolazione della velocità (1)', '', '');
