INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '180', '5021', '', '', 'Cautele da adottare prima della manovra del sorpasso', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '180', '5021', '1101', '1', 'Il conducente che intende sorpassare deve assicurarsi che la visibilità sia tale da poter effettuare la manovra senza pericolo', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '180', '5021', '1102', '2', 'Il conducente che intende sorpassare deve assicurarsi che non vi siano segnali che vietino la manovra', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '180', '5021', '1103', '3', 'Il conducente che intende sorpassare deve assicurarsi che nessun conducente abbia già iniziato la medesima manovra', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '180', '5021', '1104', '4', 'Il conducente che intende sorpassare deve assicurarsi che il veicolo proveniente in senso contrario abbia uno spazio di almeno 10 m per arrestarsi', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '180', '5021', '1105', '5', 'Il conducente che intende sorpassare deve assicurarsi che il veicolo che precede non stia rallentando', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '180', '5021', '1106', '6', 'Il conducente che intende sorpassare deve assicurarsi che il conducente che precede abbia dato via libera', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '181', '5015', '', '', 'Cautele da adottare nella manovra del sorpasso', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '181', '5015', '1107', '1', 'Nell''eseguire la manovra di sorpasso bisogna segnalare la manovra azionando l''indicatore di direzione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '181', '5015', '1108', '2', 'Nell''eseguire la manovra di sorpasso bisogna affiancare il veicolo sulla sua sinistra lasciando un adeguato margine di sicurezza', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '181', '5015', '1109', '3', 'Nell''eseguire la manovra di sorpasso bisogna assicurarsi che si disponga di uno spazio libero sufficiente', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '181', '5015', '1110', '4', 'Nell''eseguire la manovra di sorpasso occorre tener presente che sulle strade provinciali il sorpasso è ammesso solo di giorno', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '181', '5015', '1111', '5', 'Nell''eseguire la manovra di sorpasso dei motocicli, si può effettuare la manovra sia a destra che a sinistra', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '181', '5015', '1112', '6', 'Nell''eseguire la manovra di sorpasso in curva si deve sempre suonare il clacson', 'F', '');
