INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '182', '5012', '1118', '6', 'E'' vietato il sorpasso quando il veicolo che ci precede ha azionato l''indicatore destro per fermarsi', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '182', '5012', '1119', '7', 'E'' vietato il sorpasso quando il veicolo da superare è un ciclomotore', 'F', '');
