INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '183', '5013', '1120', '1', 'E'' vietato il sorpasso nelle curve, su strade con due corsie e a doppio senso di circolazione', 'V', '');
