INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '184', '5014', '1126', '1', 'E'' vietato il sorpasso quando il veicolo che precede ha segnalato di voler iniziare il sorpasso', 'V', '');
