INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '184', '5014', '1130', '5', 'E'' vietato il sorpasso nelle curve, su strade a due corsie per ogni senso di marcia', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '184', '5014', '1131', '6', 'E'' vietato il sorpasso nei dossi, su strade a due corsie per ogni senso di marcia', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '185', '5016', '', '', 'Casi in cui è vietato il sorpasso (4)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '185', '5016', '1132', '1', 'Non si può sorpassare quando si deve oltrepassare una striscia longitudinale bianca continua, singola o doppia', 'V', '');
