INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '185', '5016', '1134', '3', 'Non si può sorpassare un veicolo che si sia fermato per dare la precedenza ad altri veicoli', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '185', '5016', '1135', '4', 'Non si può sorpassare in corrispondenza dei dossi su strade a senso unico a due corsie', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '185', '5016', '1136', '5', 'Non si può sorpassare un veicolo che abbia segnalato l''intenzione di accostarsi a destra', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '185', '5016', '1137', '6', 'Non si può sorpassare sulla destra, il tram che marcia al centro della strada', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '186', '5019', '', '', 'Casi in cui è vietato il sorpasso (5)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '186', '5019', '1138', '1', 'Il sorpasso è vietato quando un veicolo si è fermato per consentire ai pedoni di attraversare la strada', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '186', '5019', '1139', '2', 'Il sorpasso è vietato quando il veicolo che segue abbia già segnalato l''intenzione di effettuare analoga manovra', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '186', '5019', '1140', '3', 'Il sorpasso è vietato quando ci sia l''apposito segnale', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '186', '5019', '1141', '4', 'Il sorpasso è vietato quando sulla strada è tracciata la linea longitudinale tratteggiata', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '186', '5019', '1142', '5', 'Il sorpasso è vietato quando il veicolo che ci precede è uno dei mezzi di soccorso con il lampeggiante giallo acceso', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '186', '5019', '1143', '6', 'Il sorpasso è vietato quando si è in corrispondenza di una curva o di un dosso sulle strade a senso unico', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '187', '5018', '', '', 'Casi in cui è permesso il sorpasso in presenza di un dosso', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '187', '5018', '1144', '1', 'Sulle strade a senso unico, si può effettuare un sorpasso anche in presenza di un dosso', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '187', '5018', '1145', '2', 'Sulle strade a due carreggiate separate, si può effettuare un sorpasso anche in presenza di un dosso', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '187', '5018', '1146', '3', 'In presenza di un dosso si può effettuare un sorpasso sul suo tratto in discesa, salvo diversa segnalazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '187', '5018', '1147', '4', 'In presenza di un dosso, si può sempre effettuare un sorpasso sul suo tratto in salita', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '187', '5018', '1148', '5', 'E’ comunque consigliabile effettuare i sorpassi in scia del veicolo che ci precede', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '187', '5018', '1149', '6', 'In presenza di nebbia, è consigliabile superare un veicolo che ci precede a velocità moderata per non rischiare di tamponarlo', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '188', '5020', '', '', 'Casi in cui è permesso il sorpasso a destra', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '188', '5020', '1150', '1', 'Il sorpasso a destra è permesso quando il conducente da sorpassare ha segnalato che intende svoltare a sinistra', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '188', '5020', '1151', '2', 'Il sorpasso a destra è permesso quando si sorpassa un tram in marcia e la larghezza della carreggiata alla sua destra lo permette', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '188', '5020', '1152', '3', 'Il sorpasso a destra è permesso quando avviene nei confronti dei motocicli e ci sia spazio sufficiente', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '188', '5020', '1153', '4', 'Il sorpasso a destra è permesso quando si percorre una strada extraurbana a senso unico', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '188', '5020', '1154', '5', 'Il sorpasso a destra è permesso quando si percorre un tratto di strada a traffico molto lento', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '189', '5017', '', '', 'Spazio necessario per la manovra di sorpasso', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '189', '5017', '1155', '1', 'Lo spazio necessario alla manovra di sorpasso aumenta se il veicolo da sorpassare accelera', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '189', '5017', '1156', '2', 'Lo spazio necessario alla manovra di sorpasso aumenta in relazione alla lunghezza del veicolo da sorpassare', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '189', '5017', '1157', '3', 'Lo spazio necessario alla manovra di sorpasso dipende dalla velocità dei due veicoli', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '189', '5017', '1158', '4', 'Lo spazio necessario alla manovra di sorpasso non interessa ai fini della sicurezza stradale', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '189', '5017', '1159', '5', 'Lo spazio necessario alla manovra di sorpasso diminuisce se il veicolo che sta sorpassando rallenta', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '189', '5017', '1160', '6', 'Lo spazio necessario alla manovra di sorpasso non dipende dalla lunghezza del veicolo da sorpassare', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '190', '5022', '', '', 'Obblighi del conducente del veicolo che viene sorpassato', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '190', '5022', '1161', '1', 'Il conducente del veicolo che viene sorpassato ha l''obbligo di facilitare la manovra di sorpasso', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '190', '5022', '1162', '2', 'Il conducente del veicolo che viene sorpassato ha l''obbligo di non costituire pericolo o intralcio alla circolazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '190', '5022', '1163', '3', 'Il conducente del veicolo che viene sorpassato ha l''obbligo di non gareggiare in velocità con il veicolo che sta sorpassando', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '190', '5022', '1164', '4', 'Il conducente del veicolo che viene sorpassato ha l''obbligo di non accelerare', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '190', '5022', '1165', '5', 'Il conducente del veicolo che viene sorpassato ha l''obbligo di ridurre sempre la propria velocità', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '190', '5022', '1166', '6', 'Il conducente del veicolo che viene sorpassato ha l''obbligo di azionare l''indicatore di direzione destro', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '190', '5022', '1167', '7', 'Il conducente del veicolo che viene sorpassato ha l''obbligo di portarsi al centro della carreggiata per evitare sorpassi in scia', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('5', '190', '5022', '1168', '8', 'Il conducente del veicolo che viene sorpassato ha l''obbligo di accelerare per permettere al veicolo sorpassante di prendere la scia per aumentare la velocità', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '191', '6013', '', '', 'Definizioni stradali: l''attraversamento pedonale', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '191', '6013', '1169', '1', 'L''attraversamento pedonale è una parte della strada dipinta con strisce bianche per l''attraversamento dei pedoni', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '191', '6013', '1170', '2', 'L''attraversamento pedonale è una parte della strada in cui i veicoli debbono dare la precedenza ai pedoni che attraversano', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '191', '6013', '1171', '3', 'L''attraversamento pedonale è una parte della strada destinata al transito dei pedoni che attraversano', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '191', '6013', '1172', '4', 'L''attraversamento pedonale è una parte della strada in cui è vietato il transito dei veicoli', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '191', '6013', '1173', '5', 'L''attraversamento pedonale è una parte della strada dipinta con strisce blu', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '191', '6013', '1174', '6', 'L''attraversamento pedonale è una parte al di fuori della strada e destinata al transito dei pedoni', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '192', '6016', '', '', 'Definizioni stradali: il marciapiede', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '192', '6016', '1175', '1', 'Il marciapiede è una parte della strada riservata ai pedoni', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '192', '6016', '1176', '2', 'Il marciapiede è una parte della strada riservata ai pedoni e anche ai veicoli in sosta se ci sono strisce di parcheggio', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '192', '6016', '1177', '3', 'Il marciapiede è una parte della strada vietata alla circolazione dei ciclomotori', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '192', '6016', '1178', '4', 'Il marciapiede è una parte della strada vietata alla circolazione dei pedoni', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '192', '6016', '1179', '5', 'Il marciapiede è una parte della strada riservata alle sole biciclette', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '192', '6016', '1180', '6', 'Il marciapiede è una parte della strada riservata ai veicoli in sosta, ma vietata ai pedoni', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '193', '6017', '', '', 'Definizioni stradali: il salvagente', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '193', '6017', '1181', '1', 'Il salvagente è destinato al riparo o alla sosta dei pedoni che attraversano la strada', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '193', '6017', '1182', '2', 'Il salvagente agevola la salita e la discesa dei passeggeri da tram, filobus od autobus', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '193', '6017', '1183', '3', 'Il salvagente può essere segnalato con colonnine luminose e luci semaforiche gialle lampeggianti', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '193', '6017', '1184', '4', 'Il salvagente può essere segnalato con colonnine luminose a luce gialla fissa', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '193', '6017', '1185', '5', 'Il salvagente è una zona destinata alla sosta dei ciclomotori', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '193', '6017', '1186', '6', 'Il salvagente è una zona dove è consentito il transito dei ciclomotori', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '193', '6017', '1187', '7', 'Il salvagente è una zona della strada destinata al primo soccorso', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '194', '6015', '', '', 'Utenti della strada ammessi a passare nelle aree pedonali', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '194', '6015', '1188', '1', 'Nelle aree pedonali possono passare, di norma, soltanto i pedoni, le biciclette e i veicoli di soccorso', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '194', '6015', '1189', '2', 'Nelle aree pedonali possono passare i veicoli al servizio di persone disabili, se espressamente consentito', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '194', '6015', '1190', '3', 'Nelle aree pedonali possono passare i veicoli in servizio di polizia, con sirena e con luce lampeggiante blu in funzione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '194', '6015', '1191', '4', 'Nelle aree pedonali possono passare i ciclomotori', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '194', '6015', '1192', '5', 'Nelle aree pedonali possono passare i quadricicli a motore purché non superino la velocità di 50 km/h', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '194', '6015', '1193', '6', 'Nelle aree pedonali possono passare tutti i veicoli sprovvisti di motore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '195', '6014', '', '', 'Definizioni stradali: la zona a traffico limitato', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '195', '6014', '1194', '1', 'Una zona a traffico limitato è un''area nella quale la circolazione è limitata ai soli veicoli autorizzati', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '195', '6014', '1195', '2', 'Una zona a traffico limitato è una area in cui la circolazione è limitata ad ore stabilite', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '195', '6014', '1196', '3', 'Una zona a traffico limitato è un''area delimitata da appositi segnali stradali', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '195', '6014', '1197', '4', 'Una zona a traffico limitato è un''area nella quale è permesso solo il transito di ciclomotori e biciclette', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '195', '6014', '1198', '5', 'Una zona a traffico limitato è un''area sempre e comunque esclusa al transito dei veicoli a motore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '195', '6014', '1199', '6', 'Una zona a traffico limitato è un''area nella quale l''accesso ai pedoni è soggetto a limitazioni', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '196', '6004', '', '', 'Casi in cui è consentita o meno la fermata', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '196', '6004', '1200', '1', 'La fermata di un veicolo è consentita, di norma, lungo il margine destro della carreggiata', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '196', '6004', '1201', '2', 'La fermata di un veicolo non è consentita sulle strisce pedonali', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '196', '6004', '1202', '3', 'La fermata di un veicolo non deve comunque causare intralcio alla circolazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '196', '6004', '1203', '4', 'La fermata di un veicolo può avere la durata massima di cinque minuti', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '196', '6004', '1204', '5', 'La fermata di un veicolo è consentita in curva', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '196', '6004', '1205', '6', 'La fermata di un veicolo è consentita sui dossi', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '197', '6002', '', '', 'Casi in cui è vietata la fermata (1)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '197', '6002', '1206', '1', 'La fermata è vietata in corrispondenza dei passaggi a livello', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '197', '6002', '1207', '2', 'La fermata è vietata in corrispondenza e in prossimità degli incroci', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '197', '6002', '1208', '3', 'La fermata è vietata sui binari del tram posti al centro della strada', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '197', '6002', '1209', '4', 'La fermata è vietata in prossimità dei segnali stradali', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '197', '6002', '1210', '5', 'La fermata è vietata sempre, dove esiste il segnale DIVIETO DI SOSTA', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '197', '6002', '1211', '6', 'La fermata è vietata sulle strade che non siano suddivise in corsie', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '198', '6003', '', '', 'Casi in cui è vietata la fermata (2)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '198', '6003', '1212', '1', 'La fermata è vietata in presenza dell''apposito segnale', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '198', '6003', '1213', '2', 'La fermata è vietata all''entrata e all''uscita delle piste ciclabili', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '198', '6003', '1214', '3', 'La fermata è vietata sulle strisce pedonali', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '198', '6003', '1215', '4', 'La fermata è vietata nei lunghi rettilinei', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '198', '6003', '1216', '5', 'La fermata è vietata sul margine destro della carreggiata', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '198', '6003', '1217', '6', 'La fermata è vietata nello spazio riservato alla fermata degli autobus', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '199', '6001', '', '', 'Casi in cui è vietata la fermata (3)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '199', '6001', '1218', '1', 'La fermata del veicolo è sempre vietata sui marciapiedi, salvo diversa segnalazione, nonché sulle strisce pedonali', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '199', '6001', '1219', '2', 'La fermata del veicolo è sempre vietata in tutti i luoghi dove sia di intralcio alla circolazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '199', '6001', '1220', '3', 'La fermata del veicolo è sempre vietata nelle curve e sui dossi', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '199', '6001', '1221', '4', 'La fermata del veicolo è sempre vietata sul margine destro della strada', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '199', '6001', '1222', '5', 'La fermata del veicolo è sempre vietata davanti ai cassonetti dei rifiuti urbani', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '199', '6001', '1223', '6', 'La fermata del veicolo è sempre vietata in seconda fila', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '200', '6009', '', '', 'Casi in cui è vietata la sosta (1)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '200', '6009', '1224', '1', 'E'' vietato sostare negli spazi riservati alla sosta dei veicoli per persone disabili', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '200', '6009', '1225', '2', 'E'' vietato sostare occupando nel parcheggio più di uno spazio', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '200', '6009', '1226', '3', 'E'' vietato sostare davanti ai distributori di carburante, durante l''orario di apertura', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '200', '6009', '1227', '4', 'E'' vietato sostare nelle strade senza marciapiede', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '200', '6009', '1228', '5', 'E'' vietato sostare lungo le strade prive di illuminazione pubblica', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '200', '6009', '1229', '6', 'E'' vietato sostare lungo le strade a forte pendenza', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '201', '6006', '', '', 'Casi in cui è vietata la sosta (2)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '201', '6006', '1230', '1', 'E'' proibito sostare in corrispondenza degli spazi destinati al passaggio dei disabili', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '201', '6006', '1231', '2', 'E'' proibito sostare sui binari tranviari', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '201', '6006', '1232', '3', 'E'' proibito sostare nelle corsie riservate ai mezzi pubblici', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '201', '6006', '1233', '4', 'E'' proibito sostare alle fermate degli autobus, solo se lo spazio è delimitato dalle strisce gialle', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '201', '6006', '1234', '5', 'E'' proibito sostare nelle discese pericolose', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '201', '6006', '1235', '6', 'E'' proibito sostare sulle strade a senso unico', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '202', '6007', '', '', 'Casi in cui è vietata la sosta (3)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '202', '6007', '1236', '1', 'La sosta è vietata nelle gallerie e sotto i ponti, salvo diversa segnalazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '202', '6007', '1237', '2', 'La sosta è vietata dove il bordo del marciapiede è dipinto con strisce gialle e nere', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '202', '6007', '1238', '3', 'La sosta è vietata negli spazi riservati alla sosta dei veicoli per persone disabili', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '202', '6007', '1239', '4', 'La sosta è vietata sempre davanti alle sale gioco', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '202', '6007', '1240', '5', 'La sosta è vietata sui marciapiedi, in ogni caso', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '202', '6007', '1241', '6', 'La sosta è vietata in prossimità dei passaggi a livello, solo se senza barriere', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '203', '6008', '', '', 'Casi in cui è vietata la sosta (4)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '203', '6008', '1242', '1', 'La sosta è vietata davanti ai passi carrabili', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '203', '6008', '1243', '2', 'La sosta è vietata sui marciapiedi, salvo diversa segnalazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '203', '6008', '1244', '3', 'La sosta è vietata in corrispondenza di incroci regolati da semaforo', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '203', '6008', '1245', '4', 'La sosta è vietata sempre sui lunghi rettilinei', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '203', '6008', '1246', '5', 'La sosta è vietata nelle strade a senso unico', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '203', '6008', '1247', '6', 'La sosta è vietata fuori dai centri abitati', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '204', '6005', '', '', 'Casi in cui è vietata la sosta (5)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '204', '6005', '1248', '1', 'La sosta non è consentita in corrispondenza o in prossimità dei passaggi a livello', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '204', '6005', '1249', '2', 'La sosta non è consentita nelle aree destinate alla fermata o alla sosta dei taxi', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '204', '6005', '1250', '3', 'La sosta non è consentita davanti ai cassonetti dei rifiuti urbani', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '204', '6005', '1251', '4', 'La sosta non è consentita nelle gallerie urbane a senso unico, solo se non sufficientemente illuminate', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '204', '6005', '1252', '5', 'La sosta non è consentita in prossimità delle strisce pedonali', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '204', '6005', '1253', '6', 'La sosta non è consentita negli spazi appositamente delimitati per la sosta', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '205', '6010', '', '', 'Sosta in doppia fila', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '205', '6010', '1254', '1', 'La sosta in doppia fila è vietata ai quadricicli leggeri a motore', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '205', '6010', '1255', '2', 'La sosta in doppia fila può ostacolare la circolazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '205', '6010', '1256', '3', 'La sosta in doppia fila è ammessa se i veicoli sono a due ruote', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '205', '6010', '1257', '4', 'La sosta in doppia fila è consentita ai quadricicli leggeri a motore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '205', '6010', '1258', '5', 'La sosta in doppia fila, nei centri abitati, è sempre vietata ai ciclomotori a due ruote', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '205', '6010', '1259', '6', 'La sosta in doppia fila è consentita solo per pochi minuti ai quadricicli leggeri a motore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '206', '6011', '', '', 'Parcheggio autorizzato ', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '206', '6011', '1260', '1', 'Quando si esce da un parcheggio autorizzato, si ha l''obbligo di dare la precedenza ai veicoli in transito', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '206', '6011', '1261', '2', 'Quando si lascia il veicolo in sosta in un parcheggio autorizzato, si ha l''obbligo di non ostacolare gli altri utenti della strada presenti nel parcheggio', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '206', '6011', '1262', '3', 'Quando si lascia il veicolo in sosta in un parcheggio autorizzato, si ha l''obbligo di disporre il veicolo all''interno degli appositi spazi delimitati', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '206', '6011', '1263', '4', 'Un parcheggio autorizzato a pagamento, è delimitato da strisce gialle', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '206', '6011', '1264', '5', 'Un parcheggio autorizzato delimitato da strisce azzurre è riservato alla sosta dei soli taxi', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '206', '6011', '1265', '6', 'Non è ammesso sostare nelle immediate vicinanze di un parcheggio autorizzato', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '207', '6012', '', '', 'Comportamenti da tenere quando ci si immette nella circolazione', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '207', '6012', '1266', '1', 'Uscendo da un parcheggio, per immettersi nella circolazione occorre dare la precedenza ai veicoli in transito ', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '207', '6012', '1267', '2', 'Uscendo da un parcheggio, per immettersi nella circolazione occorre segnalare l''intenzione di immettersi nella corsia di marcia ', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '207', '6012', '1268', '3', 'Uscendo da un parcheggio, per immettersi nella circolazione occorre azionare l''indicatore di direzione dopo essersi immessi nel flusso della circolazione', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '207', '6012', '1269', '4', 'Uscendo da un parcheggio, per immettersi nella circolazione occorre disinserire l''indicatore di direzione solo quando si è raggiunta una velocità adeguata al traffico', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '207', '6012', '1270', '5', 'Uscendo da un parcheggio, per immettersi nella circolazione occorre azionare l''indicatore di direzione, solo se sopraggiungono veicoli ', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('6', '207', '6012', '1271', '6', 'Uscendo da un parcheggio, per immettersi nella circolazione occorre accelerare a fondo facendo slittare i pneumatici ', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '208', '7023', '', '', ' Cause degli incidenti stradali (1)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '208', '7023', '1272', '1', 'Può essere causa di incidente l’inesperienza del conducente', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '208', '7023', '1273', '2', 'Può essere causa di incidente lo stato psicofisico alterato', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '208', '7023', '1274', '3', 'Può essere causa di incidente affrontare una curva a velocità eccessiva', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '208', '7023', '1275', '4', 'Può essere causa di incidente la sfortuna', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '208', '7023', '1276', '5', 'Può essere causa di incidente la marca del ciclomotore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '208', '7023', '1277', '6', 'Può essere causa di incidente l''uso del casco integrale', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '209', '7022', '', '', 'Cause degli incidenti stradali (2)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '209', '7022', '1278', '1', 'Le cause più frequenti di incidenti stradali sono attribuibili ad imprudenza o inosservanza di norme', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '209', '7022', '1279', '2', 'Le cause più frequenti di incidenti stradali dipendono dal comportamento umano', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '209', '7022', '1280', '3', 'Le cause più frequenti di incidenti stradali sono per la maggior parte dipendenti da comportamenti errati', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '209', '7022', '1281', '4', 'Le cause più frequenti di incidenti stradali dipendono principalmente da difetti del veicolo', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '209', '7022', '1282', '5', 'Le cause più frequenti di incidenti stradali dipendono dalla sfortuna', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '209', '7022', '1283', '6', 'Le cause più frequenti di incidenti stradali dipendono principalmente da errori di progettazione della strada', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '210', '7002', '', '', ' Cause degli incidenti stradali dovuti alle condizioni della strada', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '210', '7002', '1284', '1', 'Causa frequente di incidenti dovuti alle condizioni della strada può essere fondo ghiacciato', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '210', '7002', '1285', '2', 'Causa frequente di incidenti dovuti alle condizioni della strada può essere strada coperta di foglie', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '210', '7002', '1286', '3', 'Causa frequente di incidenti dovuti alle condizioni della strada può essere presenza di pietrisco sulla carreggiata', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '210', '7002', '1287', '4', 'Causa frequente di incidenti dovuti alle condizioni della strada può essere presenza di olio sull''asfalto', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '210', '7002', '1288', '5', 'Causa frequente di incidenti dovuti alle condizioni della strada può essere fondo asfaltato ruvido', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '210', '7002', '1289', '6', 'Causa frequente di incidenti dovuti alle condizioni della strada può essere una buona illuminazione', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '211', '7018', '', '', ' Cautele da adottare per evitare incidenti (1)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '211', '7018', '1290', '1', 'Al fine di evitare il prodursi di incidenti stradali, il conducente controllerà spesso l''efficienza del clacson e delle luci', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '211', '7018', '1291', '2', 'Al fine di evitare il prodursi di incidenti stradali, il conducente controllerà spesso lo spessore del battistrada dei pneumatici', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '211', '7018', '1292', '3', 'Al fine di evitare il prodursi di incidenti stradali, il conducente controllerà spesso il corretto funzionamento degli organi dello sterzo (manubrio)', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '211', '7018', '1293', '4', 'Al fine di evitare il prodursi di incidenti stradali, il conducente controllerà spesso il livello del liquido contenuto nella batteria', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '211', '7018', '1294', '5', 'Al fine di evitare il prodursi di incidenti stradali, il conducente controllerà spesso lo stato di usura del cilindro', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '211', '7018', '1295', '6', 'Al fine di evitare il prodursi di incidenti stradali, il conducente controllerà spesso la rumorosità della marmitta', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '212', '7020', '', '', ' Cautele da adottare per evitare incidenti (2)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '212', '7020', '1296', '1', 'Per garantire la sicurezza della circolazione ed evitare incidenti è necessario, fra l''altro, ricordare che se la velocità raddoppia, lo spazio di frenatura aumenta quattro volte', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '212', '7020', '1297', '2', 'Per garantire la sicurezza della circolazione ed evitare incidenti è necessario, fra l''altro, controllare periodicamente lo stato delle pastiglie dei freni a disco e dei dischi', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '212', '7020', '1298', '3', 'Per garantire la sicurezza della circolazione ed evitare incidenti è necessario, fra l''altro, controllare periodicamente lo stato delle ganasce e dei tamburi nei freni a tamburo', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '212', '7020', '1299', '4', 'Per garantire la sicurezza della circolazione ed evitare incidenti è necessario, fra l''altro, evitare di surriscaldare i freni con l''uso prolungato', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '212', '7020', '1300', '5', 'Per garantire la sicurezza della circolazione ed evitare incidenti è necessario, fra l''altro, frenare a fondo su strada scivolosa', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '212', '7020', '1301', '6', 'Per garantire la sicurezza della circolazione ed evitare incidenti è necessario, fra l''altro, evitare qualsiasi manovra di frenatura su fondo sconnesso', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '212', '7020', '1302', '7', 'Per garantire la sicurezza della circolazione ed evitare incidenti è necessario, fra l''altro, controllare che la pressione del liquido dei freni sia bassa durante la marcia', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '213', '7021', '', '', 'Cautele da adottare per evitare incidenti (3)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '213', '7021', '1303', '1', 'Per garantire la sicurezza della circolazione dei veicoli ed evitare incidenti è necessario, fra l''altro, controllare periodicamente che la pressione dei pneumatici sia quella consigliata dalla casa', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '213', '7021', '1304', '2', 'Per garantire la sicurezza della circolazione dei veicoli ed evitare incidenti è necessario, fra l''altro, sostituire i pneumatici molto consumati perché rendono meno efficace la frenatura', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '213', '7021', '1305', '3', 'Per garantire la sicurezza della circolazione dei veicoli ed evitare incidenti è necessario, fra l''altro, osservare se il pneumatico si consuma soprattutto ai bordi, perché la pressione è troppo bassa', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '213', '7021', '1306', '4', 'Per garantire la sicurezza della circolazione dei veicoli ed evitare incidenti è necessario, fra l''altro, aumentare la pressione dei pneumatici per migliorarne l''aderenza', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '213', '7021', '1307', '5', 'Per garantire la sicurezza della circolazione dei veicoli ed evitare incidenti è necessario, fra l''altro, diminuire la pressione dei pneumatici per migliorare la stabilità del veicolo', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '213', '7021', '1308', '6', 'Per garantire la sicurezza della circolazione dei veicoli ed evitare incidenti è necessario, fra l''altro, diminuire la pressione dei pneumatici perché se ne migliora il raffreddamento', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '214', '7028', '', '', ' Cause di pericolo o intralcio alla circolazione', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '214', '7028', '1309', '1', 'Fermarsi a parlare in mezzo alla strada seduti sul ciclomotore può creare pericolo o intralcio alla circolazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '214', '7028', '1310', '2', 'Può creare pericolo o intralcio alla circolazione viaggiare affiancati', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '214', '7028', '1311', '3', 'Può creare pericolo o intralcio alla circolazione viaggiare a zig zag tra i veicoli', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '214', '7028', '1312', '4', 'Può creare pericolo o intralcio alla circolazione sostare fuori dalla strada', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '214', '7028', '1313', '5', 'Può creare pericolo o intralcio alla circolazione svoltare a destra con prudenza', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '214', '7028', '1314', '6', 'Può creare pericolo o intralcio alla circolazione fermarsi sul lato destro della strada', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '215', '7019', '', '', 'Cautele da adottare quando si guida un ciclomotore', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '215', '7019', '1315', '1', 'Conducendo un ciclomotore non bisogna avere indosso oggetti pericolosi in caso di caduta', 'V', '');
