INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '228', '7017', '1400', '6', 'In caso di traffico intenso, il conducente deve procedere velocemente a zig zag tra i veicoli', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '229', '7003', '', '', 'Comportamenti da tenere in presenza di una galleria', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '229', '7003', '1401', '1', 'In presenza di una galleria è necessario rallentare l''andatura prima di entrare', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '229', '7003', '1402', '2', 'In presenza di una galleria è necessario tenere saldo il manubrio per fronteggiare colpi di vento laterale all''uscita', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '229', '7003', '1403', '3', 'Di giorno, prima di entrare in una galleria, è necessario moderare la velocità per abituare gli occhi alla penombra', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '229', '7003', '1404', '4', 'In presenza di una galleria è necessario accendere sempre i fari abbaglianti', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '229', '7003', '1405', '5', 'In presenza di una galleria è necessario frenare bruscamente', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '229', '7003', '1406', '6', 'Di giorno, prima di entrare in una galleria, è necessario indossare gli occhiali da sole', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '230', '7016', '', '', 'Comportamenti da tenere quando si incrocia un veicolo con fari a luce abbagliante accesi', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '230', '7016', '1407', '1', 'Di notte, incrociando un veicolo con fari a luce abbagliante accesi, occorre procedere con prudenza, usando le luci anabbaglianti', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '230', '7016', '1408', '2', 'Di notte, incrociando un veicolo con fari a luce abbagliante accesi, occorre cercare di non venire abbagliati, distogliendo lo sguardo dal fascio luminoso', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '230', '7016', '1409', '3', 'Di notte, incrociando un veicolo con fari a luce abbagliante accesi, occorre rallentare adeguatamente ed eventualmente fermarsi', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '230', '7016', '1410', '4', 'Di notte, incrociando un veicolo con fari a luce abbagliante accesi, occorre procedere a nostra volta con i fari abbaglianti, onde vedere bene la strada', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '230', '7016', '1411', '5', 'Di notte, incrociando un veicolo con fari a luce abbagliante accesi, occorre fermarsi bruscamente', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '230', '7016', '1412', '6', 'Di notte, incrociando un veicolo con fari a luce abbagliante accesi, occorre cercare di ripararsi gli occhi con le mani', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '231', '7015', '', '', ' Comportamenti da tenere quando un pedone attraversa la strada fuori dalle strisce pedonali', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '231', '7015', '1413', '1', 'Quando un pedone attraversa la strada fuori dalle strisce pedonali è necessario ridurre la velocità ed eventualmente fermarsi', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '231', '7015', '1414', '2', 'Quando un pedone attraversa la strada fuori dalle strisce pedonali è necessario rallentare e avvisare il pedone con un breve colpo di clacson, in caso di pericolo immediato', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '231', '7015', '1415', '3', 'Quando un pedone attraversa la strada fuori dalle strisce pedonali è necessario ridurre la velocità fino a fermarsi tempestivamente, se occorre', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '231', '7015', '1416', '4', 'Quando un pedone attraversa la strada fuori dalle strisce pedonali è necessario, nelle ore notturne, procedere con gli abbaglianti accesi così che possa accorgersi di noi', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '231', '7015', '1417', '5', 'Quando un pedone attraversa la strada fuori dalle strisce pedonali è necessario cercare di richiamare la sua attenzione con qualsiasi mezzo, perché ci dia la precedenza', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '231', '7015', '1418', '6', 'Quando un pedone attraversa la strada fuori dalle strisce pedonali è necessario proseguire comunque perché abbiamo la precedenza', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '232', '7014', '', '', 'Comportamenti da tenere quando si sbaglia la corsia di preselezione ad un incrocio', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '232', '7014', '1419', '1', 'Quando, giunti in prossimità di un incrocio, ci accorgiamo di aver sbagliato la corsia di preselezione, procediamo nella direzione già presa senza arrestarci bruscamente', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '232', '7014', '1420', '2', 'Quando, giunti in prossimità di un incrocio, ci accorgiamo di aver sbagliato la corsia di preselezione, non effettuiamo bruschi cambiamenti di corsia per non intralciare i veicoli che seguono', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '232', '7014', '1421', '3', 'Quando, giunti in prossimità di un incrocio, ci accorgiamo di aver sbagliato la corsia di preselezione, non ci fermiamo a chiedere informazioni, per non ostacolare la circolazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '232', '7014', '1422', '4', 'Quando, giunti in prossimità di un incrocio, ci accorgiamo di aver sbagliato la corsia di preselezione, effettuiamo la manovra di svolta che intendevamo fare, dando però precedenza a tutti i veicoli', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '232', '7014', '1423', '5', 'Quando, giunti in prossimità di un incrocio, ci accorgiamo di aver sbagliato la corsia di preselezione, accostiamo sulla destra e ci fermiamo finché non sia passata la colonna dei veicoli che seguono', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '232', '7014', '1424', '6', 'Quando, giunti in prossimità di un incrocio, ci accorgiamo di aver sbagliato la corsia di preselezione, cerchiamo di retrocedere, con prudenza, fino al punto in cui possiamo cambiare corsia', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '233', '7029', '', '', 'Casi in cui è obbligatorio dare la precedenza a destra e a sinistra (1)', '', '');
