INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '234', '7030', '1433', '3', 'E'' obbligatorio dare la precedenza a destra e a sinistra in presenza del segnale di STOP', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '234', '7030', '1434', '4', 'E'' obbligatorio dare la precedenza a destra e a sinistra in presenza del segnale DIRITTO DI PRECEDENZA', 'F', '');
