INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '238', '7031', '1458', '4', 'La polizza di assicurazione per la responsabilità civile copre i danni provocati a persone e cose entro i massimali', 'V', '');
