INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '239', '7032', '', '', ' Documenti che l''impresa assicuratrice deve rilasciare', '', '');
