INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '239', '7032', '1465', '3', 'In relazione al contratto di assicurazione stipulato, l''impresa assicuratrice deve rilasciare al contraente il certificato di assicurazione da esibire a richiesta assieme al certificato di circolazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '239', '7032', '1466', '4', 'In relazione al contratto di assicurazione stipulato, l''impresa assicuratrice deve rilasciare al contraente un manuale completo sulle cautele da osservare per evitare incidenti', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '239', '7032', '1467', '5', 'In relazione al contratto di assicurazione stipulato, l''impresa assicuratrice deve rilasciare al contraente un estratto del codice civile contenente articoli che riguardano l''assicurazione di ciclomotori', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '239', '7032', '1468', '6', 'In relazione al contratto di assicurazione stipulato, l''impresa assicuratrice deve rilasciare al contraente un manuale di pronto soccorso', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '240', '7033', '', '', 'Conseguenze della scadenza dell''assicurazione per la responsabilità civile del ciclomotore', '', '');
