INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '240', '7033', '1471', '3', 'Quando l’assicurazione per la responsabilità civile del ciclomotore è scaduta di validità, si può incorrere nel sequestro del veicolo', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '240', '7033', '1472', '4', 'Quando l’assicurazione per la responsabilità civile del ciclomotore è scaduta di validità, si può continuare a guidare solo per brevi tragitti', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '240', '7033', '1473', '5', 'Quando l’assicurazione per la responsabilità civile del ciclomotore è scaduta di validità, si può guidare solo su strade extraurbane', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('7', '240', '7033', '1474', '6', 'Quando l’assicurazione per la responsabilità civile del ciclomotore è scaduta di validità, possono guidarlo solo i maggiorenni', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '241', '8025', '', '', 'Uso del casco (1)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '241', '8025', '1475', '1', 'Il casco va sostituito dopo un incidente con caduta', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '241', '8025', '1476', '2', 'Il casco va sostituito in ogni caso dopo aver subìto un forte urto', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '241', '8025', '1477', '3', 'Il casco deve avere la visiera priva di graffi', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '241', '8025', '1478', '4', 'Il casco deve essere di tipo omologato', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '241', '8025', '1479', '5', 'Il casco può essere modificato internamente per muoverlo più comodamente', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '241', '8025', '1480', '6', 'Il casco se ha subìto urti molto forti ma non è rotto si può riutilizzare', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '241', '8025', '1481', '7', 'Il casco d''estate può essere indossato con il cinturino slacciato', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '242', '8023', '', '', ' Uso del casco (2)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '242', '8023', '1482', '1', 'Si deve utilizzare un casco che si indossa perfettamente', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '242', '8023', '1483', '2', 'Si deve utilizzare un casco che non si muova, una volta indossato', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '242', '8023', '1484', '3', 'Si deve utilizzare un casco omologato secondo le norme europee', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '242', '8023', '1485', '4', 'Si deve utilizzare un casco di qualsiasi misura disponibile', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '242', '8023', '1486', '5', 'Si deve utilizzare un casco più largo e comodo possibile', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '242', '8023', '1487', '6', 'Si deve utilizzare un casco con almeno tre colori', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '243', '8026', '', '', ' Uso del casco (3)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '243', '8026', '1488', '1', 'Il casco deve essere allacciato in modo che non scivoli', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '243', '8026', '1489', '2', 'Il casco deve essere allacciato con il cinturino ben stretto', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '243', '8026', '1490', '3', 'Il casco deve essere allacciato in modo che sia ben stretto sulla testa', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '243', '8026', '1491', '4', 'Il casco deve essere allacciato non obbligatoriamente', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '243', '8026', '1492', '5', 'Il casco deve essere allacciato con il cinturino allentato, in modo che non ci opprima troppo', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '243', '8026', '1493', '6', 'Il casco deve essere allacciato sempre con il cinturino allentato', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '244', '8024', '', '', ' Uso del casco (4)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '244', '8024', '1494', '1', 'Per la propria sicurezza è necessario sostituire la visiera del casco quando comincia ad essere rigata', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '244', '8024', '1495', '2', 'Per la propria sicurezza è necessario che il cinturino del casco sia in buone condizioni', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '244', '8024', '1496', '3', 'Per la propria sicurezza è necessario sostituire la visiera del casco se rotta o graffiata', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '244', '8024', '1497', '4', 'Per la propria sicurezza è necessario continuare ad usare la visiera del casco anche se graffiata', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '244', '8024', '1498', '5', 'Per la propria sicurezza è necessario circolare con la visiera del casco completamente appannata', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '244', '8024', '1499', '6', 'Per la propria sicurezza è necessario non pulire mai la visiera del casco', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '245', '8022', '', '', 'Obbligo di indossare il casco', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '245', '8022', '1500', '1', 'L''uso del casco è obbligatorio per i conducenti di ciclomotori a due ruote', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '245', '8022', '1501', '2', 'L''uso del casco è obbligatorio per limitare le lesioni alla testa in caso di incidente', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '245', '8022', '1502', '3', 'L''uso del casco è obbligatorio per il passeggero del ciclomotore', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '245', '8022', '1503', '4', 'L''uso del casco è obbligatorio per chi guida ciclomotori a due ruote, anche se ha compiuto 18 anni', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '245', '8022', '1504', '5', 'L''uso del casco è obbligatorio solo nel periodo invernale', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '245', '8022', '1505', '6', 'L''uso del casco è obbligatorio per i conducenti di ciclomotori solo fino a 16 anni di età', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '245', '8022', '1506', '7', 'L''uso del casco è obbligatorio solo per i conducenti di tricicli a motore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '246', '8027', '', '', 'Guida del ciclomotore senza casco', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '246', '8027', '1507', '1', 'Se il conducente è minorenne, viaggiare sul ciclomotore senza casco comporta il fermo del veicolo', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '246', '8027', '1508', '2', 'Viaggiare sul ciclomotore con casco e visiera alzata, ci espone al rischio di fastidiosi impatti con insetti', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '246', '8027', '1509', '3', 'In caso di incidente, viaggiare sul ciclomotore senza casco può comportare gravi lesioni al capo', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '246', '8027', '1510', '4', 'Viaggiare sul ciclomotore senza casco é consentito su strade rettilinee', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '246', '8027', '1511', '5', 'Viaggiare sul ciclomotore senza casco é consentito solo ai maggiorenni', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '246', '8027', '1512', '6', 'Viaggiare sul ciclomotore senza casco non é rischioso', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '247', '8001', '', '', ' Obbligo di usare i proiettori a luce anabbagliante', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '247', '8001', '1513', '1', 'Durante la marcia dei ciclomotori è sempre obbligatorio l''uso dei proiettori a luce anabbagliante, in ogni condizione di tempo', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '247', '8001', '1514', '2', 'Durante, la marcia dei ciclomotori è sempre obbligatorio l''uso dei proiettori a luce anabbagliante, anche durante il giorno', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '247', '8001', '1515', '3', 'Durante la marcia dei ciclomotori è sempre obbligatorio l''uso dei proiettori a luce anabbagliante, sia di giorno che di notte', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '247', '8001', '1516', '4', 'Durante la marcia dei ciclomotori è sempre obbligatorio l''uso dei proiettori a luce anabbagliante, sia nei centri abitati che fuori', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '247', '8001', '1517', '5', 'Durante la marcia dei ciclomotori è obbligatorio l''uso dei proiettori a luce anabbagliante solo se la pubblica illuminazione manca o non è sufficiente', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '247', '8001', '1518', '6', 'Durante la marcia dei ciclomotori è sempre obbligatorio l''uso dei proiettori a luce anabbagliante in sostituzione dei proiettori di profondità', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '247', '8001', '1519', '7', 'Durante la marcia dei ciclomotori è obbligatorio l''uso dei proiettori a luce anabbagliante solo di notte', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '248', '8005', '', '', 'Casi in cui è vietato l''uso dei proiettori a luce abbagliante', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '248', '8005', '1520', '1', 'Durante la marcia, l''uso dei proiettori a luce abbagliante è vietato quando si incrociano altri veicoli', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '248', '8005', '1521', '2', 'Durante la marcia, l''uso dei proiettori a luce abbagliante è vietato quando si segue un veicolo a breve distanza', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '248', '8005', '1522', '3', 'Durante la marcia, l''uso dei proiettori a luce abbagliante è vietato quando c''è il pericolo di abbagliare gli altri utenti della strada', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '248', '8005', '1523', '4', 'Durante la marcia, l''uso dei proiettori a luce abbagliante è vietato nei centri abitati', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '248', '8005', '1524', '5', 'Durante la marcia, l''uso dei proiettori a luce abbagliante è vietato mezz''ora prima del sorgere del sole', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '248', '8005', '1525', '6', 'Durante la marcia, l''uso dei proiettori a luce abbagliante è vietato solo quando il veicolo che ci precede è un''autovettura', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '248', '8005', '1526', '7', 'Durante la marcia, l''uso dei proiettori a luce abbagliante è vietato nei centri abitati, solo in caso di pioggia', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '249', '8006', '', '', 'Casi in cui è permesso lampeggiare con le luci abbaglianti', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '249', '8006', '1527', '1', 'E'' permesso lampeggiare con le luci abbaglianti per dare avvertimenti utili al fine di evitare incidenti', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '249', '8006', '1528', '2', 'E'' permesso lampeggiare con le luci abbaglianti per segnalare al veicolo che precede l''intenzione di sorpassare', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '249', '8006', '1529', '3', 'Di giorno, nei centri abitati, è permesso lampeggiare con le luci abbaglianti in sostituzione del clacson', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '249', '8006', '1530', '4', 'E'' permesso lampeggiare con le luci abbaglianti solo nelle gallerie', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '249', '8006', '1531', '5', 'E'' permesso lampeggiare con le luci abbaglianti purché l''illuminazione esterna manchi o sia insufficiente', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '249', '8006', '1532', '6', 'Di giorno, è permesso lampeggiare con le luci abbaglianti solo nei casi di pericolo immediato', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '250', '8003', '', '', ' Casi in cui è obbligatorio l''uso degli indicatori di direzione', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '250', '8003', '1533', '1', 'L''uso degli indicatori di direzione è obbligatorio per segnalare l''intenzione di sorpassare', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '250', '8003', '1534', '2', 'L''uso degli indicatori di direzione è obbligatorio per segnalare che si vuole cambiare corsia', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '250', '8003', '1535', '3', 'L''uso degli indicatori di direzione è obbligatorio ogni qualvolta si deve fare una manovra di svolta', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '250', '8003', '1536', '4', 'L''uso degli indicatori di direzione è obbligatorio quando ci si arresta al semaforo', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '250', '8003', '1537', '5', 'L''uso degli indicatori di direzione è obbligatorio nella marcia per file parallele, anche se non si cambia corsia', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '250', '8003', '1538', '6', 'L''uso degli indicatori di direzione è obbligatorio solo quando si svolta a sinistra', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '251', '8004', '', '', ' Comportamenti da adottare se sul ciclomotore non funzionano gli indicatori di direzione', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '251', '8004', '1539', '1', 'Se sul ciclomotore non funzionano gli indicatori di direzione si può proseguire sporgendo lateralmente il braccio prima di svoltare', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '251', '8004', '1540', '2', 'Se sul ciclomotore non funzionano gli indicatori di direzione bisogna provvedere alla riparazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '251', '8004', '1541', '3', 'Se sul ciclomotore non funzionano gli indicatori di direzione, per indicare l''intenzione di cambiare corsia occorre far segno con il braccio', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '251', '8004', '1542', '4', 'Se sul ciclomotore non funzionano gli indicatori di direzione non si deve proseguire in nessun caso', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '251', '8004', '1543', '5', 'Se sul ciclomotore non funzionano gli indicatori di direzione, prima di svoltare occorre alzare verticalmente il braccio', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '251', '8004', '1544', '6', 'Se sul ciclomotore non funzionano gli indicatori di direzione, prima di svoltare bisogna suonare il clacson', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '252', '8002', '', '', 'Funzione dei catadiottri', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '252', '8002', '1545', '1', 'I catadiottri sono dispositivi che riflettono la luce e hanno la funzione di indicare, specialmente di notte, la presenza di veicoli, anche se a luci spente', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '252', '8002', '1546', '2', 'I catadiottri sono dispositivi che riflettono la luce e hanno la funzione di facilitare la visibilità notturna di rimorchi isolati parcheggiati su strada', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '252', '8002', '1547', '3', 'I catadiottri sono dispositivi che riflettono la luce e hanno la funzione di segnalare la presenza del veicolo, diventando luminosi se illuminati da fari', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '252', '8002', '1548', '4', 'I catadiottri sono dispositivi che riflettono la luce e hanno la funzione di far individuare il veicolo nel caso in cui non funzionino le luci posteriori', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '252', '8002', '1549', '5', 'I catadiottri sono dispositivi che riflettono la luce e hanno la funzione di segnalare, se rossi e triangolari, la presenza di un quadriciclo a motore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '252', '8002', '1550', '6', 'I catadiottri sono dispositivi che riflettono la luce e hanno la funzione di sostituire gli indicatori di direzione se questi non funzionano', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '253', '8018', '', '', 'Regolazione degli specchietti retrovisori del ciclomotore', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '253', '8018', '1551', '1', 'Si devono regolare gli specchietti retrovisori del ciclomotore dopo che si è indossato il casco', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '253', '8018', '1552', '2', 'Si devono regolare gli specchietti retrovisori del ciclomotore dopo che si è tolto il cavalletto', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '253', '8018', '1553', '3', 'Si devono regolare gli specchietti retrovisori del ciclomotore prima di partire', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '253', '8018', '1554', '4', 'Si devono regolare gli specchietti retrovisori del ciclomotore prima di togliere il cavalletto', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '253', '8018', '1555', '5', 'Si devono regolare gli specchietti retrovisori del ciclomotore dopo aver già percorso un tratto di strada', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '254', '8021', '', '', 'Cautele da adottare per evitare incidenti', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '254', '8021', '1556', '1', 'Per garantire la sicurezza della circolazione ed evitare incidenti è necessario, fra l''altro effettuare le revisioni periodiche del ciclomotore previste dalla legge', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '254', '8021', '1557', '2', 'Per garantire la sicurezza della circolazione ed evitare incidenti è necessario, fra l''altro mantenere efficienti i dispositivi di frenatura e di illuminazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '254', '8021', '1558', '3', 'Per garantire la sicurezza della circolazione ed evitare incidenti è necessario, fra l''altro far controllare periodicamente, ad un meccanico, l''efficienza del ciclomotore', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '254', '8021', '1559', '4', 'Per garantire la sicurezza della circolazione ed evitare incidenti è necessario, fra l''altro togliere gli specchietti retrovisori', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '254', '8021', '1560', '5', 'Per garantire la sicurezza della circolazione ed evitare incidenti è necessario, fra l''altro sostituire le lampadine con altre di diverso colore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '254', '8021', '1561', '6', 'Per garantire la sicurezza della circolazione ed evitare incidenti è necessario, fra l''altro, tenere le luci spente durante il giorno', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '254', '8021', '1562', '7', 'Per garantire la sicurezza della circolazione ed evitare incidenti è necessario, fra l''altro affiancare un altro ciclomotore per parlare con il conducente', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '255', '8007', '', '', ' Uso del clacson', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '255', '8007', '1563', '1', 'Il clacson non può essere usato per motivi diversi da quelli legati alla circolazione stradale', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '255', '8007', '1564', '2', 'Nei centri abitati non si può usare il clacson, salvo casi di pericolo immediato', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '255', '8007', '1565', '3', 'Il clacson, quando è necessario, deve essere usato con moderazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '255', '8007', '1566', '4', 'Il clacson può essere usato fuori dai centri abitati se le circostanze lo rendono consigliabile', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '255', '8007', '1567', '5', 'Il clacson può essere sempre usato in prossimità di un ospedale', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '255', '8007', '1568', '6', 'Fuori dai centri abitati, si deve sempre usare il clacson se si intende sorpassare', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '255', '8007', '1569', '7', 'Il clacson non può mai essere usato nei centri abitati, né di giorno né di notte', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '255', '8007', '1570', '8', 'Si deve sempre usare il clacson in prossimità degli attraversamenti pedonali', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '256', '8010', '', '', ' Battistrada dei pneumatici dei ciclomotori', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '256', '8010', '1571', '1', 'Il battistrada dei pneumatici dei ciclomotori non deve essere liscio', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '256', '8010', '1572', '2', 'Il battistrada dei pneumatici dei ciclomotori deve essere verificato frequentemente', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '256', '8010', '1573', '3', 'Il battistrada dei pneumatici dei ciclomotori deve avere uno spessore di almeno 0,5 mm', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '256', '8010', '1574', '4', 'Il battistrada dei pneumatici dei ciclomotori deve avere uno spessore di almeno 2,5 mm', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '256', '8010', '1575', '5', 'Il battistrada dei pneumatici dei ciclomotori deve avere uno spessore di almeno 1 mm', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '256', '8010', '1576', '6', 'Il battistrada dei pneumatici dei ciclomotori non influisce sulla sicurezza', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '257', '8009', '', '', 'Pneumatici con battistrada eccessivamente consumato', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '257', '8009', '1577', '1', 'I pneumatici con battistrada eccessivamente consumato aumentano la possibilità di slittamento in curva', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '257', '8009', '1578', '2', 'I pneumatici con battistrada eccessivamente consumato si forano più facilmente', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '257', '8009', '1579', '3', 'I pneumatici con battistrada eccessivamente consumato provocano l''aumento dello spazio di frenatura del veicolo', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '257', '8009', '1580', '4', 'I pneumatici con battistrada eccessivamente consumato non influiscono nella frenatura del veicolo', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '257', '8009', '1581', '5', 'I pneumatici con battistrada eccessivamente consumato aumentano l''aderenza', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '257', '8009', '1582', '6', 'I pneumatici con battistrada eccessivamente consumato sono pericolosi soltanto in caso di pioggia', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '258', '8008', '', '', 'Pneumatici che presentano lesioni sui fianchi', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '258', '8008', '1583', '1', 'I pneumatici che presentano lesioni sui fianchi devono sempre essere sostituiti', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '258', '8008', '1584', '2', 'I pneumatici che presentano lesioni sui fianchi possono sgonfiarsi improvvisamente', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '258', '8008', '1585', '3', 'I pneumatici che presentano lesioni sui fianchi costituiscono un grave pericolo durante la circolazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '258', '8008', '1586', '4', 'I pneumatici che presentano lesioni sui fianchi possono essere usati a basse velocità', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '258', '8008', '1587', '5', 'I pneumatici che presentano lesioni sui fianchi debbono essere subito ricostruiti e ricoperti', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '258', '8008', '1588', '6', 'I pneumatici che presentano lesioni sui fianchi possono essere usati senza problemi', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '259', '8011', '', '', 'Pneumatici con pressione inferiore a quella consigliata dalla casa costruttrice', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '259', '8011', '1589', '1', 'Circolando con pneumatici a pressione inferiore a quella consigliata dalla casa costruttrice si ha maggior consumo di carburante', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '259', '8011', '1590', '2', 'Circolando con pneumatici a pressione inferiore a quella consigliata dalla casa costruttrice il battistrada si consuma maggiormente ai lati', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '259', '8011', '1591', '3', 'Circolando con pneumatici a pressione inferiore a quella consigliata dalla casa costruttrice, in caso di strada bagnata, aumenta il pericolo di aquaplaning', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '259', '8011', '1592', '4', 'Circolando con pneumatici a pressione inferiore a quella consigliata dalla casa costruttrice è minore la stabilità del veicolo', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '259', '8011', '1593', '5', 'Circolando con pneumatici a pressione inferiore a quella consigliata dalla casa costruttrice si consumano maggiormente nella loro parte centrale', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '259', '8011', '1594', '6', 'Circolando con pneumatici a pressione inferiore a quella consigliata dalla casa costruttrice si ottiene una migliore prestazione del veicolo', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '260', '8014', '', '', 'Controlli da effettuare per mantenere efficienti i freni', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '260', '8014', '1595', '1', 'Per mantenere i freni efficienti è necessario sottoporre a periodici controlli il consumo degli elementi frenanti', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '260', '8014', '1596', '2', 'Per mantenere i freni efficienti è necessario sottoporre a periodici controlli il livello dell''olio dei freni', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '260', '8014', '1597', '3', 'Per mantenere i freni efficienti è necessario sottoporre a periodici controlli la corsa della leva che li comanda', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '260', '8014', '1598', '4', 'Per mantenere i freni efficienti è necessario sottoporre a periodici controlli i cerchioni delle ruote', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '260', '8014', '1599', '5', 'Per mantenere i freni efficienti è necessario sottoporre a periodici controlli la composizione chimica del liquido idraulico dei freni', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '260', '8014', '1600', '6', 'Per mantenere i freni efficienti è necessario sottoporre a periodici controlli la struttura del pneumatico', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '261', '8015', '', '', 'Frenata con un ciclomotore su una superficie stradale asciutta', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '261', '8015', '1601', '1', 'Quando si frena con un ciclomotore su una superficie stradale asciutta bisogna utilizzare sia il freno anteriore che quello posteriore', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '261', '8015', '1602', '2', 'Quando si frena con un ciclomotore su una superficie stradale asciutta bisogna azionare entrambi i comandi dei freni', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '261', '8015', '1603', '3', 'Quando si frena con un ciclomotore su una superficie stradale asciutta bisogna evitare sempre frenate brusche', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '261', '8015', '1604', '4', 'Quando si frena con un ciclomotore su una superficie stradale asciutta bisogna utilizzare solo il freno più efficiente', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '261', '8015', '1605', '5', 'Quando si frena con un ciclomotore su una superficie stradale asciutta bisogna utilizzare solo il freno anteriore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '261', '8015', '1606', '6', 'Quando si frena con un ciclomotore su una superficie stradale asciutta basta utilizzare un solo freno', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '262', '8016', '', '', 'Frenata di emergenza con il ciclomotore', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '262', '8016', '1607', '1', 'In caso di frenata di emergenza con il ciclomotore si utilizzano entrambi i freni', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '262', '8016', '1608', '2', 'In caso di frenata di emergenza con il ciclomotore è consigliabile spostare il proprio peso all’indietro', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '262', '8016', '1609', '3', 'In caso di frenata di emergenza con il ciclomotore bisogna frenare a fondo, evitando il bloccaggio delle ruote', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '262', '8016', '1610', '4', 'In caso di frenata di emergenza con il ciclomotore si frena esclusivamente con il freno anteriore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '262', '8016', '1611', '5', 'In caso di frenata di emergenza con il ciclomotore si frena esclusivamente con il freno posteriore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '262', '8016', '1612', '6', 'In caso di frenata di emergenza con il ciclomotore si frena a scatti per bloccare le ruote', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '263', '8017', '', '', ' Comportamenti da adottare se il ciclomotore in fase di frenatura tende a sbandare', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '263', '8017', '1613', '1', 'Se il ciclomotore in fase di frenatura tende a sbandare, il conducente deve ridurre lo sforzo frenante', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '263', '8017', '1614', '2', 'Se il ciclomotore in fase di frenatura tende a sbandare, il conducente deve procedere con cautela e portarlo in officina per la messa a punto', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '263', '8017', '1615', '3', 'Se il ciclomotore in fase di frenatura tende a sbandare, il conducente deve controllare la pressione di gonfiaggio dei pneumatici', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '263', '8017', '1616', '4', 'Se il ciclomotore in fase di frenatura tende a sbandare, il conducente deve restare padrone di sé stesso', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '263', '8017', '1617', '5', 'Se il ciclomotore in fase di frenatura tende a sbandare, il conducente deve controllare il livello dell''olio del motore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '263', '8017', '1618', '6', 'Se il ciclomotore in fase di frenatura tende a sbandare, il conducente deve mantenerlo frenato a fondo e controllarne l''andamento con rapide sterzate', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '263', '8017', '1619', '7', 'Se il ciclomotore in fase di frenatura tende a sbandare, il conducente deve lasciare bruscamente l''acceleratore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '264', '8012', '', '', ' Controlli da effettuare frequentemente sul ciclomotore', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '264', '8012', '1620', '1', 'Occorre verificare frequentemente la pressione di gonfiaggio dei pneumatici', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '264', '8012', '1621', '2', 'Occorre verificare frequentemente che la pressione dei pneumatici sia quella indicata dal costruttore', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '264', '8012', '1622', '3', 'Occorre verificare frequentemente l''efficienza dell''impianto di illuminazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '264', '8012', '1623', '4', 'Occorre verificare frequentemente l''efficienza dell''impianto frenante', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '264', '8012', '1624', '5', 'Occorre verificare frequentemente che i pneumatici siano sempre leggermente sgonfi', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '264', '8012', '1625', '6', 'Occorre verificare frequentemente che i pneumatici siano più gonfi di quanto indicato dal costruttore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '265', '8019', '', '', ' Operazioni da effettuare prima di partire con il ciclomotore', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '265', '8019', '1626', '1', 'Prima di partire con il ciclomotore è necessario controllare il regolare funzionamento dei comandi dei freni', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '265', '8019', '1627', '2', 'Prima di partire con il ciclomotore è necessario togliere l’antifurto, se inserito', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '265', '8019', '1628', '3', 'Prima di partire con il ciclomotore è necessario controllare che lo sterzo giri libero', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '265', '8019', '1629', '4', 'Prima di partire con il ciclomotore è necessario regolare gli specchi retrovisori', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '265', '8019', '1630', '5', 'Prima di partire con il ciclomotore è necessario controllare il funzionamento delle luci', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '265', '8019', '1631', '6', 'Prima di partire con il ciclomotore è necessario controllare la candela', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '265', '8019', '1632', '7', 'Prima di partire con il ciclomotore è necessario indossare sempre gli occhiali da sole', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '265', '8019', '1633', '8', 'Prima di partire con il ciclomotore è necessario girare la manopola dell''acceleratore, anche a motore spento', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '266', '8020', '', '', 'Comportamenti vietati su un ciclomotore', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '266', '8020', '1634', '1', 'E'' vietato modificare il ciclomotore per renderlo più veloce', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '266', '8020', '1635', '2', 'E'' vietato truccare il dispositivo silenziatore', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '266', '8020', '1636', '3', 'E'' vietato modificare il motore del ciclomotore per consentirgli di superare i 45 km/h', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '266', '8020', '1637', '4', 'E'' vietato circolare con il ciclomotore sollevando da terra la ruota anteriore', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '266', '8020', '1638', '5', 'E'' vietato circolare di giorno su un ciclomotore tenendo i fari anabbaglianti accesi', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '266', '8020', '1639', '6', 'E'' vietato spegnere il motore durante il rifornimento di carburante', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '267', '8013', '', '', ' Controlli da effettuare frequentemente sul ciclomotore', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '267', '8013', '1640', '1', 'Il proprietario di un ciclomotore ha l''obbligo di effettuare la revisione periodica al proprio veicolo a quattro anni dalla immissione in circolazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '267', '8013', '1641', '2', 'Il proprietario di un ciclomotore ha l''obbligo di effettuare la revisione periodica al proprio veicolo ogni due anni successivamente alla prima revisione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '267', '8013', '1642', '3', 'Il proprietario di un ciclomotore ha l''obbligo di effettuare la revisione periodica al proprio veicolo ogni anno', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('8', '267', '8013', '1643', '4', 'Il proprietario di un ciclomotore ha l''obbligo di effettuare la revisione periodica al proprio veicolo ad ogni nuova immatricolazione', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '268', '9001', '', '', 'Condizioni richieste per circolare con un ciclomotore', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '268', '9001', '1644', '1', 'Se si è minorenni e non si ha la patente A1, per poter circolare con un ciclomotore occorre la patente AM', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '268', '9001', '1645', '2', 'Per poter circolare con un ciclomotore occorre aver compiuto il quattordicesimo anno di età', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '268', '9001', '1646', '3', 'Per poter circolare con un ciclomotore occorre portare con sé il certificato di circolazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '268', '9001', '1647', '4', 'Per poter circolare con un ciclomotore occorre portare con sé il certificato di assicurazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '268', '9001', '1648', '5', 'Per poter circolare con un ciclomotore occorre aver conseguito almeno la patente A1', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '268', '9001', '1649', '6', 'Per poter circolare con un ciclomotore occorre portare con sé la ricevuta di pagamento del ciclomotore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '269', '9003', '', '', 'Patente di categoria AM (1)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '269', '9003', '1650', '1', 'La patente AM si può conseguire a partire dai 14 anni', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '269', '9003', '1651', '2', 'La patente AM è necessaria, per i minorenni che non abbiano conseguito la patente A1, per poter condurre qualsiasi tipo di ciclomotore', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '269', '9003', '1652', '3', 'Per condurre un ciclomotore non è necessaria la patente AM, se si è titolari della patente A1', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '269', '9003', '1653', '4', 'La patente AM è indispensabile solo se si vuole condurre un ciclomotore con a bordo un passeggero', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '269', '9003', '1654', '5', 'Se si guida un quadriciclo leggero a motore e si è minorenni non è necessaria alcuna patente', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '269', '9003', '1655', '6', 'La patente AM deve essere rinnovata ogni 2 anni', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '270', '9004', '', '', 'Patente di categoria AM (2)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '270', '9004', '1656', '1', 'La patente AM viene rilasciata dopo aver superato un esame teorico e uno pratico', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '270', '9004', '1657', '2', 'Con la patente AM si possono condurre ciclomotori a tre o quattro ruote, anche se si è minorenni', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '270', '9004', '1658', '3', 'La patente AM deve essere portata con sé quando si è alla guida di un ciclomotore e non si possiede altra patente', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '270', '9004', '1659', '4', 'La patente AM è necessaria solo per trasportare un passeggero sul ciclomotore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '270', '9004', '1660', '5', 'La patente AM consente, solo in Italia, di condurre anche motocicli fino a 125 cm3 di cilindrata senza passeggeri a bordo', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '271', '9002', '', '', 'Documenti che chi guida un ciclomotore deve esibire agli agenti', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '271', '9002', '1661', '1', 'Chi guida ciclomotori deve esibire, a richiesta degli agenti, il certificato di circolazione del veicolo', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '271', '9002', '1662', '2', 'Chi guida ciclomotori deve esibire, a richiesta degli agenti, la patente AM o una patente di categoria superiore', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '271', '9002', '1663', '3', 'Chi guida ciclomotori deve esibire, a richiesta degli agenti, il certificato di assicurazione del veicolo', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '271', '9002', '1664', '4', 'Chi guida ciclomotori deve esibire, a richiesta degli agenti, il libretto di uso e manutenzione del veicolo', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '271', '9002', '1665', '5', 'Chi guida ciclomotori deve esibire, a richiesta degli agenti, l’atto di acquisto del veicolo', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '271', '9002', '1666', '6', 'Chi guida ciclomotori deve esibire, a richiesta degli agenti, il proprio codice fiscale', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '272', '9005', '', '', ' Posizione dei ciclomotori sulla carreggiata', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '272', '9005', '1667', '1', 'I ciclomotori devono circolare vicino al margine destro della strada', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '272', '9005', '1668', '2', 'Su strada a due corsie e a doppio senso, i ciclomotori possono usare la corsia di sinistra per il sorpasso', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '272', '9005', '1669', '3', 'Su strade a tre corsie per senso di marcia, durante la marcia normale, i ciclomotori devono percorrere la corsia di destra', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '272', '9005', '1670', '4', 'Su strada a due corsie e a doppio senso, i ciclomotori possono utilizzare per il sorpasso tutte e due le corsie', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '272', '9005', '1671', '5', 'Quando una strada è a tre corsie e a doppio senso, i ciclomotori possono circolare per file parallele', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '272', '9005', '1672', '6', 'I ciclomotori possono circolare al centro della strada quando è libera', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '273', '9006', '', '', 'Casi in cui i ciclomotori devono marciare il più possibile vicino al margine destro della strada', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '273', '9006', '1673', '1', 'Su strada a doppio senso di marcia a due corsie, i ciclomotori devono marciare il più possibile vicino al margine destro della strada quando incrociano altri veicoli', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '273', '9006', '1674', '2', 'Su strada a due corsie a doppio senso di circolazione, i ciclomotori devono marciare il più possibile vicino al margine destro della strada quando percorrono una curva', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '273', '9006', '1675', '3', 'Su strada a due corsie a due sensi di marcia, i ciclomotori devono marciare il più possibile vicino al margine destro della strada quando percorrono un dosso', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '273', '9006', '1676', '4', 'Su strada a senso unico, i ciclomotori devono marciare il più possibile vicino al margine destro della strada quando percorrono una curva', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '273', '9006', '1677', '5', 'I ciclomotori devono marciare il più possibile vicino al margine destro della strada quando devono svoltare a sinistra', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '273', '9006', '1678', '6', 'I ciclomotori devono marciare il più possibile vicino al margine destro della strada quando sorpassano un altro ciclomotore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '274', '9010', '', '', ' Obblighi alla guida di un ciclomotore', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '274', '9010', '1679', '1', 'Chi guida un ciclomotore deve dare di norma la precedenza negli incroci anche ai veicoli lenti, se provengono da destra', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '274', '9010', '1680', '2', 'Chi guida un ciclomotore deve evitare di costituire pericolo per gli altri utenti della strada', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '274', '9010', '1681', '3', 'Chi guida un ciclomotore deve guardare nello specchietto retrovisore prima di segnalare l''intenzione di cambiare corsia', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '274', '9010', '1682', '4', 'Chi guida un ciclomotore deve tenere in funzione gli anabbaglianti anche di giorno', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '274', '9010', '1683', '5', 'Chi guida un ciclomotore deve guidare sempre al centro della strada, però senza superare la striscia di corsia', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '274', '9010', '1684', '6', 'Chi guida un ciclomotore deve procedere a zig zag per superare i veicoli incolonnati', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '274', '9010', '1685', '7', 'Chi guida un ciclomotore deve procedere a velocità elevata per non creare intralcio', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '275', '9020', '', '', 'Cautele da avere alla guida di un ciclomotore', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '275', '9020', '1686', '1', 'Guidando un ciclomotore occorre fare attenzione all''apertura improvvisa delle portiere dei veicoli', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '275', '9020', '1687', '2', 'Guidando un ciclomotore è opportuno avere la visiera o gli occhiali protettivi', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '275', '9020', '1688', '3', 'Guidando un ciclomotore è opportuno non indossare indumenti svolazzanti', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '275', '9020', '1689', '4', 'Guidando un ciclomotore é opportuno indossare abiti che consentano libertà di movimento', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '275', '9020', '1690', '5', 'Guidando un ciclomotore è opportuno indossare sandali o ciabatte', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '275', '9020', '1691', '6', 'Guidando un ciclomotore è opportuno indossare indumenti svolazzanti per essere più visibili', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '275', '9020', '1692', '7', 'Guidando un ciclomotore, quando si giunge ad un semaforo, bisogna superare i veicoli fermi con la massima velocità consentita', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '275', '9020', '1693', '8', 'Guidando un ciclomotore, si può anche oltrepassare la striscia continua, se questo serve a superare i veicoli incolonnati', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '276', '9013', '', '', 'Comportamenti alla guida di un ciclomotore (1)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '276', '9013', '1694', '1', 'Alla guida di un ciclomotore è vietato parlare al telefono cellulare senza l''auricolare', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '276', '9013', '1695', '2', 'Alla guida di un ciclomotore è obbligatorio indossare il casco', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '276', '9013', '1696', '3', 'Alla guida di un ciclomotore è necessario guidare con prudenza', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '276', '9013', '1697', '4', 'Alla guida di un ciclomotore è vietato circolare sulle autostrade', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '276', '9013', '1698', '5', 'Alla guida di un ciclomotore si possono usare le cuffie per sentire la radio', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '276', '9013', '1699', '6', 'Alla guida di un ciclomotore è possibile trainare il ciclomotore in panne di un amico', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '276', '9013', '1700', '7', 'Alla guida di un ciclomotore si può sempre trasportare un passeggero', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '276', '9013', '1701', '8', 'Alla guida di un ciclomotore è possibile farsi trainare', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '277', '9022', '', '', 'Comportamenti alla guida di un ciclomotore (2) ', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '277', '9022', '1702', '1', 'Conducendo un ciclomotore bisogna essere tolleranti di fronte ai comportamenti errati degli altri utenti', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '277', '9022', '1703', '2', 'Conducendo un ciclomotore bisogna sforzarsi di prevedere il comportamento degli altri utenti', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '277', '9022', '1704', '3', 'Conducendo un ciclomotore bisogna evitare di fumare perché impedisce una presa sicura del manubrio', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '277', '9022', '1705', '4', 'Conducendo un ciclomotore l''uso di cuffie sonore migliora l''attenzione alla guida', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '277', '9022', '1706', '5', 'Conducendo un ciclomotore è consentito trasportare animali sul pianale poggiapiedi', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '277', '9022', '1707', '6', 'Conducendo un ciclomotore bisogna leggere con attenzione i cartelloni pubblicitari ', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '278', '9025', '', '', ' Comportamenti alla guida di un ciclomotore (3)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '278', '9025', '1708', '1', 'Il conducente di un ciclomotore deve evitare di prestare il veicolo a chi non ha i requisiti necessari per la guida', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '278', '9025', '1709', '2', 'Il conducente di un ciclomotore deve ricordare che la marmitta, surriscaldandosi, può provocare gravi ustioni', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '278', '9025', '1710', '3', 'Il conducente di un ciclomotore deve fare in modo che la targa sia sempre ben leggibile', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '278', '9025', '1711', '4', 'Il conducente di un ciclomotore non deve tenere acceso il motore nei locali chiusi', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '278', '9025', '1712', '5', 'Il conducente di un ciclomotore deve tenere presente che, con il casco indossato, si sentono meglio i rumori esterni', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '278', '9025', '1713', '6', 'Il conducente di un ciclomotore deve segnalare l''intenzione di svoltare solo se transitano altri veicoli', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('9', '278', '9025', '1714', '7', 'Il conducente di un ciclomotore deve spostarsi a zig zag tra le corsie per raggiungere più rapidamente l''incrocio', 'F', '');
