INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '303', '10033', '1870', '4', 'Gli effetti dell’alcool sul sistema nervoso vengono amplificati se subito dopo l’assunzione si beve un bicchiere d’acqua molto fredda', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '303', '10033', '1871', '5', 'Gli effetti dell’alcool sul sistema nervoso vengono amplificati se si beve lentamente', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '303', '10033', '1872', '6', 'Gli effetti dell’alcool sul sistema nervoso vengono amplificati se si fuma una sigaretta', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '304', '10014', '', '', 'Effetti dell''assunzione di stupefacenti sulla guida', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '304', '10014', '1873', '1', 'L''assunzione di stupefacenti influisce sulla guida perché altera le percezioni (vista, udito, riflessi)', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '304', '10014', '1874', '2', 'L''assunzione di stupefacenti influisce sulla guida perché può causare sonnolenza od eccitazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '304', '10014', '1875', '3', 'L''assunzione di stupefacenti influisce sulla guida perché altera i riflessi e la coordinazione dei movimenti', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '304', '10014', '1876', '4', 'L''assunzione di stupefacenti influisce sulla guida perché la rende più sicura', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '304', '10014', '1877', '5', 'L''assunzione di stupefacenti influisce sulla guida perché migliora l''autocontrollo', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '304', '10014', '1878', '6', 'L''assunzione di stupefacenti influisce sulla guida perché migliora la capacità visiva', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '305', '10032', '', '', ' Pericoli derivanti dall''uso di droghe', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '305', '10032', '1879', '1', 'L''uso di droghe, anche se fatto una sola volta, è pericoloso perché può causare imprudenza nella guida', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '305', '10032', '1880', '2', 'L''uso di droghe, anche se fatto una sola volta, è pericoloso perché può far durare lo stato confusionale per parecchie ore', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '305', '10032', '1881', '3', 'L''uso di droghe, anche se fatto una sola volta, è pericoloso perché può modificare, anche temporaneamente, la percezione della realtà', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '305', '10032', '1882', '4', 'L''uso di droghe, anche se fatto una sola volta, è pericoloso perché può causare disturbi dell’attenzione e della concentrazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '305', '10032', '1883', '5', 'L''uso di droghe, anche se fatto una sola volta, è pericoloso perché aumenta la capacità di giudizio e il senso di sicurezza', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '305', '10032', '1884', '6', 'L''uso di droghe, anche se fatto una sola volta, è pericoloso perché si incorre nella confisca del veicolo', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '305', '10032', '1885', '7', 'L''uso di droghe, anche se fatto una sola volta, è pericoloso perché si incorre nel fermo amministrativo del veicolo', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '306', '10016', '', '', 'Comportamenti vietati sulla strada', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '306', '10016', '1886', '1', 'Su tutte le strade è vietato danneggiare le opere e gli impianti che ad esse appartengono', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '306', '10016', '1887', '2', 'Su tutte le strade è vietato gettare dai veicoli in movimento qualsiasi cosa', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '306', '10016', '1888', '3', 'Su tutte le strade è vietato depositare rifiuti', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '306', '10016', '1889', '4', 'Su tutte le strade è vietato dare la precedenza ai pedoni', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '306', '10016', '1890', '5', 'Su tutte le strade è vietato usare l''avvisatore acustico (clacson)', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '306', '10016', '1891', '6', 'Su tutte le strade è vietato viaggiare di giorno con i fari anabbaglianti sempre accesi', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '307', '10024', '', '', 'Obblighi per chi conduce un ciclomotore', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '307', '10024', '1892', '1', 'Il conducente del ciclomotore non deve trainare o farsi trainare', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '307', '10024', '1893', '2', 'Il conducente del ciclomotore deve evitare la posizione sdraiata in avanti, anche se essa riduce la resistenza dell’aria', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '307', '10024', '1894', '3', 'Il conducente del ciclomotore deve denunciare e risarcire i danni eventualmente causati alle opere stradali', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '307', '10024', '1895', '4', 'Il conducente del ciclomotore deve denunciare entro 48 ore lo smarrimento o il furto della targa', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '307', '10024', '1896', '5', 'Il conducente del ciclomotore deve sapere che l’assicurazione risarcirà sempre i danni subìti dal passeggero trasportato', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '307', '10024', '1897', '6', 'Il conducente del ciclomotore può trasportare fino a due passeggeri se il veicolo è idoneo', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '307', '10024', '1898', '7', 'Il conducente del ciclomotore deve esporre il contrassegno di assicurazione', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '307', '10024', '1899', '8', 'Il conducente del ciclomotore non deve usare il casco se soffre di claustrofobia', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '308', '10026', '', '', 'Comportamenti da tenere se altri utenti della strada non danno la precedenza', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '308', '10026', '1900', '1', 'Se altri utenti della strada non ci danno la precedenza, occorre frenare e rinunciare al proprio diritto di precedenza', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '308', '10026', '1901', '2', 'Se altri utenti della strada non ci danno la precedenza, occorre fare tutto il possibile per evitare l’incidente, anche fermandosi se necessario', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '308', '10026', '1902', '3', 'Se altri utenti della strada non ci danno la precedenza, bisogna azionare il clacson per avvertirli, e passare comunque per primi', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '308', '10026', '1903', '4', 'Se altri utenti della strada non ci danno la precedenza perché siamo noi a doverlo fare, non vi è l’obbligo di usare prudenza', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '308', '10026', '1904', '5', 'Se altri utenti della strada non ci danno la precedenza, in caso d’incidente la responsabilità sarà sempre e solo del veicolo che non ha dato la precedenza', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '309', '10025', '', '', 'Comportamenti da tenere quando si intralciano i veicoli che seguono', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '309', '10025', '1905', '1', 'Se, conducendo un quadriciclo leggero a motore, si intralciano i veicoli che seguono, occorre ridurre la velocità e fermarsi per dare la possibilità ai veicoli che seguono di superare', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '309', '10025', '1906', '2', 'Se, conducendo un quadriciclo leggero a motore, si intralciano i veicoli che seguono, occorre fermarsi appena possibile per far defluire il traffico retrostante', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '309', '10025', '1907', '3', 'Se, conducendo un quadriciclo leggero a motore, si intralciano i veicoli che seguono, occorre permettere il sorpasso ai veicoli più veloci che seguono appena si sia giunti in un punto sicuro', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '309', '10025', '1908', '4', 'Se, conducendo un quadriciclo leggero a motore, si intralciano i veicoli che seguono, occorre portarsi al centro della strada', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '309', '10025', '1909', '5', 'Se, conducendo un quadriciclo leggero a motore, si intralciano i veicoli che seguono, occorre invertire il senso di marcia', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '309', '10025', '1910', '6', 'Se, conducendo un quadriciclo leggero a motore, si intralciano i veicoli che seguono, occorre azionare l''indicatore di direzione sinistro', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '310', '10023', '', '', 'Comportamenti da tenere se si avvicina un veicolo con luce blu lampeggiante accesa e sirena in funzione', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '310', '10023', '1911', '1', 'Se si avvicina un veicolo con luce blu lampeggiante accesa e sirena in funzione è necessario dargli la precedenza', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '310', '10023', '1912', '2', 'Se si avvicina un veicolo con luce blu lampeggiante accesa e sirena in funzione è necessario fermarsi sul lato destro della strada favorendo il transito', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '310', '10023', '1913', '3', 'Se si avvicina un veicolo con luce blu lampeggiante accesa e sirena in funzione è necessario non ostacolarne la marcia', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '310', '10023', '1914', '4', 'Se si avvicina un veicolo con luce blu lampeggiante accesa e sirena in funzione è necessario salire in ogni caso sul marciapiede', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '310', '10023', '1915', '5', 'Se si avvicina un veicolo con luce blu lampeggiante accesa e sirena in funzione è necessario seguirne la scia dopo il passaggio, per guadagnare tempo', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '310', '10023', '1916', '6', 'Se si avvicina un veicolo con luce blu lampeggiante accesa e sirena in funzione è necessario fermarsi solo se si è alla guida di un veicolo ingombrante', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '311', '10027', '', '', ' Casi in cui non bisogna impegnare l''incrocio anche se il semaforo è verde', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '311', '10027', '1917', '1', 'Pur essendo accesa la luce verde del semaforo, non bisogna impegnare l’incrocio se, a causa del traffico, non si è sicuri di poterlo attraversare', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '311', '10027', '1918', '2', 'Pur essendo accesa la luce verde del semaforo, non bisogna impegnare l’incrocio quando il vigile indica di fermarsi', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '311', '10027', '1919', '3', 'Pur essendo accesa la luce verde del semaforo, non bisogna impegnare l’incrocio quando si sente la sirena di un mezzo di soccorso', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '311', '10027', '1920', '4', 'Pur essendo accesa la luce verde del semaforo, non bisogna impegnare l’incrocio quando è presente anche il segnale DARE PRECEDENZA', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '311', '10027', '1921', '5', 'Pur essendo accesa la luce verde del semaforo, non bisogna impegnare l’incrocio quando si trova il segnale di STOP', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '311', '10027', '1922', '6', 'Pur essendo accesa la luce verde del semaforo, non bisogna impegnare l’incrocio quando dal senso opposto giunge un veicolo con il dispositivo a luce gialla lampeggiante', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '312', '10001', '', '', ' Comportamenti da tenere quando, al segnale di via libera, il veicolo che sta davanti non riparte subito', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '312', '10001', '1923', '1', 'Quando, ad un incrocio, al segnale di via libera il veicolo che sta davanti non riparte subito, è opportuno attendere pazientemente la ripresa della marcia', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '312', '10001', '1924', '2', 'Quando, ad un incrocio, al segnale di via libera il veicolo che sta davanti non riparte subito, è opportuno evitare di strombazzare', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '312', '10001', '1925', '3', 'Quando, ad un incrocio, al segnale di via libera il veicolo che sta davanti non riparte subito, è opportuno evitare di superarlo con manovre errate o improprie', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '312', '10001', '1926', '4', 'Quando, ad un incrocio, al segnale di via libera il veicolo che sta davanti non riparte subito, è opportuno suonare insistentemente per sollecitare il conducente a rimettersi in moto', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '312', '10001', '1927', '5', 'Quando, ad un incrocio, al segnale di via libera il veicolo che sta davanti non riparte subito, è opportuno cercare di sgomberare al più presto la strada, anche invertendo il senso di marcia', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '313', '10035', '', '', 'Comportamenti da tenere se sulla strada si trovano degli oggetti pericolosi', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '313', '10035', '1928', '1', 'Se sulla strada si trovano degli oggetti pericolosi (bottiglie di vetro, sassi o pezzi di legno), occorre rimuoverli dalla strada, dopo essersi accertati che non sopraggiungano veicoli', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '313', '10035', '1929', '2', 'Se sulla strada si trovano degli oggetti pericolosi (bottiglie di vetro, sassi o pezzi di legno), occorre avvisare gli addetti alla manutenzione o pulizia della strada', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '313', '10035', '1930', '3', 'Se sulla strada si trovano degli oggetti pericolosi (bottiglie di vetro, sassi o pezzi di legno), occorre prevedere il possibile rischio di cadere, passandovi sopra con la ruota del ciclomotore', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '313', '10035', '1931', '4', 'Se sulla strada si trovano degli oggetti pericolosi (bottiglie di vetro, sassi o pezzi di legno), occorre lasciarli nella posizione in cui si trovano, senza fare nulla', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '313', '10035', '1932', '5', 'Se sulla strada si trovano degli oggetti pericolosi (bottiglie di vetro, sassi o pezzi di legno), occorre trascinarli fuori dalla strada, ma solo nelle ore notturne', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '313', '10035', '1933', '6', 'Se sulla strada si trovano degli oggetti pericolosi (bottiglie di vetro, sassi o pezzi di legno), occorre spostarli verso il centro della carreggiata per essere notati a distanza dai veicoli in transito', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '314', '10036', '', '', ' Comportamenti da tenere se su una strada si nota la presenza di un cedimento del fondo stradale', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '314', '10036', '1934', '1', 'Se su una strada si nota la presenza di un cedimento del fondo stradale o un tombino scoperchiato, occorre avvisare gli addetti alla manutenzione della strada', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '314', '10036', '1935', '2', 'Se su una strada si nota la presenza di un cedimento del fondo stradale o un tombino scoperchiato, occorre avvisare del possibile pericolo gli altri utenti', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '314', '10036', '1936', '3', 'Se su una strada si nota la presenza di un cedimento del fondo stradale o un tombino scoperchiato, occorre ridurre la velocità del veicolo che si conduce', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '314', '10036', '1937', '4', 'Se su una strada si nota la presenza di un cedimento del fondo stradale o un tombino scoperchiato, occorre spostarsi dalla zona cedevole, invadendo la corsia destinata al senso opposto', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '314', '10036', '1938', '5', 'Se su una strada si nota la presenza di un cedimento del fondo stradale o un tombino scoperchiato, occorre calarsi all’interno del tombino per verificare l’eventuale presenza di operai', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '314', '10036', '1939', '6', 'Se su una strada si nota la presenza di un cedimento del fondo stradale o un tombino scoperchiato, occorre avvisare le autorità solo se si tratta di un tombino di gas, luce e acqua', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '315', '10029', '', '', 'Trasporto di un carico sul ciclomotore', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '315', '10029', '1940', '1', 'Su un ciclomotore, il carico trasportato non deve coprire i dispositivi di segnalazione visiva e la targa', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '315', '10029', '1941', '2', 'Su un ciclomotore, il carico trasportato non deve ostacolare l’azionamento dei comandi', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '315', '10029', '1942', '3', 'Su un ciclomotore, il carico trasportato non deve strisciare a terra nelle curve', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '315', '10029', '1943', '4', 'Su un ciclomotore, il carico trasportato deve essere saldamente fissato, in modo da non comportare pericolo durante la guida', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '315', '10029', '1944', '5', 'Su un ciclomotore, il carico trasportato non deve sporgere troppo, in quanto potrebbe causare la perdita di controllo del veicolo', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '315', '10029', '1945', '6', 'Su un ciclomotore, il carico trasportato può sporgere posteriormente fino a 80 cm', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '315', '10029', '1946', '7', 'Su un ciclomotore, il carico trasportato può sporgere lateralmente, fino ad un massimo di 30 cm dall’asse del veicolo', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '315', '10029', '1947', '8', 'Su un ciclomotore, il carico trasportato deve essere fissato su manubrio, parafanghi e forcella', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '316', '10028', '', '', 'Uso degli specchietti retrovisori', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '316', '10028', '1948', '1', 'Relativamente all’uso degli specchietti retrovisori di un ciclomotore è bene sapere che veicoli e pedoni possono trovarsi nell’angolo cieco e quindi non essere visti', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '316', '10028', '1949', '2', 'Relativamente all’uso degli specchietti retrovisori di un ciclomotore è bene sapere che devono essere mantenuti sempre efficienti e in buone condizioni', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '316', '10028', '1950', '3', 'Relativamente all’uso degli specchietti retrovisori di un ciclomotore è bene sapere che devono essere fissati e regolati correttamente', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '316', '10028', '1951', '4', 'Relativamente all’uso degli specchietti retrovisori di un ciclomotore è bene sapere che non devono essere incrinati o sporchi', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '316', '10028', '1952', '5', 'Relativamente all’uso degli specchietti retrovisori di un ciclomotore è bene sapere che si possono utilizzare anche quelli non omologati', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '317', '10007', '', '', ' Obbligo di segnalare il pericolo in caso di incidente', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '317', '10007', '1953', '1', 'In caso di incidente, si deve segnalare il pericolo ai veicoli che sopraggiungono per impedirne il passaggio nella zona pericolosa', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '317', '10007', '1954', '2', 'In caso di incidente, si deve segnalare il pericolo ai veicoli che sopraggiungono anche nel caso in cui si sono verificate perdite di olio sulla strada', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '317', '10007', '1955', '3', 'In caso di incidente, si deve segnalare il pericolo ai veicoli che sopraggiungono per proteggere un ferito che non si possa spostare dalla strada', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '317', '10007', '1956', '4', 'In caso di incidente, si deve segnalare il pericolo ai veicoli che sopraggiungono solo in caso di nebbia', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '317', '10007', '1957', '5', 'In caso di incidente, si deve segnalare il pericolo ai veicoli che sopraggiungono solamente se il traffico è bloccato a causa dell''incidente', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '317', '10007', '1958', '6', 'In caso di incidente, non si deve mai segnalare il pericolo ai veicoli che sopraggiungono se l''incidente è avvenuto in un centro abitato illuminato', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '318', '10005', '', '', ' Comportamenti da tenere se si urta un veicolo in sosta', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '318', '10005', '1959', '1', 'Se, guidando un ciclomotore, si urta un veicolo in sosta, bisogna fare in modo di lasciare i propri dati', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '318', '10005', '1960', '2', 'Se, guidando un ciclomotore, si urta un veicolo in sosta, occorre rintracciare il proprietario e lasciargli i propri dati', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '318', '10005', '1961', '3', 'Se, guidando un ciclomotore, si urta un veicolo in sosta, si ha l’obbligo di risarcire il danno', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '318', '10005', '1962', '4', 'Se, guidando un ciclomotore, si urta un veicolo in sosta, si può andare via se nessuno ha visto', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '318', '10005', '1963', '5', 'Se, guidando un ciclomotore, si urta un veicolo in sosta, non si ha alcuna responsabilità se si è minorenne', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '318', '10005', '1964', '6', 'Se, guidando un ciclomotore, si urta un veicolo in sosta, è meglio allontanarsi subito per non farsi incolpare di danni maggiori', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '319', '10006', '', '', 'Comportamenti da tenere se si è coinvolti in un incidente senza feriti (1)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '319', '10006', '1965', '1', 'Se, guidando un ciclomotore, si è coinvolti in un incidente senza feriti, si ha l’obbligo di fermarsi per constatare i danni', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '319', '10006', '1966', '2', 'Se, guidando un ciclomotore, si è coinvolti in un incidente senza feriti, non bisogna mai perdere la calma', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '319', '10006', '1967', '3', 'Se, guidando un ciclomotore, si è coinvolti in un incidente senza feriti, si può andare subito a casa ad avvisare i genitori', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '319', '10006', '1968', '4', 'Se, guidando un ciclomotore, si è coinvolti in un incidente senza feriti, se si è minorenni si può andare via subito', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '320', '10004', '', '', 'Comportamenti da tenere se si è coinvolti in un incidente senza feriti (2)', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '320', '10004', '1969', '1', 'Dopo un incidente stradale senza danni alle persone occorre, se possibile, spostare il ciclomotore almeno sul margine destro della carreggiata', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '320', '10004', '1970', '2', 'Dopo un incidente stradale senza danni alle persone occorre, non potendo spostare il ciclomotore, segnalarne la presenza sulla strada', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '320', '10004', '1971', '3', 'Dopo un incidente stradale senza danni alle persone occorre, non potendo spostare il ciclomotore, chiamare il soccorso stradale e rimanere sul posto in attesa', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '320', '10004', '1972', '4', 'Dopo un incidente stradale senza danni alle persone occorre coprire il ciclomotore con un telo di plastica', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '320', '10004', '1973', '5', 'Dopo un incidente stradale senza danni alle persone occorre collocare subito il veicolo sul marciapiede', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '320', '10004', '1974', '6', 'Dopo un incidente stradale senza danni alle persone occorre presentare in ogni caso denunzia al più vicino commissariato di polizia', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '321', '10008', '', '', ' Casi in cui si deve chiamare la polizia dopo un incidente stradale', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '321', '10008', '1975', '1', 'Si deve chiamare la polizia a seguito di incidente stradale se vi sono feriti', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '321', '10008', '1976', '2', 'Si deve chiamare la polizia a seguito di incidente stradale quando vi sono lesioni alle persone', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '321', '10008', '1977', '3', 'Si deve chiamare la polizia a seguito di incidente stradale quando l''altro conducente non dimostra di essere assicurato', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '321', '10008', '1978', '4', 'Si deve chiamare la polizia a seguito di incidente stradale solo in caso di danni ai veicoli', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '321', '10008', '1979', '5', 'Si deve chiamare la polizia a seguito di incidente stradale quando non ci siano altri testimoni', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '322', '10009', '', '', ' Comportamenti da tenere se si è coinvolti in un incidente con feriti', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '322', '10009', '1980', '1', 'Se vi sono feriti a seguito di un incidente stradale si deve chiamare subito i soccorsi e descrivere i danni riportati dalle persone ferite', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '322', '10009', '1981', '2', 'Se vi sono feriti a seguito di un incidente stradale si deve avvertire rapidamente il 112, il 113 o il 118', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '322', '10009', '1982', '3', 'Se vi sono feriti a seguito di un incidente stradale si deve chiamare i soccorsi al numero telefonico 118', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '322', '10009', '1983', '4', 'Se vi sono feriti a seguito di un incidente stradale si deve somministrare loro bevande alcooliche', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '322', '10009', '1984', '5', 'Se vi sono feriti a seguito di un incidente stradale si deve proseguire senza fermarsi a prestare soccorso', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '322', '10009', '1985', '6', 'Se vi sono feriti a seguito di un incidente stradale si deve prestare aiuto solo se si è personalmente responsabili dell’incidente', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '323', '10011', '', '', ' Obbligo di soccorso', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '323', '10011', '1986', '1', 'Soccorrere un infortunato sulla strada è obbligatorio anche per dovere morale', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '323', '10011', '1987', '2', 'Soccorrere un infortunato sulla strada è un obbligo previsto dal codice penale', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '323', '10011', '1988', '3', 'Soccorrere un infortunato sulla strada è obbligatorio perché così si possono limitare gli effetti negativi dell''incidente', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '323', '10011', '1989', '4', 'Soccorrere un infortunato sulla strada non è obbligatorio', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '323', '10011', '1990', '5', 'Soccorrere un infortunato sulla strada è obbligatorio solo per i medici e il personale sanitario', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '323', '10011', '1991', '6', 'Soccorrere un infortunato sulla strada non è obbligatorio, anche per evitare di essere coinvolti', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '324', '10010', '', '', 'Comportamenti da tenere in caso di incidente', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '324', '10010', '1992', '1', 'In caso di incidente il soccorritore deve valutare la situazione per scongiurare ulteriori pericoli', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '324', '10010', '1993', '2', 'In caso di incidente il soccorritore deve chiamare i soccorsi e rimanere fino all’arrivo degli agenti', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '324', '10010', '1994', '3', 'In caso di incidente il soccorritore deve segnalare come meglio può l''incidente', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '324', '10010', '1995', '4', 'In caso di incidente il soccorritore deve allontanarsi velocemente dal luogo dell''incidente', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '324', '10010', '1996', '5', 'In caso di incidente il soccorritore deve identificare l''infortunato', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '324', '10010', '1997', '6', 'In caso di incidente il soccorritore deve somministrare farmaci', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '325', '10002', '', '', 'Responsabilità civile derivante da incidente stradale ', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '325', '10002', '1998', '1', 'La responsabilità civile derivante dall’incidente stradale sorge qualora si causi un danno a persone, animali o cose', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '325', '10002', '1999', '2', 'La responsabilità civile derivante dall’incidente stradale grava anche sul proprietario, anche se egli non è presente al momento dell’incidente', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '325', '10002', '2000', '3', 'La responsabilità civile derivante dall’incidente stradale obbliga il responsabile al risarcimento del danno causato', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '325', '10002', '2001', '4', 'La responsabilità civile derivante dall’incidente stradale è di ciascuno dei conducenti coinvolti, finché non sia dimostrato il contrario', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '325', '10002', '2002', '5', 'La responsabilità civile derivante dall’incidente stradale si limita al risarcimento dei danni alle sole cose', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '325', '10002', '2003', '6', 'La responsabilità civile derivante dall’incidente stradale è esclusa per danni di lieve entità', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '325', '10002', '2004', '7', 'La responsabilità civile derivante dall’incidente stradale è a carico dell''impresa assicuratrice anche per danni superiori ai massimali', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '325', '10002', '2005', '8', 'La responsabilità civile derivante dall’incidente stradale consiste nell’obbligo di assistere gli eventuali feriti', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '326', '10003', '', '', ' Responsabilità penale derivante da incidente stradale', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '326', '10003', '2006', '1', 'La responsabilità penale derivante da incidente stradale sorge qualora esso provochi lesioni gravi a persone', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '326', '10003', '2007', '2', 'La responsabilità penale derivante da incidente stradale sorge in caso di omicidio colposo', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '326', '10003', '2008', '3', 'La responsabilità penale derivante da incidente stradale sorge allorché vengano violate norme contemplate dal codice penale', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '326', '10003', '2009', '4', 'La responsabilità penale derivante da incidente stradale è personale', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '326', '10003', '2010', '5', 'La responsabilità penale derivante da incidente stradale è di chi ha causato l’incidente', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '326', '10003', '2011', '6', 'La responsabilità penale derivante da incidente stradale sorge qualora esso provochi lesioni di qualsiasi entità', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '326', '10003', '2012', '7', 'La responsabilità penale derivante da incidente stradale sorge in ogni caso in cui sia violato il codice della strada', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '326', '10003', '2013', '8', 'La responsabilità penale derivante da incidente stradale ricade sempre nei confronti del genitore, nel caso in cui il conducente sia minorenne', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '326', '10003', '2014', '9', 'La responsabilità penale derivante da incidente stradale è esclusa quando si sia prestato soccorso alla vittima', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '327', '10018', '', '', 'Comportamenti da tenere per limitare i consumi di carburante del ciclomotore', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '327', '10018', '2015', '1', 'Per limitare i consumi di carburante del ciclomotore conviene mantenere il più possibile costante la velocità', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '327', '10018', '2016', '2', 'Per limitare i consumi di carburante del ciclomotore conviene ridurre il numero e l''intensità delle accelerazioni', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '327', '10018', '2017', '3', 'Per limitare i consumi di carburante del ciclomotore è necessario curare la manutenzione del motore', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '327', '10018', '2018', '4', 'Per limitare i consumi di carburante del ciclomotore si devono mantenere elevate velocità', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '327', '10018', '2019', '5', 'Per limitare i consumi di carburante del ciclomotore conviene evitare percorsi pianeggianti', 'F', '');
