INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '328', '10020', '2023', '3', 'L''inquinamento atmosferico prodotto dai ciclomotori può essere ridotto spegnendo il motore in caso di arresto prolungato', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '328', '10020', '2024', '4', 'L''inquinamento atmosferico prodotto dai ciclomotori può essere ridotto utilizzando costantemente il motore ai massimi giri', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '328', '10020', '2025', '5', 'L''inquinamento atmosferico prodotto dai ciclomotori può essere ridotto tenendo il motore, durante le fermate, ad elevato numero di giri', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '328', '10020', '2026', '6', 'L''inquinamento atmosferico prodotto dai ciclomotori può essere ridotto accelerando bruscamente con il ciclomotore sul cavalletto', 'F', '');
