INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '329', '10019', '2027', '1', 'Per ridurre l''inquinamento dell''aria provocato dalla circolazione dei ciclomotori, il conducente deve mantenere in buono stato di manutenzione gli organi di alimentazione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '329', '10019', '2028', '2', 'Per ridurre l''inquinamento dell''aria provocato dalla circolazione dei ciclomotori, il conducente deve mantenere in buono stato di manutenzione gli organi di accensione', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '329', '10019', '2029', '3', 'Per ridurre l''inquinamento dell''aria provocato dalla circolazione dei ciclomotori il conducente deve sostituire il filtro dell''aria nei tempi suggeriti dalla casa costruttrice del veicolo', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '329', '10019', '2030', '4', 'Per ridurre l''inquinamento dell''aria provocato dalla circolazione dei ciclomotori il conducente deve ridurre la pressione di gonfiaggio dei pneumatici', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '329', '10019', '2031', '5', 'Per ridurre l''inquinamento dell''aria provocato dalla circolazione dei ciclomotori il conducente deve marciare alle più elevate velocità permesse dal codice e dai segnali stradali', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '329', '10019', '2032', '6', 'Per ridurre l''inquinamento dell''aria provocato dalla circolazione dei ciclomotori il conducente deve accelerare ripetutamente da fermo per tenere caldo il motore e migliorarne il rendimento', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '330', '10021', '', '', ' Comportamenti da tenere per evitare rumori molesti durante la circolazione', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '330', '10021', '2033', '1', 'Per evitare rumori molesti durante la circolazione si deve, fra l''altro, far controllare che il dispositivo silenziatore funzioni bene e sia di tipo approvato', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '330', '10021', '2034', '2', 'Per evitare rumori molesti durante la circolazione si deve, fra l''altro, usare il clacson nei centri abitati solo in caso di effettivo e immediato pericolo', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '330', '10021', '2035', '3', 'Per evitare rumori molesti durante la circolazione si deve, fra l''altro, evitare di frenare bruscamente provocando lo strisciamento dei pneumatici', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '330', '10021', '2036', '4', 'Per evitare rumori molesti durante la circolazione si deve, fra l''altro, suonare per richiamare un conducente che ha commesso un''infrazione', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '330', '10021', '2037', '5', 'Per evitare rumori molesti durante la circolazione si deve, fra l''altro, sgommare alla partenza quando compare il verde del semaforo, per sgomberare al più presto l''incrocio', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '330', '10021', '2038', '6', 'Per evitare rumori molesti durante la circolazione si deve, fra l''altro, mantenere il motore acceso durante le fermate per esigenze del traffico, accelerando ripetutamente', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('', '331', '10022', '', '', ' Comportamenti da tenere per ridurre il rumore emesso dai ciclomotori', '', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '331', '10022', '2039', '1', 'Per ridurre il rumore emesso dai ciclomotori è necessario far verificare che il dispositivo silenziatore funzioni bene e sia di tipo approvato', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '331', '10022', '2040', '2', 'Per ridurre il rumore emesso dai ciclomotori è necessario far sostituire la marmitta deteriorata con altra di tipo approvato per lo stesso veicolo', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '331', '10022', '2041', '3', 'Per ridurre il rumore emesso dai ciclomotori è necessario far controllare i freni, se stridono', 'V', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '331', '10022', '2042', '4', 'Per ridurre il rumore emesso dai ciclomotori è necessario modificare il dispositivo silenziatore in modo da migliorare il rendimento del motore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '331', '10022', '2043', '5', 'Per ridurre il rumore emesso dai ciclomotori è necessario eliminare la marmitta per migliorare il rendimento del motore', 'F', '');
INSERT INTO AM (Chapter, Topic_No, Topic_Num, Ques_Num, Per_topic, Question, Answer,blank ) VALUES ('10', '331', '10022', '2044', '6', 'Per ridurre il rumore emesso dai ciclomotori è necessario sostituire il dispositivo silenziatore, se inefficiente, con altro di qualsiasi tipo', 'F', '');
